style.css:
@-moz-document regexp("http.*://psarips..*")
{
* {
    scrollbar-color: #2a2c2e #1c1e1f;
}
#bf6b ~ * {
    display: inherit !important;
}
body {
    background: #252525 !important;
}
#loginform, .wp-core-ui .button-primary, .login #login_error, .login .message, .login .success {
    background: #333 !important;
    color: #777 !important;
}
body > strong, body > div:not(#wrapper):not(#wpadminbar):not(#login):not(#wpwrap), body > :not(div) {
    visibility: hidden !important;
}
select, input:not(.wpcf7-submit), textarea {
    background: #222 !important;
	border: 2px solid #444 !important;
}
#menu-top-menu {
    text-align: center !important;
}
#wrapper, #fancybox-tmp, #fancybox-loading, #fancybox-overlay, #fancybox-wrap, #wpadminbar {
    visibility: visible;
}
.page-title, #tag_cloud-2, #html_javascript_adder-12, #html_javascript_adder-7, #html_javascript_adder-4, .container-inner > a:first-child, #recent-comments-3, #recent-posts-2, #feedburnerformwidget-2, .widget_recent_comments li:nth-child(n+7), #header-image-wrap, .widget_hu_tabs {
    display: none !important;
}
#sb_messages {
    height: 350px !important;
}
#header {
    background: #252525 !important;
    position: fixed !important;
    top: 0 !important;
    z-index: 10000 !important;
    padding-bottom: 0 !important;
/*     padding: 0 !important; */
}
#nav-topbar {
    -webkit-transform: translateX(0%) !important;
    -ms-transform: translateX(0%) !important;
    transform: translateX(0%) !important;
}
#nav-header {
    position: fixed !important;
    width: 100% !important;
    left: 50% !important;
    -webkit-transform: translateX(-50%) !important;
    -ms-transform: translateX(-50%) !important;
    transform: translateX(-50%) !important;
}
#nav-header, #nav-topbar.nav-container, #nav-topbar .nav ul, #footer-bottom, .search-expand, #wpadminbar, .dropshadowboxes-container > div, .search {
    background-color: #222 !important;
}
#menu-main-menu {
    left: 50% !important;
    -webkit-transform: translateX(-50%) !important;
    -ms-transform: translateX(-50%) !important;
    transform: translateX(-50%) !important;
}
#footer, #nav-footer {
    padding: 0px !important;
    background-color: #252525 !important;
}
#page {
    padding-top: 10px !important;
    padding-bottom: 0 !important;
    margin-top: 90px !important;
    background-color: #252525 !important;
}
.page-title, .notebox, .notebox::before {
    background-color: transparent !important;
    border-bottom: 0px !important;
}
.main-inner {
    background: transparent !important;
}
.main, #page .container-inner, .content {
    background: #292929 !important;
    background-color: #292929 !important;
}
div.sidebar-content > #text-2 > div.textwidget > div > a {
    color: #3B8DBD !important;
}
#text-2 {
    background: #2d2d2d !important;
}
.container-inner > .main::after, .container-inner > .main::before {
	background: #2d2d2d !important;
}
.sidebar {
    background: #2d2d2d !important;
    margin-bottom: 0px !important;
    position: absolute;
    right: 0;
    height: 100% !important;
    top: 0;
    padding-bottom: 0;
}
.col-2cl .sidebar.s1 {
    float: unset;
    margin-right: unset;
}
.sidebar-content > div > h3, .toggle-search {
    color: #aaa !important;
}
.s1:hover {
    z-index: 2 !important;
}
.featured {
    width: 49% !important;
    font-size-adjust: .45;
    border: 0 !important;
    float: left !important;
    padding-right: 10px;
    margin-top: -28px;
    min-height: 370px;
    max-height: 370px;
    height: 370px !important;
    overflow: hidden;
}
.featured > article > div > h2 > a {
    font-size-adjust: .30;
}
.featured > article > div > h2 > a, .post-row > article > div > h2 > a {
    color: #aaa;
}
.post-row .post-category {
    line-height: 1.1em;
}
.post-row {
    width: 49% !important;
    font-size-adjust: .45;
    border: 0 !important;
    margin-bottom: 7px !important;
    min-height: 400px;
    max-height: 400px;
    height: 400px;
    overflow: hidden;
    padding-bottom: 55px;
}
.post-list > .post-row:last-child {
    height: auto;
    min-height: unset;
    max-height: unset;
}
.entry.excerpt {
    line-height: 1.2em;
}
.entry-inner > div:first-child {
    background-color: #292929 !important;
}
.entry-inner > div.separator, #advads_ad_widget-1, #advads_ad_widget-2, #advads_ad_widget-3 {
    display: none;
}
.sp-wrap, .sp-body, .spdiv, .sp-head {
    background-color: #333 !important;
    border-color: #666 !important;
    color: #777 !important;
}
.post-tags > a, .sidebar-content, .hr, hr {
    background: #444 !important;
}
.hr, hr, .alx-posts li, .alx-tab li, .widget_archive ul li, .widget_categories ul li, .widget_links ul li, .widget_meta ul li, .widget_nav_menu ul li a, .widget_pages ul li a, .widget_recent_comments ul li, .widget_recent_entries ul li, .widget_rss ul li, #nav-footer.nav-container {
	border-color: #444 !important;
}
.post-title a {
    color: #3b8dbd !important;
}
.wpcf7 p {
    color: #666 !important;
}
.wpcf7 p > span > input, .wpcf7 p > span > textarea {
    background-color: #444 !important;
    color: #999 !important;
    border-color: #666 !important;
}
.social-links .social-tooltip:hover:after {
    top: 34px;
}
.social-links .social-tooltip:hover:before {
    top: 29px;
    border-width: 0px 5px 5px 5px;
}
.comment code, .entry code {
    background: #111 !important;
}
.post-title {
    line-height: 1.0em !important;
}
#wpcomm .wc-comment-header {
    border-top: 0 !important;
}
#wpcomm .wpdiscuz-front-actions, #wpcomm .wc-comment-header, #wpcomm .wc-comment-right .wc-comment-text p, #wpcomm .wc-comment .wc-comment-right, #wpcomm .wc-comment-right .wc-comment-text, #wpcomm .wc-comment-right .wc-comment-text *, #wpcomm .wc-comment-footer, #wpcomm .wc-comment-label span, #wpcomm .wc-blog-administrator > .wc-comment-left .wc-comment-label, #wpcomm .wc-comment .wc-comment-left .wc-comment-label {
    background: #353535 !important;
}
#wpcomm .wc-blog-administrator > .wc-comment-left .wc-comment-label, #wpcomm .wc-blog-subscriber > .wc-comment-left .wc-comment-label, #wpcomm .wc-blog-premium > .wc-comment-left .wc-comment-label, .wp-pagenavi a , #wpcomm, .related-posts, .content .post-nav, .author-bio, #footer .alx-posts li, #footer .alx-tab li, #footer .widget_archive ul li, #footer .widget_calendar td, #footer .widget_calendar th, #footer .widget_categories ul li, #footer .widget_links ul li, #footer .widget_meta ul li, #footer .widget_nav_menu ul li a, #footer .widget_pages ul li a, #footer .widget_recent_comments ul li, #footer .widget_recent_entries ul li, #footer .widget_rss ul li {
    border-color: #353535 !important;
}
#footer .widget_calendar thead {
	background-color: #353535;
}
.content .post-nav li.previous {
	-webkit-box-shadow: 1px 0 0 #353535, inset -1px 0 0 #353535;
	box-shadow: 1px 0 0 #353535, inset -1px 0 0 #353535;
}
.wp-pagenavi a:active, .wp-pagenavi a:hover, .wp-pagenavi span.current {
    border-top: 1px solid #444 !important;
}

#wpwrap h2, #wpwrap h3, #wpwrap .form-table th, #wpwrap .form-wrap label {
    color: #666 !important;
}
#wpwrap .postbox, #wpwrap #activity-widget #latest-comments #the-comment-list .comment-item {
    border-color: #444;
    background: #282828;
}
}
