style.css:
@-moz-document domain("www.worldtobuild.com") {
:root {
    --main-color: /*[[mainColor]]*/;
    --text-color: /*[[textColor]]*/;
}

a {
    color: var(--main-color);
}

a:focus,
a:hover {
    color: var(--main-color);
}

/* HEADER */
.wtb-site-header {
    background: var(--main-color);
    color: var(--text-color);
}

.wtb-dropdown-item {
    color: var(--main-color);
}

.wtb-dropdown-item:hover {
    color: var(--main-color);
    transform: scale(1.1);
}

.wtb-dropdown-title {
    color: var(--main-color);
}


/* BUTTONS */
.wtb-button,
.button.success {
    background: var(--main-color);
    color: var(--text-color);
}

.wtb-button:focus,
.wtb-button:hover,
.button.success:focus,
.button.success:hover {
    background: var(--main-color);
    color: var(--text-color);
    filter: brightness(80%);
}

.button.disabled.success,
.button.disabled.success:focus,
.button.disabled.success:hover,
.button[disabled].success,
.button[disabled].success:focus,
.button[disabled].success:hover {
    background-color: var(--main-color);
}

.button.general {
    background: var(--main-color);
    color: var(--text-color);
}

.button.general:focus,
.button.general:hover {
    background: var(--main-color);
    color: var(--text-color);
    filter: brightness(80%);
}

.wtb-site-header-login-button {
    color: var(--main-color);
}

.wtb-search-button {
    background: var(--main-color);
}

.wtb-tab button {
    color: var(--main-color);
    ;
}

.wtb-tab-hover,
.wtb-tab button.active {
    color: var(--main-color);
}

.wtb-tab-hover:hover {
    color: var(--main-color);
    transform: scale(1.1);
}

.wtb-shop-filter-button {
    background: var(--main-color);
}

.wtb-error-button {
    border: 2px solid var(--main-color) !important;
    background: var(--main-color) !important;
}

.wtb-error-button:hover,
.wtb-error-return:focus {
    border: 2px solid var(--main-color) !important;
    background: transparent !important;
    color: var(--main-color) !important;
}


/* INPUT BOXES */
.wtb-input,
input,
textarea,
select {
    color: var(--main-color) !important;
    border: 0 solid var(--main-color) !important;
}

.wtb-input:focus,
input:focus,
textarea:focus,
select:focus {
    border: 2px solid var(--main-color) !important;
}


/* HOME PAGE */
.wtb-topBar-updated,
.new-in-shopContainer {
    background: var(--main-color);
}


/* FOOTER */
.wtb-site-footer > div > div > div > div > div > .large-5.medium-5.small-12,
.wtb-site-footer a,
.wtb-site-footer a:active,
.wtb-site-footer a:link,
.wtb-site-footer a:visited,
.wtb-hover-grow > a > .fab {
    color: var(--main-color) !important;
}

/* GAMES PAGE */
/* THUMBNAIL OVERLAY COLORS */
/*[[overlaysTrue]]*/
/* END */
.small-6.medium-4.large-4 > a > div > div {
    background: var(--main-color) !important;
    border: 1px solid var(--main-color) !important;
}

.small-6.medium-4.large-4 > a > div > div > font {
    color: var(--text-color) !important;
}

.large-6.medium-6.small-12 > h4,
.small-6.medium-4 > a > div:nth-child(2),
.small-6.medium-4 > a > div > font:nth-child(2) {
    color: var(--main-color) !important;
}

.small-6.medium-4 > a > div:nth-child(3) {
    color: var(--main-color) !important;
    filter: brightness(60%);
}

.small-6.medium-4 > a > div > font:nth-child(1) {
    color: var(--main-color) !important;
    filter: brightness(60%);
}

.wtb-ribbon-color-red {
    border-color: var(--main-color);
}

.wtb-ribbon-color-red span {
    background: var(--main-color);
}

.wtb-ribbon-color-red span::before,
.wtb-ribbon-color-red span::after {
    border-left: 3px solid var(--main-color);
    border-right: 3px solid var(--main-color);
    border-top: 3px solid var(--main-color);
}

.wtb-page-current.bold,
.small-4 > a > .button-squish > i {
    color: var(--main-color);
}

.fa.fa-angle-left.fa-lg,
.fa.fa-angle-right.fa-lg {
    color: var(--main-color) !important;
}


/* GAME PAGE */
.w2b-game-thumbnail {
    border-radius: 7px;
    background-color: var(--main-color) !important;
}

.large-10.medium-10.small-12 > h4,
.large-12.medium-12.small-12 > h6,
.world-info-box.text-center > div > div > a,
.world-info-box.text-center > div > div > font,
.fa.fa-ellipsis-v.dropbtn {
    color: var(--main-color) !important;
}

.dropdown-content a {
    color: var(--main-color) !important;
}


/* STORE PAGE */
.wtb-header-red {
    background: var(--main-color);
}

.wtb-page-button {
    background: var(--main-color);
}

.wtb-page-button > i {
    color: var(--text-color);
}

.wtb-page-current {
    color: var(--main-color) !important;
    font-weight: bold;
}


/* FORUM */
.forum-category-renewed {
    border: 2px solid var(--main-color) !important;
}

.speech-bubble,
.large-12.medium-12.small-12.small-padding.forumThreadBox {
    background: var(--main-color) !important;
}

.speech-bubble::after {
    border-top: 20px solid var(--main-color) !important;
}


/* FORUM POST */
#replying-content-nickName,
#replying-content-userName,
#report-abuse-link,
.medium-padding > .grid-x > .large-3.medium-5.small-12.text-center > div > a,
.small-padding > div > a > font > .fas.fa-quote-right,
.forum-returnAriaTrue-Left {
    color: var(--main-color) !important;
}

/* PROFILE PAGE */
#message-user-button {
    color: var(--main-color);
}

.report-abuse-flag:hover {
    color: var(--main-color);
}

.wtb-tab-profile {
    background: var(--main-color);
}

.wtb-tab-profile button {
    background: var(--main-color);
}

#pagination-inventory > a > button {
    color: var(--main-color) !important;
}

.dropdown-link > h6 {
    background: var(--main-color) !important;
}


/* MY WORLDS/STUFF PAGE */
.grid-x > .grid-y > div > .fas.fa-user,
.grid-x > .grid-y > div > .fas.fa-lock,
.grid-x > .grid-y > div > font {
    color: var(--main-color);
}


/* MISC STUFF */
.wtb-tooltip .wtb-tooltiptext {
    background-color: var(--main-color);
}

.wtb-tooltip .wtb-tooltiptext::after {
    border-color: var(--main-color) transparent transparent transparent;
}

.large-3.medium-3.hide-for-small-only.small-padding.text-center > #password-reset-lockIcon {
    color: var(--main-color) !important;
}
}
