.speech {
    position: relative;
    width: 90%;
    padding: 10px;
    color: #e7e7e7;
    min-height: 35px;
    background: #919191;
}

.speech:after {
    content: '';
    position: absolute;
    bottom: -15px;
    /* value = - border-top-width - border-bottom-width */
    left: 50px;
    /* controls horizontal position */
    border-width: 15px 10px 0;
    /* vary these values to change the angle of the vertex */
    border-style: solid;
    /* reduce the damage in FF3.0 */
    display: block;
    width: 0;
}

.speech.left {
    margin-left: 50px;
}

.speech.right {
    margin-right: 50px;
}

.speech.top {
    margin-top: 50px;
}

.speech.left:after {
    top: 16px;
    /* controls vertical position */
    left: -50px;
    /* value = - border-left-width - border-right-width */
    bottom: auto;
    border-width: 10px 50px 10px 0;
    border-color: transparent #919191;
}

.speech.right:after {
    top: 16px;
    /* controls vertical position */
    right: -50px;
    /* value = - border-left-width - border-right-width */
    bottom: auto;
    left: auto;
    border-width: 10px 0 10px 50px;
    border-color: transparent #919191;
}

.speech.top:after {
    content: "";
    position: absolute;
    border-style: solid;
    /* reduce the damage in FF3.0 */
    display: block;
    width: 0;
    top: -15px;
    /* value = - border-top-width - border-bottom-width */
    right: 50px;
    /* controls horizontal position */
    bottom: auto;
    left: auto;
    border-width: 0 15px 15px;
    /* vary these values to change the angle of the vertex */
    border-color: #919191 transparent;
}

