style.css:
@-moz-document url-prefix("https://yandex.ru/maps"), url-prefix("https://yandex.com/maps"), url-prefix("https://yandex.com.tr/harita"), regexp("https://yandex.([a-z]{2})/maps.*") {
::-webkit-scrollbar {background: #222; height: 8px; width: 8px;}
::-webkit-scrollbar-track {background-clip: padding-box; border: solid transparent; border-width: 0;}
::-webkit-scrollbar-button {width: 0px; height: 0px;}
::-webkit-scrollbar-thumb {background-color: rgba(255,255,255,.4); background-clip: padding-box;}
::-webkit-scrollbar-thumb:hover {background-color: rgba(255,255,255,.5);}
::-webkit-scrollbar-thumb:active {background-color: rgba(255,255,255,.6);}
::-webkit-scrollbar-corner {background: rgba(34,34,34,.2);}

html {background: #222;}
body {background: #222; color: #eee;}
.body {background: #000; color: #eee;}
/*[[mapbrightness]]*/

/* ==== Body ==== */

.search-placemark-title {background-color: rgba(34,34,34,.9); color: #ccc;}
.search-placemark-title._background:before {background: rgba(34,34,34,.9);}
.search-placemark-title._background._low-relevance .search-placemark-title__title {color: #ccc;}
.search-placemark-title__title {color: #eee;}
.search-placemark-title__subtitle {color: #aaa;}
.search-placemark-title__subtitle-item-rating-image path[fill="#666"] {fill: #aaa;}

.route-points-view__route-point-info {background-color: #222; color: #eee;}

.search-placemark use[fill="#FFF"] {fill: #222; stroke: #444;}
.low-relevance-icons-provider__base-content:empty {background-color: #222;}
.ymaps-2-1-74-transport-pin_filled_no .ymaps-2-1-74-transport-pin__tail, .ymaps-2-1-74-transport-pin_filled_no .ymaps-2-1-74-transport-pin__tail:before, .ymaps-2-1-74-transport-pin_filled_no.ymaps-2-1-74-transport-pin_tail_bicycle .ymaps-2-1-74-transport-pin__label, .ymaps-2-1-74-transport-pin_filled_no.ymaps-2-1-74-transport-pin_tail_pedestrian .ymaps-2-1-74-transport-pin__label {background-color: #222 !important;}
