.ymaps-2-1-74-transport-pin__body {background-color: #222;}
.ymaps-2-1-74-transport-pin__body::after {background: rgba(255,255,255,.2);}
.ymaps-2-1-74-route-pin__text {background: #222; color: #eee;}
.ymaps-2-1-74-route-pin__text-a {border-color: rgba(255,255,255,.2);}

.ymaps-2-1-74-hint__text {background: #222;}

.ymaps-2-1-74-balloon__layout {background: #222;}
.ymaps-2-1-74-balloon_size_mini .ymaps-2-1-74-balloon__close-button {filter: invert(1);}
.ymaps-2-1-74-balloon__content {background: #222;}
.ymaps-2-1-74-balloon__tail:after {background-color: #222;}

.traffic-info-layer-view {background-color: #222;}
.checkbox_small {color: #eee;}
.checkbox_small__box {background-color: #222; border-color: #444;}
.checkbox_small:hover .checkbox_small__box {border-color: rgba(255,255,255,.3);}
.checkbox_small._checked .checkbox_small__box:hover {background-color: #888;}

.popup._type_balloon, .popup._type_normal {background-color: #222;}
.close-button._color_black {color: #eee;}
.close-button__img path {fill: #eee;}
.whats-here-preview__line {background: #444;}
.whats-here-preview__control-route-icon path {fill: #aaa;}
.whats-here-preview__control-search-icon path {fill: #eee;}

.context-menu {background-color: #222;}
.context-menu__item {color: #eee;}
.context-menu__item:hover, .context-menu__item:active {background-color: #333;}
.context-menu__icon {filter: invert(1);}

.ruler-balloon__content {background: #222; border-color: #444;}

.mrc-mini-player-view {border-color: #222;}

.dialog__container {background-color: #222;}
.full-objects-view__title {color: #ccc;}
.full-items-categories-view__category {color: #aaa;}
.mobile-app-share-view__qr {background-color: #282828;}
.dialog-close-button {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M12 0c6.627 0 12 5.373 12 12s-5.373 12-12 12S0 18.627 0 12 5.373 0 12 0zm3.707 9.707a1 1 0 1 0-1.414-1.414L12 10.586 9.707 8.293a1 1 0 0 0-1.414 1.414L10.586 12l-2.293 2.293a1 1 0 0 0 1.414 1.414L12 13.414l2.293 2.293a1 1 0 0 0 1.414-1.414L13.414 12l2.293-2.293z" fill="%23eee"/></svg>');}

.lang-switcher-view__text {background-color: rgba(34,34,34,.6);}
.services-view {background-color: #222;}

.ruler-balloon__content {background: #222; border-color: #444;}

/* Header Panel */

.search-form-view {background-color: #222;}
.small-search-form-view__pin {background-color: #222; background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24"><path fill="%23e00000" d="M14.88 9.75l4.46-2.42c0 3.54-2.47 7.59-4.64 11.07L11 24l1.67-10.33 2.21-3.92z"/><circle cx="12" cy="7.33" r="7.33" fill="%23f33"/><circle cx="12" cy="7.33" r="3" fill="%23222"/></svg>');}
.input._view_search {background-color: #222;}
.input._view_search .input__context {border-color: #222;}
.input._view_search .input__control {color: #eee;}
.input_air-search-large {background-color: #222;}
.input_air-search-large__context {border-color: #222;}
.input_air-search-large__control {color: #eee;}
.button_air-search-large__icon {background-color: #222; color: #eee;}
.button._view_air, .button._view_air-alt {background-color: #222;}
.button._view_search .button__icon {background-color: #222; color: #eee;}
.small-search-form-view__delimiter {background: #444;}
.small-search-form-view__icon._type_back {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path fill="%23eee" d="M9.416 14.01l3.953 3.952c.388.39.39 1.018-.004 1.41-.39.39-1.03.386-1.41.004L6.29 13.713C6.098 13.52 6 13.265 6 13.01l.004-.005c0-.256.097-.51.288-.7l5.663-5.663c.39-.39 1.018-.39 1.41.003.39.39.386 1.03.004 1.41L9.415 12.01H19.66c.552 0 1 .443 1 1 0 .55-.45 1-1 1H9.416z"/></svg>');}
