.small-search-form-view__icon._type_route {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="26" height="26" viewBox="0 0 26 26"><path fill="%23eee" d="M7.719 4.542a1.002 1.002 0 0 0-.421 1.664l3.501 3.501c.189.189.445.293.706.293l.246-.031a.993.993 0 0 0 .712-.681l1.501-5a1.003 1.003 0 0 0-1.246-1.246l-4.999 1.5zM14 22.999a3 3 0 1 0 0-6 3 3 0 0 0 0 6zM6.389 9.389c-1.61 1.415-1.839 3.929-.515 5.626 1.332 1.708 3.762 1.986 5.427.62l4.601-3.775a1.99 1.99 0 0 1 2.842.326c.706.905.581 2.263-.277 3.017l-.982.864a.958.958 0 0 0-.1 1.329c.33.395.91.441 1.296.103l.982-.864c1.612-1.416 1.842-3.928.516-5.626a3.797 3.797 0 0 0-5.426-.621l-4.601 3.774a1.988 1.988 0 0 1-2.843-.325c-.704-.903-.58-2.264.277-3.016l.982-.864a.93.93 0 0 0 .1-1.33c-.361-.433-1.123-.255-1.296-.102l-.983.864z"/></svg>');}
.small-search-form-view__icon._type_close {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="14" height="14" viewBox="0 0 14 14"><path fill="%23eee" d="M7 5.586L1.707.293c-.39-.39-1.024-.39-1.414 0-.39.39-.39 1.024 0 1.414L5.586 7 .293 12.293c-.39.39-.39 1.024 0 1.414.39.39 1.024.39 1.414 0L7 8.414l5.293 5.293c.39.39 1.024.39 1.414 0 .39-.39.39-1.024 0-1.414L8.414 7l5.293-5.293c.39-.39.39-1.024 0-1.414-.39-.39-1.024-.39-1.414 0L7 5.586z"/></svg>');}

.suggest-item-view {background: #222;}
.suggest__content {background: #222; border-top-color: #444;}
.suggest__group:not(:last-of-type) {border-bottom-color: #444;}
.suggest-item-view._selected {background: #333;}
.suggest-item-view__title em {color: #eee;}

.map-panorama-photo-control__element {color: #ccc;}
.map-panorama-photo-control__element._active, .map-panorama-photo-control__element:hover {background-color: #222;}

.button._view_air .button__icon, .button._view_air-alt .button__icon {color: #999;}
.button._view_air:not(._disabled):hover .button__icon, .button._view_air-alt:not(._disabled):hover .button__icon {color: #eee;}
.traffic-raw-icon__icon {color: #999;}
.traffic-raw-icon._color_none .traffic-raw-icon__icon path:nth-child(2) {fill: #999;}
.traffic-raw-icon:hover .traffic-raw-icon__icon {color: #eee;}
.traffic-raw-icon._color_none:hover .traffic-raw-icon__icon path:nth-child(2) {fill: #eee;}
.map-layers-control__icon._hovered .layers-icon-dark-polygon {fill: #666;}
.map-layers-control__icon .layers-icon-pale-polygon {fill: #999;}
.map-layers-control__icon._hovered .layers-icon-pale-polygon {fill: #eee;}

.map-layers-panel {background-color: #222;}
.map-layers-panel__layers {border-bottom-color: #444;}
.map-layers-selector {background-color: #333;}
.map-layers-selector__layer {color: #ccc;}
.map-layers-panel__modes {border-bottom-color: #444;}
.map-layers-button {color: #ccc;}
.map-layers-button:not(._with-checkbox):hover {background-color: #333; color: #eee;}
.traffic-icon {filter: invert(1);}
.map-layers-button:not(._with-checkbox):hover .map-layers-button__icon {color: #eee;}
.map-layers-button._active .map-layers-button__icon, .map-layers-button._active:hover .map-layers-button__icon {background-color: #333; color: #198cff;}
.map-layers-button._active, .map-layers-button._active:hover {color: #198cff;}

.popup._type_balloon .popup__arrow:before, .popup._type_normal .popup__arrow:before, .popup._type_map-hint .popup__arrow:before {background-color: #222;}
.popup._type_map-hint {background-color: #222;}
.map-layers-control-selector__item {color: #eee;}
.map-layers-control-selector__item:hover {background-color: #333;}
.map-layers-control__additional {border-top-color: #444;}
.parking-control, .bookmarks-control {color: #eee;}
.parking-control__text:hover, .bookmarks-control__text:hover {background: #333;}
.parking-control__text:before {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M6.846 3h10.308c1.337 0 1.822.139 2.311.401.489.261.873.645 1.134 1.134.261.489.401.974.401 2.311v10.308c0 1.337-.139 1.822-.401 2.311-.261.489-.645.873-1.134 1.134-.489.261-.974.401-2.311.401h-10.308c-1.337 0-1.822-.139-2.311-.401-.489-.261-.873-.645-1.134-1.134-.261-.489-.401-.974-.401-2.311v-10.308c0-1.337.139-1.822.401-2.311.261-.489.645-.873 1.134-1.134.489-.261.974-.401 2.311-.401zm4.154 11h1.5c1.929 0 3.5-1.567 3.5-3.5 0-1.929-1.567-3.5-3.5-3.5h-2.997c-.276 0-.5.224-.5.5l-.003 9.002c0 .276.224.5.5.5h1c.276 0 .5-.224.5-.5v-2.502zm0-5h1.5c.827 0 1.5.674 1.5 1.5 0 .827-.674 1.5-1.5 1.5h-1.5v-3z" fill="%23666"/></svg>');}
.bookmarks-control__text:before {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M6,7 L14,7 C15.1045695,7 16,7.89471188 16,9 L16,20.4995651 C16,20.7758871 15.7761424,20.9998904 15.5,20.9998904 C15.4223775,20.9998904 15.3458209,20.9818061 15.2763932,20.9470697 L10,18.3999996 L4.7236068,20.9470697 C4.47661755,21.0706446 4.17628103,20.9704673 4.0527864,20.7233174 C4.01807255,20.6538445 4,20.5772382 4,20.4995651 L4,9 C4,7.89471188 4.8954305,7 6,7 Z M8,5.5 L8,5 C8,3.8954305 8.8954305,3 10,3 L18,3 C19.1045695,3 20,3.8954305 20,5 L20,16.190983 C20,16.4671254 19.7761424,16.690983 19.5,16.690983 C19.4223775,16.690983 19.3458209,16.6729105 19.2763932,16.6381966 L17.5,15.5854599 L17.5,8.5999999 C17.5,6.72223177 16.2777678,5.5 14.3999996,5.5 L8,5.5 Z" fill="%23666"/></svg>');}

.feedback-menu-view__item {color: #eee;}
.feedback-menu-view__item:hover {background-color: #333;}
.feedback-menu-view__section:not(:last-child) {border-bottom-color: #444;}
.feedback-menu-view__icon path {fill: #666;}

.map-share-view__label {color: #eee;}
.social-share-view {filter: invert(1);}

.input_small {background-color: #222; box-shadow: inset 0 0 0 2px #444;}
.input_small:not(._focused):hover {box-shadow: inset 0 0 0 2px rgba(255,255,255,.3);}
.input_small__control {color: #eee;}
.input_medium {background-color: #222; box-shadow: inset 0 0 0 2px #444;}
.input_medium:not(._focused):hover {box-shadow: inset 0 0 0 2px rgba(255,255,255,.3);}
.input_medium__control {color: #eee;}
.button_small {background-color: #222; border-color: #444;}
.button_small:hover {border-color: rgba(255,255,255,.3);}
.button_small._checked:hover, .button_small._checked:not(:focus):hover {background-color: #888; border-color: #888;}
.button_small__text {color: #eee;}
.button_small__tick svg path, .button_small:hover:not(._disabled):not(._checked) .button_small__tick path {fill: rgba(255,255,255,.3);}
.map-share-frame-view {border-color: rgba(255,255,255,.2);}

.main-controls__menu-icon {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path fill-rule="evenodd" fill="%23eee" clip-rule="evenodd" d="M4 7c0 .55.4 1 .88 1h14.24c.49 0 .88-.44.88-1 0-.55-.4-1-.88-1H4.88C4.4 6 4 6.44 4 7zm0 5c0 .55.4 1 .88 1h14.24c.49 0 .88-.44.88-1 0-.55-.4-1-.88-1H4.88c-.49 0-.88.44-.88 1zm0 5c0 .55.4 1 .88 1h14.24c.49 0 .88-.44.88-1 0-.55-.4-1-.88-1H4.88c-.49 0-.88.44-.88 1z"/></svg>');}
.user-menu-view__menu {background-color: #222;}

.main-user-name-view__menu {background-color: #222;}
.main-user-name-view__menu-item {color: #ccc;}
.main-user-name-view__menu-item:hover {background-color: #333; color: #198cff;}

.user-menu-view__userpic {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24"><path fill="%23eee" d="M12 13c-2.6 0-4.5-2.7-4.5-6C7.5 4 9.2 2 12 2s4.5 1.9 4.5 5c0 3.3-2 6-4.5 6zm0 9c-4.4 0-7-.6-7.9-1.8a.8.8 0 0 1-.1-.4c.1-3.2 2.8-4.8 8-4.8s7.9 1.6 8 4.8l-.1.4c-1 1.2-3.5 1.8-7.9 1.8z"/></svg>');}
.user-account-view__avatar {background-color: #222;}

.user-menu-view__content {border-top-color: #444;}
.user-menu-item-view._with-icon {color: #eee;}
.user-menu-item-view._with-icon:hover {background-color: #333;}
.user-menu-item-view:hover {color: #198cff;}
.user-menu-view__separator {border-bottom-color: #444;}

/* Print */

.print-promo-banner-view {background-color: #181818;}
.print-promo-banner-view__image {filter: invert(1) hue-rotate(180deg) contrast(83%);}
.print-promo-banner-view__heading {color: #aaa;}
.print-promo-banner-view__actions-ignore-text {color: #aaa;}
.print-promo-banner-view__actions-ignore-text:hover {color: #eee;}

.print-view__first-page, .print-view__second-page {background-color: #282828;}
.button_medium__tick svg path, .button_medium:hover:not(._disabled):not(._checked) .button_medium__tick path {fill: rgba(255,255,255,.3);}
.select__dropdown {background-color: #222;}
.menu-item:not(._disabled):hover {background-color: #333;}
.menu-item._checked:not(._disabled-tick) .menu-item__text:before {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="11" height="8" viewBox="0 0 11 8"><path fill="%23eee" d="M3.293 7.707c.39.39 1.024.39 1.414 0l6-6c.39-.39.39-1.024 0-1.414-.39-.39-1.024-.39-1.414 0L3.998 5.6l-2.29-2.307c-.39-.39-1.025-.39-1.415 0-.39.39-.39 1.024 0 1.414l3 3z"/></svg>');}
.print-view__disclaimer {background-color: #222;}
.print-comment-view {background: #282828;}

.button_large {background-color: #222; border-color: #444;}
.button_large:hover {border-color: rgba(255,255,255,.3);}
.button_large__text {color: #eee;}
.button_large__tick svg path, .button_large:hover:not(._disabled):not(._checked) .button_large__tick path {fill: rgba(255,255,255,.3);}

/* Left Panel */

.sidebar-view {background-color: #181818;}
.sidebar-view__panel {background: #222;}

.home-panel-content-view__wrapper {border-bottom-color: rgba(255,255,255,.2);}
.catalog-small-item-view__icon {transition: .1s;}
.catalog-small-item-view__icon[style="background-color: rgb(255, 161, 94);"] {background-color: #cc7333 !important;}
.catalog-small-item-view__icon[style="background-color: rgb(96, 168, 240);"] {background-color: #2e73b8 !important;}
.catalog-small-item-view__icon[style="background-color: rgb(211, 141, 224);"] {background-color: #a34cb3 !important;}
.catalog-small-item-view__icon[style="background-color: rgb(156, 137, 250);"] {background-color: #6653c6 !important;}
.catalog-small-item-view__icon[style="background-color: rgb(141, 194, 87);"] {background-color: #693 !important;}
.catalog-small-item-view__icon[style="background-color: rgb(245, 140, 182);"] {background-color: #e06c9a !important;}

