style.css:
@-moz-document url-prefix("https://latex.telecomnancy.univ-lorraine.fr/project/") {
/*  TOP BAR */
.project-name .name {
  color: #ccc !important;
}
.toolbar.toolbar-header {
  background-color: #333;
  border-color: #222;
  box-shadow: 0 0 2px #111;
}
.toolbar .btn-full-height {
  border-color: #222 !important;
}
.toolbar .btn-full-height:hover {
  background-color: #444;
}
.fa {
  color: #ccc !important;
}
/* LEFT SIDEBAR */
.file-tree-inner {
  background-color: #222;
}
li .entity-name {
  color: #aaa !important;
}
li .entity-name:hover {
  background-color: #333 !important;
}
.toolbar.toolbar-alt {
  background-color: #333;
  border-color: #111;
}
h4 {
  border-color: #111 !important;
}
/* LEFT MENU */
#left-menu {
 	background-color: #333; 
}
#left-menu label {
 	color: #aaa !important; 
}
#left-menu a {
 	color: #eee !important; 
}
/* RESIZERS */
 .ui-layout-resizer {
  background-color: #222;
  border-color: #111;
}
.ui-layout-resizer .ui-layout-toggler:hover {
  background-color: #333;
}
.synctex-controls .btn-xs {
  background-color: #333;
  border-color: #111;
}
/* HIGHLIGHTING CTRL+F */
.ace_selected-word {
    border: 1px solid #f36411 !important;
    background-color: #985502 !important;
}
.ace_marker-layer .ace_selection {
    border-radius: 0 !important;
}
/* PDF VIEWER */
 .pdf-viewer .pdfjs-viewer {
  background-color: #222;
}
.pdf-viewer .pdfjs-viewer .page-container {
  background-color: #222;
  margin: 3px auto;
}
.pdf-viewer .pdfjs-viewer canvas, .pdf-viewer .pdfjs-viewer div.pdf-canvas {
  box-shadow: none;
}
.toolbar.toolbar-tall {
  background-color: #333;
  border-color: #111;
}
/* LOADING SCREEN */
.loading-screen {
  background-color: #333 !important; 
}
.loading-screen h3 {
  color: #aaa !important;
}
/* CHAT */
.messages {
  background-color: #222 !important;     
}
div.message {
  color: #ccc !important;
  background-color: #333 !important;
  box-shadow: -1px 2px 3px rgba(0, 0, 0, 0.5) !important;
}
  div.message a {
    color: #aaa !important; 
  }
.new-message {
  background-color: #333 !important;
  border-top: 1px solid #111 !important;
}
.new-message textarea {
  color: #ccc !important;
  background-color: #222 !important;    
  border: 1px solid #111 !important;
}
.date {
  border-color: #111 !important;   
}
}

@-moz-document url("https://latex.telecomnancy.univ-lorraine.fr/project") {
body, html {
  background-color: #333;
}
/* NAVBAR */
.navbar {
  background-color: #333;
  border: none !important;
}
.navbar-title {
  color: #aaa;
}
.navbar ul li a {
  color: #aaa !important; 
  border-color: #aaa !important;
}
.navbar ul li a:hover {
  background-color: #222 !important;
}
.navbar-toggle {
 	border-color: #aaa !important; 
}
.navbar-toggle:hover {
 	background-color: #222 !important; 
}
/* ASIDE */
aside ul li a {
  color: #aaa !important; 
}
li.active {
 	background-color: #111 !important;
}
/* CONTENT */
input.form-control {
  background-color: #333;
  border-color: #222;
}
input.form-control:focus {
  border-color: #aaa;
  box-shadow: none;
}
.fa {
  color: #aaa !important;
}
.content {
  background-color: #222;
}
.card {
 	background-color: #333;
  color: #aaa;
}
.card a {
 	color: #eee !important; 
}
.card li {
 	border-color: #222 !important; 
}
.card li:hover {
 	background-color: #222 !important; 
}
/* FOOTER */
.site-footer {
  background-color: #333;
  border: none !important;
  color: #aaa !important;
}
.site-footer a {
  color: #eee !important;
}
}
