style.css:
@-moz-document domain("2e.aonprd.com") {
body,
html {
    font-size: 16px;
    background:#333!important;
}
body,
h1,
h2,
h3,
h4,
h5,
h6,
.menutitle h1,
.menutitle h2,
.menutitle h3 {
        font-family: "Bookman Old Style", "Trebuchet MS", "Times New Roman", serif;
    color: #aab;
}
h1,
h2,
h3,
.menutitle h1,
.menutitle h2 {
    font-size: 1.25rem;
    padding: 0;
}
.menutitle {
    display:none;
}
.theme-toggle-wrapper{
    display:none;
}
body.dark,body.light,#page {
    color: #aab;
    border: 0!important;
}
#main-wrapper{
        background: #333!important;
}
body.dark .external-link,
body.light .external-link{
    color: #ddd;
}
#main-menu,
#main-menu a {
    background: 0;
    font-family: "Bookman Old Style", "Trebuchet MS", "Times New Roman", serif;
    color: #aab;
    line-height: 1.5;
    padding: 0;
}
body.dark #page,body.light #page {
    border: 0;
    background: #333;
}
img[alt="flourish"], #main-menu img.flourish {
    display: none
}
img[title="PFS Legal"] {
    opacity: 0.5;
    margin: 0 !important;
}
body.dark #main-wrapper,
#main-wrapper {
    background: #333;
    margin: 0;
    margin-left: 200px;
}
.old {
    margin-left: 0 !important;
}
#wrapper {
    margin: 0;
    width: 100%;
}
#main-menu #minMaxMenu {
    display: none;
}
#main-menu {
    background: 0;
    text-align: left;
    padding: 0;
    margin: 0;
    width: 200px;
}
#main-menu.old {
    padding: 0;
    background: none;
    width: 200px;
    line-height: 1.1;
}
#main-menu a {
    text-align: left;
    font-size: 80%;
    line-height: 1.1;
    margin: 0;
}
#main-menu.old .nav-parent {
    margin: 0;
    padding-left: 0;
}
#main-menu .nav-parent {
    background: 0;
    padding: 0;
}
.splash {
    display: none;
}
body {
    margin: 0;
    padding: 0;
}
hr {
    border: 0;
}
h1.title {
    background: #889;
    color: #333;
    line-height: 2;
    margin: 0;
    padding: 0;
    height: auto;
    padding-left: 1rem;
    margin-top: 1rem;
}
h1.title a {
    color: #333 !important;
}
body.dark h2.title,
body.dark h3.title,
body.light h2.title,
body.light h3.title{
    background: #667;
    color: #aab;
    line-height: 1.5;
    margin: 0;
    padding: 0;
    height: auto;
    padding-left: 1rem;
    margin-bottom: 1rem;
    margin-top: 1rem;
}
b,
a {
    color: #dde !important;
}
#main {
    line-height: 1.75;
}
h1 span,
h2 span,
h3 span {
    padding-right: 1rem;
}
.trait {
    background: #667;
    border: 2px solid #aab;
    margin-right: 0.25rem;
}
tr {
    background-color: #333 !important;
    border-bottom: 3px solid #333 !important;
}
table,
td {
    color: #aab;
    border: 0 !important;
}
td {
    border: 0;
}
body.dark .sidebar,
body.light .sidebar {
    background: #222;
    border: 5px solid #333;
    border-radius: 8px;
}
body.light .actiondark {
    filter:invert(1);
}
.search {
    padding: 0;
    margin: 0;
        height: 30px;

    width: 100%;
}
.search-input,button {
    box-sizing: border-box !important;
    height: 30px;
    padding: 3px !important;
    font-size:16px;
    border: 0 !important;
    border-radius: 0 0 0 0 !important;
}
#footer{
    display:none!important;
}
input,select{
    background:#333;
    color:#fff;
    border:1px solid #fff2;
    font-size: 18px;
    padding: 10px;
}
option{
    background:#333;
}
body.light .sidebar-nofloat,
body.dark .sidebar-nofloat {
    border: none;
    background-color: #444;
}
}
