style.css:
@-moz-document domain("facebook.com") {
body {
    background-image: url("http://i.imgur.com/RgbGh.png") !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
    background-position: top center !important;
    background-color: #5f83bc !important;
}
.widePage #blueBar #pageHead {
    margin-left: auto;
    margin-right: auto;
    width: 60%;
}
.fbTimelineUnit:hover {
    -webkit-transform: scale(1.1);
    box-shadow: 0px 0px 30px gray;
    opacity: 1;
    background-color: #fff;
}
#content,
.fbTimelineScrubber {
    margin-top: 50px !important;
}
.uiMediaThumb {
    opacity: 0.8;
    filter: alpha(opacity=80);
    -webkit-transition: opacity 0.5s linear;
}
.uiMediaThumb:hover {
    opacity: 1;
    filter: alpha(opacity=100);
    -webkit-transition: opacity 0.5s linear;
    -webkit-transform: scale(1.1);
    box-shadow: 0px 0px 30px gray;
}
#mainContainer {
    border: 8px solid white !important;
    background-color: #fff;
    border-radius: 15px;
}
.profile-picture {
    border: 8px solid white;
    -webkit-transform: rotate(-5deg);
    margin-left: -20px;
}
.profilePicThumb {
    -webkit-transform: rotate(-5deg);
    -webkit-transition-duration: 0.5s;
}
.profile-picture:hover,
.profilePicThumb:hover {
    -webkit-transform: scale(1.4);
    -webkit-transition-duration: 0.5s;
    position: relative;
    z-index: 5;
}
.profile-friends li:hover {
    -webkit-transform: scale(1.1);
    position: relative;
    z-index: 5;
}
.event_profile .event_upload_image {
    width: 179px;
    border: 8px solid white !important;
    -webkit-transform: rotate(-2deg);
    margin-left: -40px;
}
}
