style.css:
@-moz-document domain("meneame.net") {
/* bordes */
    div,a,span {
         border:none !important;
                                        } 
	button,select,form {
         border-color:#444 !important;
    }
    /* fondos */
     .menu01-itemsl span.button-new,.widget-popular-links-slider .link.slick-slide.slick-active,.section-profile .contents-layout,.pages a, .pages span,.news-summary .warn, .story-blog .warn,#toplink,.news-details a.comments,.sidebox .header,fieldset,.comment-body,.topbox,.header-top-wrapper,.comment-header, .comment-header a, .comment-header span, .comment-header strong, select,.sneaker-legend {
         background:#1b1b1b; /* !important;*/
    }
    /* fondos_secundarios */
     .comment.high .comment-body, .comment.high .comment-header, .comment.high .comment-header .comment-date, .comment.high .comment-header a, #toplink a.footer,.header-menu-wrapper,.news-shakeit *, .subname, .upname a:visited, .subname a:link ,legend, legend a, legend a:visited,.button, input[type="button"], input[type="submit"],.rich-edit-key {
         background: #999; /* !important;*/
    }
    /* botones */
     .section-profile .contents-layout,.sidebox-sub {
         background:#272727 !important;
    }
    /* botones */
     .voters-list,.menealo, .menealo a,.section-profile .contents-layout .contents-menu a.selected, tr:hover td, table.keyvalue tr:hover th, .section-profile .contents-layout .contents-menu a:hover, .section-profile .contents-layout .contents-menu a:hover span .sidebox .clics, .btn.btn-mnm.btn-inverted, .news-shakeit span,.news-body .box {
         background:#333 !important;
    }
    /* textos */
     .news-summary .warn, .story-blog .warn,.note, body,.news-content,p, span,.menealo span, .comment-text,.section-profile .contents-layout .contents-menu a span, select {
         color:#999; /* !important;*/
    }
.header-menu01 .selected a, .header-menu01 ul li a:hover { background:none; border-bottom:3px solid #ccc !important; margin-bottom:-3px; padding-bottom:6px; }
    /* enlaces */
     .comment-header a,.news-details, .news-details.main {
         color:#999 !important;
    }
    /* textos */
    .section,.section-profile-header-info p,.menealo span,.menealo a, a:not(.vote), .news-details button, .news-details span.karma-letter {
         color:#ccc !important;
    }
.news-details {
    background:#131313;
}
.news-summary.promoted-article .center-content { 
    background: #222; 
}
    /* votos */
     .sidebox .votes span, .sidebox .clics span,.header-menu01 a,.news-shakeit .votes a,.alert p,.comment.high .comment-header, .comment.high .comment-body .comment-text, .comment.high .comment-header .comment-date, .comment.high .comment-header a {
         color:black !important;
    }
    /* titulos */
     h1, h2, h3, h4, h4 a, h5, h6, .section-profile .contents-layout .contents-menu a span,.sidebox-sub .description {
         color:#999 !important;
    }
    /* extras */
    .comment.high .comment-text, .news-shakeit * /*::placeholder,*/ {
         color: #333 !important;
    }

     input, textarea,.sidebox .votes, .sidebox .clics {
         background-color: #c3c3c3 !important;
    }

     .subname.big {
         color:#fff !important;
         font-size: 10px;
         padding: 3px 12px;
    }
    /* .current,.item span {
         color:#ff6600 !important;
    }*/
     #footcol1,.sneaker {
         background:none;
    }
    /* perfil */
     .section-profile .contents-layout .contents-body,body,.topbox,.sidebox-rounded.orange .header {
         background:black;
    }
	#notifier_panel div a {
        color: #000 !important;
	}

	.sidebox .header {
        background-color: grey !important;
    }

	.sidebox .header h4 a{
        color: white !important;
    }

    .comment.comment-avatar-large  {
		background-color: #222;
        border-radius: 10px;
    }
    .comment.comment-avatar-large  .comment-body {
        background-color: #222; /*  !important;*/
    }
    .comment.comment-avatar-large  .comment-header, .comment.comment-avatar-large .comment-header span, .comment.comment-avatar-large .comment-header a {
        background-color: #222; /* !important;*/
    }
.cell .votes {
    background-color: grey;
    border-radius: 5px;
}

#notifier_panel ::before, .finished {
    color: #222 !important;
}

.notifications {
    border-radius: 30%;
}

.fa.fa-search {
    color: #ccc !important;
}


#footthingy p {
    border-color: white !important;
}

.sidebox div ul {
    background-color: #222;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}

.sidebox-rounded.orange .header {
    background-color: grey !important;
}

.sidebox-rounded.orange .header h4 a {
    color:white !important;
}

.sidebox .body {
    background-color: #222;
}

.alert-warning {
    color: #fff;
    background-color: #626262;
    border-color: #faebcc;
}

.btn.btn-mnm {
    white-space: nowrap;
    font-size: 12px;
    padding: 5px 15px;
    transition: all .3s ease .1s;
    background-color: #8a837f;
    color: white;
    margin: 0;
}

.btn.btn-mnm.btn-sm:hover, .btn.btn-mnm.btn-lg:hover {
    background-color: #222 !important;
}

.section.section-article-submit .btn-gray, .section.section-article-submit .story-blog-aside .dropdown-menu button{
    color:#222;
}

.commentform a.emoji-tab {
    color: #333;
    background-color: grey;
}

.commentform a.emoji-tab.active {
    color: white;
    background-color: #4d4846;
}

.emojiCat li {
    border: 1px solid #b9b4ab;
        border-bottom-color: rgb(185, 180, 171);
        border-bottom-style: solid;
        border-bottom-width: 1px;

}

.header-top-wrapper {
    background: #6d6767 !important;
}

.mini-icon-text i.fa.fa-edit {
    color: #fff;
}

#cboxContent {
    background-color: grey;
}

.clics {
    border-radius: 5px;
}

.header h4 span {
    color: white !important;
}

.news-details span.votes-up i, .news-details span.votes-anonymous i, .news-details span.votes-down i {
    color: #c8bfba;
}

.news-details a i, .news-details button i {
    color: #c8bfba;
    margin-right: 5px;
}

#comunicado .white_box {
    background: linear-gradient(to right, #84817f 0%, #484848 100%) !important;
}

.body.mainsites ul li, .popular-link-widget-right div.subtitle {
    color: #ccc;
}

a.vote.up::hover {
    color: green !important;
}

.comment.high.comment-avatar-large, .comment.high.comment-avatar-large .comment-body, .comment.high.comment-avatar-large .comment-header, .comment.high.comment-avatar-large .comment-header a, .comment.high .comment-header .comment-date {
    background-color: #808080;
}

.pages span.current {
    background-color: #999;
    color: #222;
}

.pages span {
    color: #999;
}

.pages a:hover {
    background-color: grey;
}

.pages span.nextprev {
    color: grey;
}

.news-shakeit .votes, .news-shakeit .clics, .news-shakeit.mnm-queued .votes, .news-shakeit.mnm-queued .clics {
    background-color: #9D9D9D;
}

.sidebox-rounded .show-responses, .news-tags {
    color: #bbb;	
}


ul#userinfo a.notifications span {
    color: #222;
}

#toplink p, .popular-link-widget-right div.description {
    color: #999;
}

.popular-link-widget-right a.meneame-button {
    background-color: #605955 !important;
}

div.article-container div#editor {
    background-color: #999;
    border-radius: 5px;
}

.sidebox-rounded .body {
    background-color: #222;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}

a.action-link, .action-text {
    background-color: #222;
    color: white;
}

#backTop {
    background: url(https://mnmstatic.net/v_149/img/common/uparr-48-w.png) no-repeat center center rgba(53, 51, 51, 0.8);
        background-size: auto;
    background-size: 24px 24px;
}
}
