style.css:
@-moz-document domain(ani.gamer.com.tw)
{
:root {
    --c00: #000;
    --c01: #fff;
    --c0: #cacdd7;
    --c1: #a5a8b1;
    --c2: #777c84;
    --c3: #45494f;
    --c4: #31363e;
    --c5: #1d1f22;
    --c6: #0c0f17;
    --rd: 0px;
    /*圓角*/
}
/* var(--c00)*/
/*----------------------------------------全站共用*/
::-webkit-scrollbar {
    width: 12px;
    height: 10px;
    border-radius: 0px;
    background-color: var(--c4);
    border-left: 1px solid var(--c00);
}
::-webkit-scrollbar:hover {
    background: var(--c4);
}
::-webkit-scrollbar-thumb {
    background-color: var(--c3);
    border: 0px;
    border-radius: 0px;
    border-left: 1px solid var(--c00);
}
::-webkit-scrollbar-thumb:hover {
    background: var(--c2);
}
body {
    /*整頁背景*/
    background: var(--c6);
}
input {
    /*輸入欄位 預設*/
    background-color: var(--c5);
}
.sky {
    /*網站標題區塊*/
    background-color: var(--c6);
}
.logo {
    /*網站Logo*/
    opacity: .6;
}
.search {
    /*搜尋*/
    border-radius: var(--rd);
    border: 0px;
}
.search button i {
    margin-top: 2px;
}
.search input[type="text"],
.search input[type="text"],
.search_result input[type="text"] {
    /*搜尋輸入欄*/
    color: var(--c1);
    border: 0px;
}
.search input[type="text"]:focus,
.search input[type="text"]:hover {
    /*搜尋輸入欄 hover+focus*/
    background: var(--c5);
}
.search button,
.search_result button {
    /*搜尋 按鈕*/
    background: var(--c5);
    color: var(--c2);
}
.search button:hover,
.search button:focus {
    /*搜尋 按鈕 hover+focus*/
    background: var(--c4);
    color: var(--c1);
}
ul.member a {
    /*功能 icon*/
    color: var(--c3);
}
ul.member a:hover {
    /*功能 icon hover*/
    color: var(--c2);
}
ul.member li.is-active i {
    /*功能 icon active*/
    color: var(--c2);
}
ul.member span {
    /*通知 數量 我改成小標示了*/
    font-size: 0px;
    border-radius: 20px;
    background: var(--c1);
    width: 10px;
    height: 10px;
    top: 10px;
}

.TOP-msg {
    /*小窗*/
    background-color: var(--c5);
    border: 1px solid var(--c00);
    color: var(--c2);
    font-size: 13px;
    line-height: 20px;
    letter-spacing: .1px;
}
.TOP-msglist {
    /*小窗 的 小窗？*/
    background-color: var(--c5);
}
.TOP-msglist .new {
    /*小窗 最新通知*/
    color: var(--c1);
    background-color: var(--c4);
}
.TOP-msglist a .msgname {
    /*小窗 重點文字？*/
    color: var(--c1);
}
.TOP-msglist a .link,
.TOP-msglist .title .link{
    /*小窗 連結文字*/
    color: var(--c1);
}
.TOP-msglist a:hover {
    /*小窗 連結文字 hover*/
    color: var(--c2);
}
.TOP-msglist a {
    /*小窗 連結文字*/
    color: var(--c2);
}
.TOP-msglist a .link:hover {
    /*小窗 連結文字 hover*/
    color: var(--c1);
}
#topBarMsgList_light_0 .TOP-msgpic img {
    /*小窗 圖示暗化*/
    opacity: .5;
}
div.TOP-msg > span {
    /*小窗 視窗標題*/
    background-color: var(--c4);
    color: var(--c1);
    border-bottom: 1px solid var(--c6);
}
.TOP-msglist div {
    /*小窗 視窗分隔線*/
    border-top: 0px;
    border-bottom: 1px solid var(--c6);
}
.TOP-msglist .empty-item p {
    /*空內容 文字*/
    color: var(--c1);
}
.TOP-msgbtn {
    /*小窗 下方按鈕*/
    background: var(--c4);
    border-top: 1px solid var(--c3);
}
.TOP-msgbtn a {
    /*小窗 下方按鈕 文字*/
    color: var(--c2);
}
.TOP-msgbtn a:hover {
    /*小窗 下方按鈕 文字 hover*/
    color: var(--c1);
    text-decoration: none;
}
.TOP-msglist div:hover {
    /*小窗 單則 hover*/
    background-color: var(--c4);
}
.TOP-msglist .title a {
    /*小窗 單則 標題*/
    color: var(--c1);
}
.TOP-msgbtn a:last-child {
    /*看所有通知 設定 中間的分隔線*/
    border-left: 1px solid var(--c2);
}
.TOP-msgbtn a:first-child {
    /*看所有通知 設定 中間的分隔線 去掉左邊的部分*/
    border: 0px;
}

li.head {
    /*頭像*/
    opacity: .5;
}
li.bahago {
    /*返回巴哈姆特*/
    opacity: .5;
}


.mainmenu {
    /*導覽列*/
    background-color: var(--c5);
    border-bottom: 1px solid var(--c00);
}
.mainmenu li {
    /*導覽列 目錄*/
    border-bottom: 1px solid var(--c00);
    background: var(--c5) !important;
    padding: 0;
    margin-right: 0;
}
.mainmenu a {
    /*導覽列 目錄 連結文字*/
    color: var(--c2);
}
.mainmenu a:hover,
.mainmenu a:focus {
    /*導覽列 目錄 連結文字 hover+focus*/
    color: var(--c1);
}
.mainmenu li a {
    width: 100%;
    padding: 0 15px;
}
.mainmenu li a:hover {
    background-color: var(--c4);
}
.mainmenu li[style*="background:"] {
    background: transparent !important;
}
.news_title {
    /*相關新聞 新聞標題*/
    color: var(--c2);
}
.news_big .readmore{
    /*詳全文 按鈕*/
    border: 0px;
    border-radius: var(--rd);
    color: var(--c1);
    background: var(--c4);
}
.news_big a:hover .readmore {
    /*詳全文 按鈕 hover*/
    color: var(--c1);
    background: var(--c3);
    border-radius: var(--rd);
}


.footer {
    /*頁尾*/
    background: var(--c5);
    color: var(--c2);
}
.footer a:hover {
    /*頁尾 連結文字 hover*/
    color: var(--c1);
}
.footer a {
    /*頁尾 連結文字*/
    color: var(--c2);
    text-decoration: none;
}

/*----------------------------------------首頁*/
body .BH_background .container .index_new .title .arrange .anime_date_list .btn_date_active {
    /*本季新番 或 已選中項目*/
    border: 0px;
    background-color: var(--c5);
}
body .BH_background .container .index_new .title .arrange .anime_date_list .date_item {
    border: 1px solid var(--c4);
    border-radius: var(--rd);
}
body .BH_background .container .index_new .title .arrange .anime_date_list .date_item span {
    color: var(--c2);
}
body .BH_background .container .index_new .title .arrange .anime_date_list .date_item:hover {
    border: 1px solid var(--c4);
    background-color: var(--c4);
}
body .BH_background .container .index_new .title .arrange .anime_date_list .date_item:hover span {
    color: var(--c1);
}


.arrange .now {
    /*排序按鈕 現在排序方式 文字*/
    color: var(--c2);
}
.arrange a {
    /*排序按鈕 文字*/
    color: var(--c3);
}
.arrange a:hover {
    /*排序按鈕 文字 hover*/
    color: var(--c1);
}


.title h1,
.only-title h1 {
    /*標題*/
    color: var(--c1);
    border-right: 1px solid var(--c3);
}

.whilebg {
    /*各種底色的樣子？？？*/
    border-top: 1px solid var(--c5);
    background: var(--c6);
}

body .BH_background .container .old_list .anime_btn_more i {
    /*更多...按鈕*/
    border: 0px;
    color: var(--c2);
    background-color: var(--c5);
    box-shadow: none;
    border-radius: var(--rd);
}
body .BH_background .container .old_list .anime_btn_more i:hover {
    /*更多...按鈕 hover*/
    border: 0px;
    color: var(--c1);
    background-color: var(--c4);
    box-shadow: none;
}

section.old_list,
section.news_list {
    /*首頁區塊分隔線*/
    border-top: 1px solid var(--c5);
}

.newanime .newanime-title {
    /*第一區 動畫 動畫標題*/
    color: var(--c01);
}
.newanime:hover .newanime-title {
    /*第一區 動畫 動畫標題 hover*/
    color: var(--c1);
}
body .BH_background .container .index_new .index_season .view-grid__item .newanime .newanime__content .newanime__content__info .newanime-playdate {
    /*第一區 動畫 周幾*/
    color: var(--c1);
    background-color: var(--c00);
    border-radius: 20px;
}
body .BH_background .container .index_new .index_season .view-grid__item .newanime .newanime__content .newanime__content__info .newanime-count i {
    /*第一區 動畫 觀看次數 icon*/
    -webkit-filter: contrast(0);
    filter: contrast(0);
}
.newanime .newanime-count {
    /*第一區 動畫 觀看次數*/
    color: var(--c2);
}
.newanime__order {
    /*第一區 動畫 愛心按鈕*/
    top: 5px;
    right: 5px;
    border-radius: 50px;
    width: 30px;
    height: 30px;
    background: var(--c5);
    color: var(--c1);
    font-size: .2em;
}
.newanime__order i {
    /*第一區 動畫 愛心按鈕 圖示*/
    position: relative;
    top: 1px;
    left: 0px;
    font-size: 14px;
}
.material-icons {
    /*第一區 動畫 愛心按鈕 圖示*/
    font-size: 24px;
    line-height: 1;
    letter-spacing: normal;
    text-transform: none;
    display: inline-block;
    white-space: nowrap;
    word-wrap: normal;
    direction: ltr;
}
.newanime__order.is-order,
.newanime__order:hover {
    /*第一區 動畫 已經按了的愛心按鈕 圖示*/
    background: var(--c01);
    border: 1.2px solid var(--c6);
    color: var(--c6);
}

.anime_list .new {
    /*其他區 上架共x集*/
    color: var(--c1);
}
.order {
    /*其他區 動畫 愛心按鈕 圖示*/
    top: 5px;
    right: 14px;
    border-radius: 50px;
    width: 30px;
    height: 30px;
    background: var(--c5);
    color: var(--c1);
    border: 1.2px solid var(--c5);
}

.order > i {
    /*其他區 動畫 愛心按鈕 圖示*/
    font-size: 16px;
    margin-left: 6px;
    margin-top: .4px;
}
.order:hover {
    /*其他區 動畫 愛心按鈕 hover*/
    background-color: var(--c01);
    border: 1.2px solid var(--c6);
    color: var(--c6);
}
.order.yes {
    /*其他區 動畫 已經按了的愛心按鈕*/
    background-color: var(--c01);
    border: 1.2px solid var(--c6);
    color: var(--c6);
}
/*----------------------------------------動畫分類*/
.class_list .now a {
    /*動畫分類 目前分類 文字*/
    background: var(--c4);
}
.class_list li a {
    /*動畫分類 分類 文字*/
    background: var(--c5);
    color: var(--c1);
}
.class_list li a:hover {
    /*動畫分類 目前分類 文字 hover*/
    background: var(--c4);
    color: var(--c1);
}
.page_number {
    /*頁次...*/
    color: var(--c3);
}
a.prev,
a.next {
    /*前一頁*/
    /*後一頁*/
    background: var(--c5);
    color: var(--c1);
}
a.prev:hover,
a.prev:focus,
a.next:hover,
a.next:focus {
    /*前一頁*/
    /*後一頁*/
    /*hover+focus*/
    background: var(--c4);
}
.page_number .now {
    /*目前頁次*/
    background: var(--c4);
}
.page_number a {
    /*頁次 文字*/
    background: var(--c5);
    color: var(--c1);
}
.page_number a:hover,
.page_number a:focus {
    /*頁次 文字 hover+focus*/
    background: var(--c4);
}

/*----------------------------------------新聞*/
.news_big .pic {
    /*新聞圖片 底色*/
    background-color: var(--c6);
}
.news_big span {
    /*新聞標題*/
    color: var(--c1);
}
.news_big p {
    /*新聞內文*/
    color: var(--c3);
}
body .BH_background .container .news_list .news_big li a span i {
    /*新聞 詳全文 按鈕*/
    background-color: var(--c5);
    border-radius: var(--rd);
}
.news_big i {
    /*新聞 詳全文 按鈕*/
    border: 0px;
    color: var(--c1);
}
body .BH_background .container .news_list .news_big li a:hover span i,
.news_big a:hover i {
    /*新聞 詳全文 按鈕 hover*/
    background-color: var(--c4);
    color: var(--c1);
}
.news_text li {
    /*側邊新聞 標題 圖標 ●*/
    background: none;
    border-bottom: 1px solid var(--c5);
}
.news_text li::before {
    /*側邊新聞 標題 圖標 ●*/
    content: "";
    background: var(--c4);
    width: 8px;
    height: 8px;
    position: absolute;
    margin-left: -13px;
    margin-top: 7px;
    border-radius: 10px;
}
.news_text a {
    /*側邊新聞 標題*/
    color: var(--c2);
}


/*----------------------------------------觀看紀錄*/
.center-container .history .user-watch-list .anime-card {
    background: var(--c5);
    border-radius: var(--rd);
    -webkit-box-shadow: none;
    box-shadow: none;
}
.history-anime-title {
    /*動畫標題*/
    color: var(--c1);
    width: 100%;
    border-bottom: 1px solid var(--c3);
}
.center-container .history .user-watch-list .anime-card .anime-info .history-lastwatch {
    /*觀看至*/
    color: var(--c2);
}
.center-container .history .user-watch-list .anime-card .anime-info .history-lastwatch .user-lastwatch {
    /*數字*/
    color: var(--c1);
}
.center-container .history .user-watch-list .anime-card .anime-info a {
    /*觀看下一集 文字*/
    color: var(--c2);
}
.center-container .history .user-watch-list .anime-card .anime-info a:hover {
    /*觀看下一集 文字 hover*/
    color: var(--c1);
}

.center-container .history .user-watch-list .anime-card .more-btn {
    /*展開按鈕*/
    background: var(--c4);
    color: var(--c1);
    border-radius: var(--rd);
}
.center-container .history .user-watch-list .anime-card .more-btn:hover {
    /*展開按鈕 hover*/
    background: var(--c3);
}
.center-container .history .user-watch-list .anime-card .history-list-triangle {
    /*展開 小箭頭*/
    border-color: transparent transparent var(--c4) transparent;
}
.center-container .history .user-watchTime-list .watch-list-card {
    /*展開*/
    background: var(--c5);
    border-radius: var(--rd);
    -webkit-box-shadow: none;
    box-shadow: none;
    border-top: 2px solid var(--c4);
}
.center-container .history .user-watchTime-list .watch-list-card .user-watch-textlist li a {
    /*展開 動畫 集數名稱*/
    color: var(--c1);
}
.center-container .history .user-watchTime-list .watch-list-card .user-watch-textlist li a .date {
    /*展開 動畫 觀看日期*/
    color: var(--c2);
}
.center-container .history .user-watchTime-list .watch-list-card .user-watch-textlist li {
    /*列表*/
    border-bottom: 1px solid var(--c4);
}
.center-container .history .user-watchTime-list .watch-list-card .user-watch-textlist li:hover {
    /*列表 hover*/
    background: var(--c4);
    border-radius: var(--rd);
}

.anig-tb{font-size: 12px;padding: 6px;background: var(--c4);color: var(--c1);border: 0px;
}.anig-tb:hover {background: var(--c3);color: var(--c01);}
/*----------------------------------------付費方案*/
.cart_header {
    /*無廣告版 底板*/
    background: var(--c5);
}
.cart_header h1 {
    /*無廣告版 標題*/
    color: var(--c1);
    margin-left: 15px;
}
.cart h1 {
    /*無廣告版 中標*/
    color: var(--c2);
}
.cart .program {
    /*方案區塊*/
    color: var(--c1);
    background: var(--c5);
}
.cart span,
.cart b {
    /*金額*/
    color: var(--c1);
}
.pay_select,
.pay_select select {
    /*方案選擇 下拉選單*/
    border: 0px;
    background: var(--c5);
}
.unselect .pay_select {
    /*方案選擇 下拉選單*/
    border: 0px;
}
.unselect .program {
    /*方案選擇 非選中方案*/
    border: 0px;
    background: var(--c5);
}
.selected .program {
    /*方案選擇 選中方案*/
    border: 0px;
    background: var(--c5);
}
.cart button {
    /*方案選擇 下一步*/
    background: var(--c4);
    color: var(--c1);
}
.cart button:hover {
    /*方案選擇 下一步 hover*/
    background: var(--c3);
    color: var(--c1);
}
input#code {
    /*序號兌換 輸入欄*/
    border: 0px;
    color: var(--c1);
}





/*2020 版*/

.container .payarea-content .paycard-area .paycard-container .paycard {
    /*方案區塊*/
    color: var(--c2);
    background: var(--c5);
}
.container .payarea-content h1{
    /*付費方案 標題*/
    color: var(--c2);
}
.container .payarea-content .paycard-area .paycard-container .paycard .titleline {
    /*方案區塊 分隔線*/
    background: var(--c4);
}
.container .payarea-content .paycard-area .paycard-container .paycard .paychose .triangle > div:nth-of-type(1),
.container .payarea-content .paycard-area .paycard-container .paycard .paychose .triangle > div:nth-of-type(2) {
    /*下拉選單 小箭頭*/
    background: var(--c2);
}
.container .payarea-content .paycard-area .paycard-container .userpaychose {
    /*下拉選單 區塊 外框線*/
    border: 1px solid var(--c4);
}
.container .payarea-content .paycard-area .paycard-container .userpaychose .paychose {
    /*下拉選單 文字和分隔線*/
    color: var(--c2);
    background: var(--c5);
    border-bottom: 1px solid var(--c4);
}
.container .payarea-content .paycard-area .paycard-container .userpaychose {
    /*下拉選單 底色*/
    background: var(--c5);
}

.container .payarea-content .paycard-area .paycard-container .paycard .paychose {
    color: var(--c1);
    background: var(--c4);
}
.container .payarea-content .paycard-area .paycard-container .paycard .paychose:hover {
    background: var(--c3);
}
.container .payarea-content .paycard-area .paycard-container .paycard.is-sp .paychose {
    background: var(--c4);
}
.container .payarea-content .paycard-area .paycard-container .paycard.is-sp .paychose:hover {
    background: var(--c3);
}
.container .payarea-content .paycard-area .paycard-container .paycard.is-btn {
    /*查詢購買紀錄 文字*/
    color: var(--c2);
}
.container .payarea-content .paycard-area .paycard-container .paycard.is-btn:hover {
    /*查詢購買紀錄 文字*/
    -webkit-box-shadow: 0 0 3px 3px var(--c3);
    box-shadow: 0 0 3px 3px var(--c3);
}
.container .payarea-content .paycard-area .paycard-container .paycard .number-key {
    /*序號輸入欄位*/
    border:1px solid var(--c2);
    color: var(--c2);
}
.container .payarea-content .paycard-area .paycard-container .paycard .paycard-nextbtn {
    /*下一步按鈕*/
    background: var(--c4);
    color: var(--c1);
}
.container .payarea-content .paycard-area .paycard-container .paycard .paycard-nextbtn:hover {
    /*下一步按鈕 hover*/
    background: var(--c3);
    color: var(--c1);
}
.pay_confirm {
    /*付款確認區塊*/
    background: var(--c5);
    color: var(--c2);
}
.pay_confirm h2 {
    /*付款確認區塊 小標和分隔線*/
    border-bottom: 1px solid var(--c3);
    color: var(--c1);
}
.pay_confirm p a {
    /*付款確認區塊 連結*/
    color: var(--c1);
}
