style.css:
@-moz-document domain("forum.minecorp.ru") {
/*/ Настройка /*/

:root {
    /*/ Цвет ссылок /*/
  	--links-color: #47AE42;
	/*/ Синий - #2E65FF. Зеленый - #47AE42 /*/
    
    /*/ Цвет ссылок в списке модераторов /*/
	--mod-list-color: #337D2F;
    /*/ Синий - #3657B3. Зеленый - #337D2F /*/
}

A, .hn.post-ident a, .online .author-ident .userstatus span, .report-post first-item, .fancy_spoiler_switcher_header, .main-item .item-subject .hn span, .main-item .info-lastpost A, .main-content .main-item .hn .item-status em.sticky  {
    /*/ Цвет ссылок /*/
    color:  var(--links-color);
    /*/ Синий - #2E65FF. Зеленый - #47AE42 /*/
}
.main-item .item-subject a {
    /*/ Цвет ссылок в списке модераторов /*/
	color: var(--mod-list-color);
    /*/ Синий - #3657B3. Зеленый - #337D2F /*/
}
.main-item .item-subject {
    /*/ Обычный текст /*/
	color: #d9d9d9;
}
.post div, h3.post-ident, h4.post-title, .posthead h3, .userstatus span, .sig-line {
    border: 0px solid #1c1c1c;
}
DIV.posthead H3.hn, DIV.posthead H3.hn  {
    color: #434343;
    border: 0px solid #1c1c1c;
    border-bottom: 0px solid #1c1c1c;
	background-color: #1c1c1c;
    background: #1c1c1c
}
.post div, h3.post-ident, h4.post-title, .posthead h3, .userstatus span, .sig-line {
    border-color: #e2e2e2;
}
body {
    background-color: #171717;
	background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(23,23,23,1) 0%, rgba(33,33,33,1) 90.1% );
    background-attachment: fixed;
    min-height: 100%;
    color: #f1f1f1;
}
.main-foot {
    border-radius: 0px 0px 5px 5px;
	border: 0px solid #47ae42;
    box-shadow: rgba(0, 0, 0, 0.3) 0px 0px 0px 0px;
    background-color: #1c1c1c;
    color: #424242;
}
.post, DIV.post-options, .posthead, .postbody, .post-contacts, .hn.post-ident {
	border: 0px solid #1c1c1c;
    border-left: 0px solid #1c1c1c;
    box-shadow: rgba(0, 0, 0, 0.3) 0px 0px 0px 0px;
    background-color: #1c1c1c;
    color: #424242;
}
.postbody {
	color: #f2f2f2;
}

.fancy_spoiler_switcher {
	background: #212121;
    background-color: #212121;

    border: 0px solid #47ae42!important;
    outline: 0px solid #ddd;
    border-radius: 5px 5px 5px 5px;
}
.post .fancy_spoiler_switcher_header STRONG {color: var(--links-color)}
.fancy_spoiler {color: #f2f2f2;
border-top: 0px dashed #47ae42!important;}
.brd .entry-content .codebox {
	background: #212121;
    background-color: #212121;
    border: 0px solid #47ae42;
    outline: 0px solid #ddd;
    border-radius: 5px 5px 5px 5px;
    border-left: 15px solid #333;
}
#pun_bbcode_bar input.image {
    border: 0px solid #1c1c1c;
    border-color: #5a5a5a;
    background-color: #1c1c1c!important;
    border-radius: 5px 5px 5px 5px;
}
    
#pun_bbcode_bar input.image:hover {
    border: 0px solid #1c1c1c;
  	background-color:#212121!important;
    border-radius: 5px 5px 5px 5px;
}
SPAN.sig-line {
    border-top: 1px solid #424242;
}
.postbody .sig-content STRONG {
	color: #424242;
}
.brd .entry-content .quotebox {
	background-color: #212121;
    background: #212121;
    border: 0px solid #212121;
    border-radius: 5px 5px 5px 5px;
}
#brd-stats ul li.st-users strong, ul li strong {
color: #f2f2f2;
}
.entry-content .quotebox, .entry-content .codebox {
    margin: 0.3cm 0cm;
    border: 0px solid #212121;
}
.brd .entry-content .quotebox {
	outline:  0px solid #212121;
}
IMG {
    border: 0px solid #212121;
	border-radius: 10px 10px 10px 10px;
}
.post STRONG {
	color: #c4c4c4;
}
.post-contacts A {
	color: #424242;
}
.author-ident .userstatus span {
    border-color: #2e2e2e;
	color: #2e2e2e
}
.online .author-ident .userstatus span{
    border-color: #2e65ff;
}
.brd .postfoot .post-actions .edit-post {
    background: rgba(0,0,0,0);
    border: 0px solid #1c1c1c;
    box-shadow: rgba(0, 0, 0, 0.3) 0px 0px 0px 0px;
    color: #424242
}
.brd .postfoot .post-actions .edit-post a {
    background: rgba(0,0,0,0);
    border: 0px solid #1c1c1c;
    color: #424242
}
.brd .postfoot .post-actions .edit-post:hover a {
    background: rgba(0,0,0,0);
    border: 0px solid #1c1c1c;
    color: #C4C4C4
}
.brd .postfoot .post-actions .report-post {
    background: rgba(0,0,0,0);
    border: 0px solid #1c1c1c;
    box-shadow: rgba(0, 0, 0, 0.3) 0px 0px 0px 0px;
    color: #424242
}
.brd .postfoot .post-actions .report-post a {
    background: rgba(0,0,0,0);
    border: 0px solid #1c1c1c;
    color: #424242
}
.brd .postfoot .post-actions .report-post:hover a {
    background: rgba(0,0,0,0);
    border: 0px solid #1c1c1c;
    color: #C4C4C4
}
brd-crumbs-top {
	color: #424242
}#brd-crumbs-top A {
	color: #424242
}
#brd-crumbs-end {
	color: #424242
}#brd-crumbs-end A {
	color: #424242
}
.main-item A {
	/*/ color: #618bff; /*/
}   
.main-item .info-lastpost {
	color: #f2f2f2;
}

.main-head {
    /*/ background-image: linear-gradient( 118.2deg,  rgba(243,156,18,1) 20.7%, rgba(255,0,97,1) 96.9% ); /*/
    /*/ background-image: radial-gradient( circle farthest-corner at 10% 20%,  rgba(23,23,23,1) 0%, rgba(28,28,28,1) 90.1% ); /*/
    background: #1c1c1c;
    border: 0px solid #47ae42;
    color: #FFFFFF;
    border-radius: 5px 5px 0px 0px;
}
.post{
	padding: 0px 0px 0px 0px;
	margin: 0cm 0cm;
}
.main-content .sticky {background: #1c1c1c;
border: 0px solid #363636}
p.ct-options.options{
    float: none;
    font-size: 0px;
	opacity: 0
    }
.pun_poll_item {
    border: solid 0px #eee!important;
    box-shadow: rgba(0, 0, 0, 0) 0px 0px 0px 0px!important;
    color:rgba(0, 0, 0, 0);
    background:rgba(0, 0, 0, 0)
}
.main-frm, .main-frm SPAN, .main-frm LABEL, .pun_poll_header {
    color: #f2f2f2;
	background:rgba(0, 0, 0, 0)
}

.main-subhead {
    box-shadow: rgba(0, 0, 0, 0.3) 0px 0px 0px 0px;
    /*/ background: url(http://vertexofvortex.github.io/files/minecorpdark-style/img/main-head-bg.png) repeat-x; /*/
    background: #1c1c1c;
    padding: 010px 0px 0px 0px;
    height: 0 px;
    line-height: 0px;
	margin: 0cm 0cm;
    border-left: solid 0px #363636;
    border-right: solid 0px #363636;
}
textarea#fld1, input#fld1, textarea#fld3, input#fld2, input#fld3, input#fld4, input#fld5, input#fld6, input#fld7, input#fld8, input#fld9, input#fld10, input#quest, input#fld11, input#fld12, input#fld13, input#fld14, input#fld15 {
    outline: 0px;
    color: #f2f2f2!important;
    background: #212121;
    border: 5px solid #212121;
    border-radius: 5px 5px 5px 5px;
    font-size: 12px
}
.main-extensions, #brd-stats, #brd-online, #brd-qpost {
    background: #1c1c1c;
    border: 0px solid #363636;
	border-radius: 5px 5px 5px 5px;
}
.main-category {
    
    border: 0px solid #47ae42;
    border-top: 1px solid #1d1d1d;
    background: #1c1c1c;
}
.paging {
    color: #424242
}
.paging A {
    color: #424242;
    border: 0px solid #47ae42;
	background: #1c1c1c;
}
.paging STRONG {
    color: #6d6d6d;
    border: 0px solid #47ae42;
	background: #1c1c1c;
}
.main-content {
    
    border: 0px solid #47ae42;
    border-top: 0px solid #1d1d1d;
    background: #1c1c1c;
    border-radius: 0px 0px 5px 5px;
}
.main-item, .main-item li, .main-item div, td {
        border-color: #1c1c1c;
}
#brd-navlinks {
    border: 0px solid #47ae42;
    box-shadow: rgba(0, 0, 0, 0.3) 0px 0px 0px 0px;
    background: rgba(0, 0, 0, 0);
}
#brd-navlinks li.isactive {
    background: #2e2e2e;
}
#brd-navlinks li:hover {
    background: #292929;
}
a:link, a:visited, .js_link {
    /*/color: #47ae42;/*/
}
li.info-posts strong {
    color: #fafafa;
}

li.info-topics strong {
    color: #fafafa;
}
#brd-announcement {
    color: rgba(0,0,0,0);
    background: rgba(0,0,0,0);
    border: 0px solid #363636;
    box-shadow: rgba(0, 0, 0, 0.3) 0px 0px 0px 0px;
}
#brd-announcement P Strong {
    color: #424242
}
#brd-announcement P A {
    color: #6d6d6d
}
#visit-links A {
    color: #424242
}
#brd-announcement P IMG  {
    height:0px;
    font-size:0px;
}
#brd-announcement P IMG A  {
    height:0px;
    font-size:0px;
}
div#brd-head.gen-content

B {
	color: #fafafa;
}
strong {
	color: #fafafa;
}

.main-content .redirect .icon {
    background: url(https://vertexofvortex.github.io/files/minecorpdark-style/img/status.png) -48px 0px no-repeat!important;
}

.main-content .new .icon {
    background: url(https://vertexofvortex.github.io/files/minecorpdark-style/img/status.png) -192px 0px no-repeat!important;
}

.main-content .sticky.closed .icon {
background: url(https://vertexofvortex.github.io/files/minecorpdark-style/img/status.png) -144px 0px no-repeat!important;
}

.main-content .sticky .icon {
