style.css:
@-moz-document url-prefix("https://www.eldarya.es"), url-prefix("https://www.eldarya.com.br/"), url-prefix("https://www.eldarya.de/"), url-prefix("https://www.eldarya.fr/"), url-prefix("https://www.eldarya.hu/"), url-prefix("https://www.eldarya.it/"), url-prefix("https://www.eldarya.pl/"), url-prefix("https://www.eldarya.ru/"), url-prefix("https://www.eldarya.com/") {
/* * * images used for this code are not my property, credit goes to their owners * * */
.flavr-button.purple, .button.button.button-purple { background-color: #8a2be2; border: 2px groove White!important; color: White }
.flavr-button.purple:hover, .button.button.button-purple:hover { background-color: #9370db; border: 2px groove White!important; color: White }
.flavr-button, .button.button { background-color: #ee82ee; border: 2px groove White!important; color: White }
.flavr-button:hover, .button.button:hover { background-color: thistle; border: 2px groove White!important; color: White }
.flavr-button.pink, .button.button.button-pink { background-color: #CCAACC; border: 2px groove White!important; color: White }
.flavr-button.pink:hover, .button.button.button-pink:hover { background-color: #CCCCCC; border: 2px groove White!important; color: White }
#header-menu a { font-family: Palatino Linotype, serif; font-weight: bold }
#header-portraits { width: 197px; height: 132px; background: none!important; }
#player-infos { background: transparent!important; left: -30px!important; margin-top: -15px!important; }
#player-headerPortrait { background: none; border-radius: 80px }
#player-headerPortrait a { top: 2.5px; left: 3px; border: groove 3px White; box-shadow: 0 0 30px 5px White }
#player-infos:after { background-image: none; }
#player-info-dots { top: 35px }
#player-info-dots .player-info-dot.lit { background: rgba(255, 255, 255, 0); border: 2px solid transparent; width: 15px; height: 15px }
#player-info-dots .player-info-dot.lit:after { background: transparent; content: '🌸'; font-size: 15px; line-height: 3px; text-align: left!important }
.tooltip-content { background-color: Plum; border: 2px solid White; }
#player-info-level { background: Plum; width: 40px; height: 40px; line-height: 40px; left: 85px; top: -5px }
#player-infos .player-name { margin-left: -20px; margin-bottom: 20px; -webkit-transform: none; border-radius: 10px; border-bottom: none; padding: 0 0 5px 0; color: White; font-weight: 600; font-family: Palatino Linotype, serif; letter-spacing: 1px; display: inline-block; border: 3px groove White !important; background: linear-gradient(to right, #F6A6FF, #FBE4FF, #F6A6FF)!important; left: 40px }
#player-infos .player-name a { font-style: oblique!important; letter-spacing: 1px }
#player-infos .player-currencies { border: groove 2px White; background: linear-gradient(to right, #F6A6FF, #FBE4FF, #F6A6FF)!important; margin-left: 55px; border-radius: 10px; border-top: none; font-style: normal!important; letter-spacing: 1px; padding: 0 12px 0 12px }
#player-infos .player-name .player-name-content { padding: 5px 10px 0 10px; }
#player-infos .player-name.guard-2 #player-guard { display: none }
#pet-headerPortrait #pet-headerPortrait-base { background: url(https://www.eldarya.es/static/img/pet/mood/profile/aaf130ffc85c65e9c760bee0833bd865~1428582301.png) no-repeat, url(https://i.pinimg.com/originals/3c/c3/e2/3cc3e2997475d39c738ce4057c8b28c8.png) no-repeat!important; background-size: 75%, 100%!important; border-radius: 20px; }
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td { font-family: Merriweather, serif!important; }
body, body.night { background: url(https://img.fonwall.ru/o/17/devushka-polet-kryilya-zamok.jpg) fixed top center!important; background-size: cover!important }
#main-menu .menu-label span { background-color: Thistle!important; }
#main-menu ul, #main-menu ul:after, #main-menu:after { text-align: center; box-shadow: none!important; background: transparent; border: none!important; }
#main-menu li { background-image: none!important; margin-top: 15px; margin-bottom: -45px; box-shadow: none; border: none }
#main-menu ul, #main-menu ul:after, #main-menu:after { text-align: center; box-shadow: none!important; background: transparent; border: none!important; }
#main-menu .menu-label { display: block; top: -5px }
#main-menu .main-menu-episode a, #main-menu .main-menu-minigames a, #main-menu .main-menu-mall a, #main-menu .main-menu-marketplace a, #main-menu .main-menu-ranking a, #main-menu .main-menu-library a, #main-menu .main-menu-messaging a, #main-menu .main-menu-bank a { background: transparent; height: 20px; }
#main-menu .main-menu-episode.selected, #main-menu .main-menu-minigames.selected, #main-menu .main-menu-marketplace.selected, #main-menu .main-menu-ranking.selected, #main-menu .main-menu-library.selected, #main-menu .main-menu-messaging.selected, #main-menu .main-menu-bank.selected { background: transparent; height: 30px; }
li.main-menu-episode, li.main-menu-minigames, li.main-menu-marketplace, li.main-menu-library, li.main-menu-messaging { margin-left: 10px!important; margin-right: 10px!important }
li.main-menu-bank.last { margin-left: -5px!important; }
li.main-menu-ranking { margin-left: 20px!important; margin-right: 20px!important }
#main-menu .main-menu-episode.selected, #main-menu .main-menu-minigames.selected, #main-menu .main-menu-marketplace.selected, #main-menu .main-menu-ranking.selected, #main-menu .main-menu-library.selected, #main-menu .main-menu-messaging.selected, #main-menu .main-menu-bank.selected { background: transparent; height: 68px; }
#container .page-main-container { background: #7b68ee8f!important; box-shadow: none!important; border: none!important; }
#container h1 { background: transparent; color: #7cddeae8 }
#container h1 .title-end { background: transparent }
#ranking-guards li.gold .ranking-guard-position { background: PaleGoldenrod; border-radius: 50px; line-height: 60px; height: 70px; box-shadow: 0 0 15px White }
#ranking-guards li.silver .ranking-guard-position { background: Silver; border-radius: 50px; line-height: 60px; height: 70px; box-shadow: 0 0 15px White }
#ranking-guards li.bronze .ranking-guard-position { background: Sienna; border-radius: 50px; line-height: 60px; height: 70px; box-shadow: 0 0 15px White }
#episode-menu > li > a, #episode-menu > li a:before, #episode-menu > li a::after { color: White; background: #e690dc; border: none; }
#episode-menu > li.active a, #episode-menu > li a:hover { color: #ff96f8; border: none; }
#episode-menu > li.active a:before, #episode-menu > li a:hover:before, #episode-menu > li a:hover::after, #episode-menu li.active a::after { border: none }
.episode-list .episode .episode-number, #episode-summary .episode-number { background: Plum; border-radius: 50px; line-height: 70px; height: 70px; box-shadow: 0 0 15px White }
.episode-list .episode .episode-description p, #episode-summary .episode-description p { color: Violet; letter-spacing: 1px; text-align: center; }
.episode-list .episode .episode-conditions, .episode-list .episode .episode-rewards { background-color: #85E3FF }
#header #header-top { background-color: rgba(251, 188, 247, .5); }
#footer { background-color: rgba(251, 188, 247, .5); }
.index-carousel { border-radius: 10px 35px 10px 35px; border: 3px groove White; background: linear-gradient(to left top, #957dade3 20%, #d291bce8 40%, #e0bbe4e8 60%, #fec8d8e6 80%, #ffdfd3e0 100%); }
.index-carousel-title { color: White; text-align: center!important }
.index-main-container .index-carousel-description { color: White; text-align: justify; font-size: 10px }
#index-ranking { border: 3px groove White !important; background: linear-gradient(to left, mediumslateblue, #5276e2, violet, plum, #ead4ea); }
#ranking-list { background: linear-gradient(0deg, #6c469f96 0%, #9d8ce49c 76%, #c8b7f8b8 96%); }
#index-news { text-align: center; background: linear-gradient(to top right, #7b68eea3, #5276e2a8, #ee82eead, #dda0dda8, #ead4ead1); border-radius: 10px; border: 3px groove White; }
#index-news .text a { color: dimgray; font-size: 11px }
#index-news .date { background: #ffbaff }
#index-player { right: 5px; border-radius: 10px 35px 10px 35px; border: 3px groove White }
#news-list { background: linear-gradient(to bottom right, #7b68eea3, #5276e2a8, #ee82eead, #dda0dda8, #ead4ead1) }
.marketplace-abstract { box-shadow: 0 2px 3px rgba(76, 82, 193, 0.5); background: linear-gradient(to top, rgb(229, 237, 255) 0%, rgb(255, 255, 255) 50%, rgba(255, 255, 255, 0.39) 100%) }
.marketplace-abstract:hover { background: linear-gradient(to top, rgb(195, 213, 255) 20%, rgb(255, 255, 255) 50%, rgba(236, 234, 234, .39) 100%) }
.marketplace-misc-info { color: #d586c4d4 }
.marketplace-abstract li { color: White }
#marketplace-itemDetail { top: 500px !important; right: -10px !important; }
.marketplace-abstract .abstract-name { font-weight: bold; font-size: 18px; }
.marketplace-abstract .abstract-time, .marketplace-sales-item #bid-count { font-size: 14px; color: #bb8dc6; font-weight: bold; }
.marketplace-abstract .abstract-type { font-weight: bold; font-size: 13px; color: #85ead2bd; }
.marketplace-assistance { background: #58c7ecb5; border-radius: 6px; margin: 15px 48px; padding: 15px; color: rgb(255, 255, 255); text-shadow: 0px 0px 2px white, 0px 0px 3px #000000; font-weight: bold; font-size: 14px; }
.marketplace-abstract h2 { color: #a8abf0; padding: 10px 0px 2px; margin: 0px 10px; border-bottom: 2px solid #787ddd; text-align: left; }
.marketplace-menu a { font-size: 14px; font-weight: bold; text-align: center; text-decoration: none; }
.marketplace-menu li.selected, .marketplace-submenu li.selected { background: #8bf5dcb8; }
#marketplace-pagination-container { position: relative !important; top: -600px !important; }
#marketplace-search > div.marketplace-search-list { position: relative !important; top: 30px !important; }
#ranking-guards li { float: left; width: 256px; padding-bottom: 10px; padding-top: 100px; background-color: #383838ab; position: relative; margin: 18px; border-radius: 10px; }
#ranking-prize-container { background: #7b68ee8f; }
#ranking-top-players .ranking-top-players-nickname a { color: lightpink }
#ranking-top-players .ranking-top-players-number { border: 2px groove DarkSlateBlue; background: MediumPurple }
#pet-info-name { font-size: 25px; font-family: Merriweater, serif; font-weight: 600 }
#pet-info-main { background-color: #fd96fd9e; position: relative; padding: 15px; border-radius: 10px; }
#pet-info-energy-level, #event-bonus-progressbar-fill { background-image: url("https://dumpshare.net/images/6400225714041gradient.png"); background-size: cover; }
#pet-portrait { border-radius: 100%; border: 3px solid #ffd4e4; }
#pet-info-evolve div.on { color: #fff87d; font-weight: bold; letter-spacing: 2px; font-size: 14px }
#pet-info-food, #pet-info-levels { background-color: #fd96fd9e; }
#exploration-place { background: #ff8fd0 }
#messageBarLevel { background-image: url("https://dumpshare.net/images/6400225714041gradient.png"); background-size: cover; }
#main-bonus-progressbar { background-color: #fd96fd9e; }
#treasurehunt-fold { background: #b66cff }
#treasurehunt-container h2 { border-right: 1px solid #b66cff; }
.appearance-item-info-name { text-transform: none; color: #1db5a6 }
#player-left-tab { background: linear-gradient(to left top, mediumslateblue, #5276e2bd, #ee82eec7, #dda0ddd1, #ead4ead6) }
}
