style.css:
@-moz-document url-prefix("https://4pda.ru/"), url-prefix("http://4pda.ru/") {
body {
    color: #ccc;
    /*текст этим цветом вылезает по страницам в самых неожиданных местах*/
    background: #262626!important;
    /*печальные попытки поменять цвет фона страницы ведутся тут не один час*/
}

/*Верх*/
div .menu {
    background: #2c2c2c!important;
    /*полоса сверху*/
}
.holder-no-hidden {
    background: #2c2c2c!important;
    /*полоса сверху*/
}
.menu-main-item {
    background: #2c2c2c;
    /*подложка главного меню*/
}
.menu-main-item:hover > a {
    background: #777;
    /*цвет при наведении на пункт главного меню*/
}
div.menu-sub {
    background: #777;
    /*подлжка выпадающего меню*/
}
a.menu-sub-item:hover {
    background: #2c2c2c;
    /*цвет при наведении на пункт выпадающего меню*/
}
a.menu-sub-item:focus {
    background: #2c2c2c;
    /*цвет при нажатии на пункт выпадающего меню*/
}
div.menu-user:hover > a {
    background: #777;
    /*цвет при наведении на меню юзера*/
}
.second-menu .menu-brands {
    background: #262626
}
.news-list p a {
    color: #ccc;
}

ul.dropdown-menu {
    background: #777;
    /*подлжка меню юзера*/
}
a.dropdown-menu {
    background: #777;
    /*подлжка пунктов меню юзера*/
}
div .dropdown-menu > li > a:hover,
div .events-popup > a:hover {
    background: #2c2c2c;
    /*цвет при наведении на пункт меню юзера_____надо разбить*/
}
span#events-count {
    background: #2c2c2c;
    /*цвет подложки счетчика уведомлений*/
}
.menu-search-input {
    background-color: #ccc
}
div .menu-search-button {
    color: #777
}
div .menu-search-button:hover {
    color: #2c2c2c
}
a.edit-link.btn {
    color: #ddd!important;
}
a.edit-link.btn:hover {
    color: #2c2c2c!important;
}
button.btn {
    color: #ddd!important;
}
button.btn:hover {
    color: #2c2c2c!important;
}

.content {
     background: #2c2c2c!important;
}
.btn-review-all {
     background-color: #2c2c2c!important;
}
/*Низ*/
.post {
    background: #2c2c2c;
    /*подложка поста*/
}
.post p {
    color: #ccc;
    /*цвет текста поста*/
}
.post .list-post-title a,
.post h2 a {
    color: #ddd;
    /*цвет заголовка поста*/
}
.post .list-post-title a:hover,
.post h2 a:hover {
    color: #777;
    /*цвет заголовка поста при наведении*/
}
.post .btn-more {
    background: #777;
    /*цвет подложки кнопки далее*/
}
.post .btn-more:hover {
    background: #aaa;
    /*цвет подложки кнопки далее при наведении*/
}
.bg-shadow {
    background: none;
    /*убираем градиент на окончании текста поста в предпросмотре*/
}
.side-box {
    background: #2c2c2c!important;
}
.side-box h2,
.rygxOpH5gAbQ h2 {
    color: #ddd;
}
.slider-list .title a,
.testimonials-list h3 a,
.testimonials-list .meta-box .name a,
.top-product-list .title a {
    color: #ccc;
}
.price-slider .ui-slider {
    background: #777;
}
.price-slider .ui-slider .ui-slider-range {
    background: #aaa;
}
.btn {
    background: #777;
    border: 1px solid #777;
}
.btn:hover {
    background: #aaa;
    border: 1px solid #aaa;
}
.testimonials-list {
    color: #ccc;
}
.top-product h3 a {
    color: #ddd;
}

/*Совсем низ*/
.footer-nav {
    background: #2c2c2c;
}
.footer-panel {
    background: #2c2c2c;
    padding: 24px 0;
    color: #fff;
}
.page-nav li.active a,
.page-nav a:hover {
    background: #777;
    color: #fff;
    text-decoration: none;
}
.footer-nav-list a {
    color: #ddd;
}
.f-box a {
    color: #ddd;
}

/*Посты*/
.news-list .number {
    background: #777;
}
.news-list .label {
    background: #aaa;
}
.product-detail {
    background: #2c2c2c;
    border-bottom: 0px solid #ca1919;
}
.product-detail h1 {
    color: #ddd;
}
.content-box h2 {
    color: #ddd;
}
.container {
    background: #2c2c2c;
    border: 0px solid #ccc;
}
.content-box p {
    color: #ccc;
}
.content-box li {
    color: #ddd;
}
.product-detail .info-holder .name a {
    color: #777;
}
.product-detail .more-box .meta a {
    color: #777;
}
.product-detail .more-box .number,
.product-detail .more-box .count-autor {
    background: #777;
}
.page-nav.box {
    background: #2c2c2c;
    border: 1px solid #ccc;
}
.page-nav a {
    color: #777;
}
.comment-box {
    background: #2c2c2c;
}
.comment-box .comment-list .nickname {
    color: #ddd
}
.comment-box p {
    color: #ccc;
}
textarea {
    background-color: #ccc;
}
.product-detail .photo img {
    width: 100.4%;
    /*костыль для рамки у фото материала*/
}

/*Анлогин*/
div .userevents {
    background: #777
}
div[class^="r"] {
    background: #2c2c2c;
}
.materials-box {
    color: #ddd;
    background: #2c2c2c;
}
.materials-slider .slider-item h3 a {
    color: #ccc;
}
.box-title {
    border-bottom: 0px solid #ccc;
}
#logostrip {
    background-color: #000;
}
.row1 {
    background: #2c2c2c!important;
}
.row2 {
    background: #2c2c2c;
}
.borderwrap table th {
    background-color: #555;
}
div .maintitle {
    background-color: #777!important;
    background: #2c2c2c!important;
}
td.catend {
    background: #2c2c2c!important;
}
table {
    border-collapse: collapse!important;
}
table.ipbtable,
tr.ipbtable,
td.ipbtable {
    background: transparent;
    color: #ccc;
}
a:link,
a:visited,
a:active {
    background: transparent;
    color: #ddd;
}
.borderwrap,
.borderwrapm {
    background: #FFF;
    border: 0px solid #2982cc;
}
table th,
.borderwrap table th,
.subtitle,
.subtitlediv,
.postlinksbar {
    border-bottom: 0px solid #5176b5;
    color: #ddd;
}
.forumdesc,
.forumdesc a:link,
.forumdesc a:visited,
.forumdesc a:active {
    background: transparent;
    color: #bbb;
}
hr.sfr {
    border: none;
    border-top: dotted 1px #aaa;
    margin: 5px 0px 3px 0px;
}
.copyright {
    background: #2c2c2c;
}
.toplinks span {
    background: #000;
    border: 0px solid #c2cfdf;
}
.formtable td.formtitle,
.formsubtitle {
    background: #2c2c2c;
}
.formsubtitle {
    color: #ddd;
}

/*div {
    border: 0px solid #fff!important ;
}*/
.borderwrap table th a:link,
.subtitle a:link,
.subtitlediv a:link,
.borderwrap table th a:visited,
.subtitle a:visited,
.subtitlediv a:visited,
.borderwrap table th a:active,
.subtitle a:active,
.subtitlediv a:active,
.borderwrap table th a:hover,
.subtitle a:hover,
.subtitlediv a:hover {
    color: #ddd;
}
#gfooter td {
    background: #2c2c2c;
    color: #eee;
}
.globalmesscontent {
    background: #2c2c2c;
    border: 0;
    color: #ddd!important;
}
.borderwrap h3,
.maintitle,
.maintitlecollapse {
    border-bottom-width: 0px;
}
h4 {
    display: none!important;
    border: 0px solid #c2cfdf!important;
}
#navstrip a:link,
#navstrip a:visited {
    color: #ddd;
}
#userlinks,
#userlinksguest {
    background: #2c2c2c;
    border: 0px solid #c2cfdf;
}
.usercp_menu,
.usercp_menu_out {
    color: #777;
    border: 0px solid #f0f5fa;
}
.popupmenu,
.popupmenu-new {
    background: #777!important;
    border: 0px solid #3a4f6c;
}
.popmenubutton a:link,
.popmenubutton a:visited,
.popmenubutton-new a:link,
.popmenubutton-new a:visited,
.popupmenu-item a:link,
.popupmenu-item a:visited,
.popupmenu-item-last a:link,
.popupmenu-item-last a:visited {
    color: #ccc;
    text-decoration: none;
}
.ipb-top-left-link a:link,
.ipb-top-left-link a:visited,
.ipb-top-right-link a:link,
.ipb-top-right-link a:visited {
    color: #777;
}
.pagelinklast,
.minipagelinklast {
    background: #777;
}
.minipagelink,
.minipagelinklast {
    border: 0px solid #c2cfdf;
}
.pagelink,
.pagelinklast,
.pagecurrent,
.minipagelink,
.minipagelinklast {
    background: #888;
    border: 1px solid #888;
}
.desc {
    color: #CCC;
}
.darkrow1 {
    background: #777;
    color: #ddd;
}
.g-btn.blue {
    background: #2c2c2c;
    border-color: #2c2c2c;
    color: #ddd;
    box-shadow: #2c2c2c 0 0px 0px -0px;
}
.popmenubutton-new,
.popmenubutton {
    width: 120px;
    white-space: nowrap;
    background-color: #777;
    border: 0px solid #3a4f6c;
    color: #ccc;
}
.input-warn,
.input-green,
.input-checkbox,
input,
textarea,
select {
    background: #ccc;
    border: 0px solid #4c77b6;
}
.textarea,
.searchinput,
.button,
button.editor_button,
.gobutton {
    background: #ccc;
    border: 0px solid #4c77b6;
}
.post2,
.bg3 {
    background: #2c2c2c;
}
.post1,
.bg1 {
    background: #2c2c2c;
}
td.formbuttonrow,
.pformstrip,
.borderwrap p.formbuttonrow,
.borderwrap p.formbuttonrow1 {
    background: #000 !important;
    border: 0px solid #fff;
    border-top: 0px solid #5176b5;
}
.post-edit-reason {
    background: #333;
    color: #888;
    border: 0px solid #c5c7cd;
}
.post-block > .block-title {
    color: #000;
    line-height: 2;
    min-height: 20px;
    max-width: 100%;
    margin: 0;
    padding: 3px 5px;
    background: #555;
    font-weight: bold;
}
.post-block > .block-body {
    background: #555;
    color: #ddd;
}
.post-block {
    border: 0px dotted #aaa;
    border-left: 4px solid #111;
}
.post-block > .block-title {
    color: #111;
}
.upopupmenu,
.upopupmenu-new {
    background: #777;
    border: 0px solid #fff;
}
.upopupmenu-item a:link,
.upopupmenu-item a:visited,
.upopupmenu-item-last a:link,
.upopupmenu-item-last a:visited {
    color: #ccc;
}
#submenu {
    color: #ccc;
}
.tablepad {
    background: #2c2c2c;
}
.maintitlecollapse {
    border-bottom: 0px solid #5176b5;
    color: #FFF;
    background: #2c2c2c;
    border-top-width: 0px;
    border-right-width: 0px;
    border-bottom-width: 0px;
    border-left-width: 0px;
}
.navbar {
    border-bottom: 0px solid #c6e0ff;
    background: #000;
}
.list-group .list-group-item {
    border-bottom: 0px solid #c6e0ff;
    background: #2c2c2c;
    background-color: #2c2c2c
}
.list-group .list-group-item:hover,
.list-group .list-group-item:focus,
.list-group .list-group-item:active {
    background: #777;
}
.list-group .list-group-item .bage,
.list-group .list-group-item .icon-close {
    background: #2c2c2c!important;
}
div#contacts {
    border-right: 0px solid #c6e0ff;
    background: #2c2c2c;
}
.footer {
    border-top: 0px solid #c6e0ff;
    background: #000;
}
.form-thread {
    background: #2c2c2c;
}
.btn.blue {
    background: #777;
    border-color: #777;
    color: #ddd;
}
.btn.blue:hover {
    background: #aaa;
    border-color: #aaa;
    color: #ddd;
}
.form-input {
    background: #ccc;
    border: 0px solid #c6e0ff;
}
.borderwrap h3 {
    background: #2c2c2c;
}
.borderwrap p {
    background: #2c2c2c;
    border: 0px solid #ccc;
}
.borderwrap {
    background: #2c2c2c;
    border: 0px solid #2982cc;
}
}
