style.css:
@-moz-document domain("tweetdeck.twitter.com") {
  section:first-of-type
  {
    width: 620px!important;
  }
section{
    width: 400px!important;
}

  html.dark .media-size-medium
  {
    height: 220px!important;
  }

  html.dark .more-tweets-glow
  {
    width: 300px!important;
    height: 5px!important;
    margin-top: -5px!important;
  }

  html.dark .column-header,
html.dark .column-header-temp
  {
    background-color: #222222;
    border-bottom: 2px solid #000;
  }

  html.dark .stream-item
  {
    background-color: #111111;
    border-bottom: 6px solid #000;
  }

  html.dark .app-columns-container,
html.dark .app-content,
html.dark .column-panel,
html.dark .column-nav-item,
html.dark .app-title,
html.dark .app-navigator,
html.dark .app-header
  {
    background-color: #000;
  }

  html.dark .column
  {
    background-color: #090909;
  }

  html.dark .column-header-link
  {
    color: #ff0066;
  }

  html.dark .avatar
  {
    border-radius: 20%;
  }

  .js-modal-panel
  {
    width: 753px!important;
  }

  html.dark .mdl-column-rhs
  {
    width: 100%;
  }

  html.dark .scroll-y
  {
    overflow-y: hidden;
  }

  html.dark .scroll-styled-h::-webkit-scrollbar-track,
html.dark .scroll-styled-v::-webkit-scrollbar-track
  {
    border-left: none;
  }

  html.dark .br--14
  {
    border-radius: 2px;
  }

  html.dark .quoted-tweet
  {
    border: 1px solid #252525!important;
    color: #fff;
    font-size: .9em;
    border-color: #252525!important;
  }
html.dark .quoted-tweet .url-ext {
	color: #ff0066;
}
html.dark .Button--primary {
    background-color: #ff0066!important;
    border: none!important;
    color: #fff!important;
}
html.dark .app-search-fake {
	color:ff0066!important;
	border: 1px solid #ff0066!important;
    color: #ff0066!important;
}
html.dark .compose, html.dark .old-composer-footer {
	background-color: #ff0066!important;
}
html.dark .btn-on-blue {
	background-color: #111111!important;
}
html.dark .js-spinner-button {
    background-color: #111111!important;
}
html.dark .bg-color-twitter-blue {
	background-color: #ff0066!important;
}
html.dark .app-nav-link {
	color: #ff0066!important;
}
html.dark .app-nav-tab {
	color: #ff0066!important;
}
html.dark .txt-mute, html.dark .txt-mute a {
	color: #606060!important;
}
html.dark .tweet-footer a {
	color: #606060!important;
}
html.dark .overlay, html.dark .ovl {
background: rgba(0, 0, 0, 0.95)!important
}
html.dark input, html.dark select, html.dark textarea {
	background: #252525!important;
}
html.dark .column-panel, html.dark .scroll-conversation {
	background-color: #000!important;
}
html.dark .inline-reply {
	background-color: #ff0066!important;
}
html.dark .tweet-detail-action:hover .reply-count {
	color: #ff0066!important;
}

html.dark ::-webkit-scrollbar {
  width: 4px!important;
  height: 4px!important;
}
html.dark ::-webkit-scrollbar-button {
  width: 0px!important;
  height: 0px!important;
}
html.dark ::-webkit-scrollbar-thumb {
  background: #dedede!important;
  border: 0px none #ffffff!important;
  border-radius: 50px!important;
}
html.dark ::-webkit-scrollbar-thumb:hover {
  background: #ffffff!important;
}
html.dark ::-webkit-scrollbar-thumb:active {
  background: #ffffff!important;
}
html.dark::-webkit-scrollbar-track {
  background: #000000!important;
  border: 0px none #ffffff!important;
  border-radius: 50px!important;
}
html.dark ::-webkit-scrollbar-track:hover {
  background: #000000!important;
}
html.dark ::-webkit-scrollbar-track:active {
  background: #000000!important;
}
html.dark ::-webkit-scrollbar-corner {
  background: transparent!important;
}
html.dark .thread {
	background-color: #ff0066!important;
}
html.dark .other-replies-link, html.dark .other-replies-link:hover {
	color: #ff0066!important;
}
html.dark .tweet-text a {
	color: #ff0066!important;
}
html.dark .color-twitter-blue {
	color: #ff0066!important;
}
html.dark a:active, html.dark a:focus, html.dark a:hover {
	color: #ff0066!important;
}
html.dark .compose-header {
	border-bottom: 1px solid #fff!important;
}
html.dark .dropdown-menu {
	background-color: #111111!important;
}
html.dark .caret-outer {
	border-bottom: 7px solid #111111!important;
}
html.dark .caret-inner {
	border-bottom: 6px solid #111111!important;
}
html.dark .js-account-manager-container a {
	color: #ff0066!important;
}
html.dark .js-dropdown-content a:hover {
	color: #fff!important;
}
html.dark .dropdown-menu .is-selected {
	background: #ff0066!important;
}
html.dark .js-column-back {
	color: #ff0066;
}
html.dark .detail-view-inline-text {
	background-color: #252525!important;
}
html.dark .conversation-more {
	color: #ff0066!important;
}
html.dark .column-nav-updates {
	color: #ff0066!important;
}
html.dark .is-new .column-type-icon{
    color:#ff0066;
}
}
