style.css:
@-moz-document domain("britannica.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.area_header,
.text-uppercase
{
	text-transform: capitalize!important;
}

.border,
.border-top-blue,
.border-top-green,
.border-top-orange,
.card,
.md-quiz-intro .md-intro-content,
.pb-15,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

#wod_home > .wrap_t > .w_text > .text_pron > .wod_text,

.area_header
{
	font-size: 2.5rem!important;
}

#qa_home > .question > .qholder > .actq,

.md-content,
.topic-paragraph,

body
{
	font-size: 1.85rem!important;
}

.font-14
{
	font-size: 1.2rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#footer .ad_holder,
#wrap_cr_br,
#wrap_cr_t,

.adthrive-ad
{
	display: none!important;
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.bc-homepage-dialogue
{
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.container
{
	max-width: none!important;
}

#wod_home > .wrap_t > .w_text > .text_pron
{
	width: auto!important;
}

#hp_cent_t > .hp_cent_box {
    width: 100%;
}

#wrap_cr,
#wrap_cr_bl
{
	width: 98%!important;
}

#wrap_c
{
	width: 75%!important;
}

}

