@-moz-document domain("britannica.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

.caption,
.credit,
.text-muted
{
	color: #a6dcf5!important;
}


}

@-moz-document domain("britannica.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.bg-navy,
.md-footer .footer-bck
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.bcLightboxTemplate,
.card,
.facts-page .md-page-wrapper,
.fancybox-skin,
.grey-box,
.list-page .md-page-wrapper,
.new-topic .md-page-wrapper,
.pb-15,
.popular-topics-story .md-page-wrapper,
.topic-collection .md-page-wrapper,
.topic-media-browse-page .md-page-wrapper
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.btn-blue,
.btn-outline-blue
{	
	background: radial-gradient(circle, #01748b 0%, #0C406C 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-blue:before,
.btn-outline-blue::before
{
	background: radial-gradient(circle, #0C406C 0%, #01748b 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn-blue:hover:before,
.btn-outline-blue:hover::before {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.btn-yellow,

a.subscribe-link
{	
	background: radial-gradient(circle, #651c03 0%, #e1410b 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-yellow::before,

a.subscribe-link:before
{
	background: radial-gradient(circle, #e1410b 0%, #651c03 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn-yellow:hover::before,

a.subscribe-link:hover:before
{
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.btn-green
{	
	background: radial-gradient(circle, #066503 0%, #3de10b 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-green::before
{
	background: radial-gradient(circle, #3de10b 0%, #066503 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn-green:hover::before {
  opacity: 1!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

#hp_cent,

.bg-white,
.card,
.fancybox-wrap
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#hp_cent:hover,

.bg-white:hover,
.card:hover,
.fancybox-wrap:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

.qa-treading-articles
{
	background: linear-gradient(to bottom, #14599d, #000 100%)!important;
}

/* Green Gradient 
_____________________________________________________________________________________________________*/

.qa-treading-features
{
	background: linear-gradient(to bottom, #00794a 0%, #000 100%)!important;
}

/* Orange Gradient 
_____________________________________________________________________________________________________*/

.qa-treading-lists
{
	background: linear-gradient(to bottom, #f87307 0%, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.area_header
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

.qa-treading-articles,
.qa-treading-features,
.qa-treading-lists
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

}

@-moz-document domain("britannica.com") {
/* Image Settings */

.card,
.col-md-25,
.mb-30,
.p-10,
.pt-10,

p
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.card:hover,
.col-md-25:hover,
.mb-30:hover,
.p-10:hover,
.pt-10:hover,

p:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

