}
::-moz-selection {
    background: transparent !important;
    color: transparent !important
}
::selection {
    background: transparent !important;
    color: transparent !important
}
:focus {
    outline: none !important
}
/*----- MENU & CO BACKGROUND-COLORS -----*/
 div[style="display: block;"], div[role="navigation"] {
