    color:#0abdc6
}

/* 2. Left colum */

/* 2.1. Left header */
.drawer__header {
    background: #133e7c;
}
.drawer__header a:hover {
    background: #091833;
    color: #ea00d9;
}
.drawer__tab {
    color: #0abdc6;
}

/* 2.2. Search bar */
.search__input {
    background: #091833;
