style.css:
@-moz-document domain("web.bale.ai"), domain("w2.bale.ai") {
* {
    /*font-weight:bold !important;*/
}
.app {
    max-width:100% !important
}
.toolbar__main {
    background-color: #2d9574;
}
.sidebar__quick-search .search-button-contain {
    background-color: #36997a;
    color: #fff!important;
}
.sidebar__buttons .more_vert {
    background: #3ba785;
}
.chat, .main {
    background-color: #1a262b;
}
.chat {
    background-repeat: repeat;
    background-image: none;
}
.message__body {
    background-color: #2d3238;
    box-shadow: none;
}
.message .text {
    color: #ffffff;
}
.message--sended .message__body {
    background: #006e56;
    border-radius: 15px 2px 15px 15px;
    box-shadow: none;
}
.chat__footer {
    background: #0c1417;
    border-top: 2px solid rgb(0, 110, 86);
}
.main .button--blue {
    background: linear-gradient(to right top, #2d2c72, #4ceeb5);
    color: #fff;
}
.chat__container {
    background-color: #121c20;
}
.recent__item__title {
    color: #e7e7e7!important;
}
.recent__item__title__subText {
    color: #3ba785;
}
.recent__item--active .recent__item__title__subText, .recent__item:hover .recent__item__title__subText {
    color: #3ba785;
}
.recent__item {
    border-bottom: 1px solid #3ba785!important;
}
.sidebar .recent__item--active, .sidebar .recent__item--active:hover {
    background-color: #253237!important;
    border-right: 5px solid #53cca6;
}
.recent__item--active .recent__item__title, .recent__item:hover .recent__item__title {
    color: #00fdc6!important;
}
.tabs-header__list__item {
    color: #ffffff;
    border-bottom: 2px solid #006e56
}
.tabs-header__list__item.active {
    border-bottom: 2px solid #43c89e;
    color: #3ba785;
}
.sidebar .recent__item:hover {
    background-color: #006e56!important;
    border-right: 5px solid #f6f6fb;
}
.recent__item--deActive {
    background: #00ffaf;
    color: black;
}
.recent__item__title__status {
    font-size: 12px;
    color: #acacac;
}
.toolbar__main.LEGAL, .toolbar__main.VERIFIED {
    background-color: #2d9574;
}
.message--active, .message:hover {
    background-color: rgba(199, 209, 214, 0.05);
}
.sidebar__header__user {
    background-color: #36997a!important;
    background: linear-gradient(to right top, #2d2c72, #4ceeb5);
}
.chat__footer .button--action {
    background-color: #ffffff0d;
}
.button--flat {
    color: #00ffae;
    background-color: rgba(70, 142, 229, .15);
}
.dropdown__menu {
    background-color: hsl(167, 100%, 22%);
}
.dropdown__menu__item {
    color: #00ffaf;
}
.dropdown__menu__item i {
    color: #00ffaf;
}
.dropdown__menu__item:hover {
    color: #fff;
    background-color: #36997a;
}
.message__hashtag {
    color: #00ffaf;
}
.message__mention {
    color: #00ffaf;
}
.message--sended .message__preview {
    background: #459b7f;
}
.message__preview__info {
    width: 4px;
    border-right: 0;
    background: #00ffaf;
}
.message__preview {
    background: #ebeff12b;
}
.compose__box {
    border-radius: 0;
    box-shadow: inherit;
}
.dropdown--small .dropdown__menu__item {
    color: #ffffff;
}
.dropdown--small .dropdown__menu__item:hover {
    color: #112e25;
    background-color: #00ffac;
}
.dropdown__menu__item:hover .icon, .dropdown__menu__item:hover a, .dropdown__menu__item:hover i {
    color: #2d9574;
    fill: #2d9574!important;
}
.dropdown__menu__item i {
    color: #ffffff;
}
.message__actions__menu--opened .icon {
    transform: rotate(0deg);
    color: #2d9574!important;
    fill: #2d9574!important;
}
.modal--fullscreen .modal__content {
    background-color: #222d33;
}
.input__material--filled label {
    color: #a0a0a0;
}
.input__material--filled input {
    box-shadow: 0 1px 0 0 #2e9271;
}
.profile .about label {
    color: #989b9d;
}
.picture-changer__controls a {
    color: #2d9574;
}
.button--rised {
    color: #fff;
    background-color: #4ceeb5;
    box-shadow: 0 2px 3px 0 rgba(0, 0, 0, .1), 0 2px 8px 0 rgba(0, 0, 0, .15);
    background: linear-gradient(to right top, #2d2c72, #4ceeb5);
}
.modal--fullscreen .modal__header {
    border-bottom: 1px solid #307a61;
    box-shadow: none;
    background: linear-gradient(to right top, #2d2c72, #4ceeb5);
}
.modal--fullscreen .modal__header h1 {
    color: #fbfbfb;
    text-shadow: 1px 1px 0px #000;
}
.button {
    color: rgba(204, 204, 204, 0.5);
}
.status--pending, .status--received, .status--sent {
    color: rgb(255, 255, 255);
}
.activity {
    background-color: #141d21;
}
.button--flat {
    border: 0px;
}
.button {
    color: rgb(212, 212, 212);
    background-color: rgba(255, 255, 255, 0.09);
}
.search {
    background-color: #06090a;
}
.search__results__item--message .title {
    color: rgb(162, 162, 162);
}
.search__results__item--message .content {
    color: rgba(206, 206, 206, 0.6);
}
.search__results__item--message .text .highlighted {
    color: #00ffc5;
}
.search__filter__item--active {
    color: #00ffae!important;
    box-shadow: inset 0 -2px 0 0 #00ffae !important;
}
.search__results__item {
    border-bottom: 1px solid rgba(181, 181, 181, 0.31);
}
.avatar__placeholder--lblue {
    background-color: #006e56;
}
.message .text a {
    color: #00ffb5;
}
.message .text a {
    color: #00ffb5;
}
.date-divider {
    color: #fff;
    background: rgb(47, 59, 119);
}
.message .message__sender a .message__sender__name {
    color: #00ffaf!important;
}
.sidebar-footer {
    background-color: #0c1417;
    box-shadow: 0 -2px 16px rgb(32, 49, 56);
}
.sidebar-footer__toggle-view-button {
    border-left: 1px solid #006e56;
}
.sidebar-footer__nav__button:first-child {
    border-left: 1px solid #006e56;
}
.compose__message, .compose--editing .compose__message {
    color: #ffffff;
    background: #27303a;
    border:0px;
}
.compose__box {
    background-color: #27303a;
}
.unread-divider:after {
    border-bottom: 2px dotted #00ffaf;
}
.unread-divider .text {
    background-color: #00ffaf;
}
.modal__header {
    background-color: #2d9574;
    color: white;
}
.modal__header h1 {
    color:white;
}
.preferences .modal__body {
    background-color: #141e22;
}
.preferences__body {
    background-color:#1a262b;
    color:white;
}
.preferences__tabs__content h4 {
    color: #ffffff9c;
}
.preferences__tabs__content ul li .hint {
    color: rgba(215, 215, 215, 0.3);
}
.preferences__tabs__tab--active {
    background-color:#00ffaf!important;
    color:black;
}
.preferences__tabs__content ul li .icon {
    color:white;
}
.modal__header .button {
    background-color: #36997a!important;
    background: linear-gradient(to right top, #2d2c72, #4ceeb5);
    box-shadow: 1px 1px 10px #00000082;
}
.radio label:before {
    box-shadow: inset 0 0 0 2px #2d9574;
}
}
