style.css:
@-moz-document url-prefix("https://4chan.org"), domain("4chan.org"), domain("4channnel.org"), url-prefix("https://boards.4channel.org"), url-prefix("https://boards.4chan.org"), url-prefix("https://www.4chan.org"), url-prefix("https://www.4channel.org") {
/*Variables*/
:root {
--bg: #fffff4;                  
--accent: #693232;
--dubs: #ffdd1a;
--greentext: #789922;
--logo: url(https://i.imgur.com/1KYBwWl.png);
--mascot:url(https://i.imgur.com/JHAFlqL.png);
}  

/*Body & global things */
* {
    text-decoration: none !important;
    line-height: 15px;
    font-family: Monospace, Arial, sans-serif !important;
    
}

body {
    width: 100vw !important;
}

hr {
    border: none !important;
}

.boardBanner,
#scroll-marker,
.boardTitle,
body {
    background-color: var(--bg) !important;
    background-image: var(--mascot) !important;
    background-repeat: no-repeat;
    background-size: 233px 251px;
    background-attachment: fixed;
    background-position: right bottom;
}

a,
a:visited {
    color: var(--accent) !important;
}

a:hover,
.file-info>a:hover,
a.replylink,
a.linkify:hover,
a.quotelink:hover,
#custom-board-list>a:hover,
div.post div.postInfo span.postNum a:hover,
.posteruid .hand:hover,
.fa:hover,
#file-n-submit > input:hover,
#filter-btn:hover,
.menu-button:not(.fa-bars):hover,
#menu>span:hover,
.dialog.submenu.focused>label:hover,
.dialog.submenu>label:hover,
a.embedder:hover {
    color: var(--dubs) !important;
}

.center
.globalMessage,
#blotter-msgs,
tfoot,
.navLinks,
.hide-reply-button,
.hide-thread-button,
.sauce,
.sideArrows,
.watch-thread-link,
.page-num,
.middlead.center,
#togglePostFormLink,
.postInfo>input[type=checkbox],
.bottomCtrl,
.pages.cataloglink,
.webm-title,
#tex-preview-button,
.move {
    display: none !important;
}

.notification.success,
.notification.info,
.notification.warning,
.notification.error {
    background-color: var(--bg) !important;
    color: var(--accent) !important;
    border: 1px solid var(--accent) !important;
    font-family: monospace !important;
    border-radius: 0 !important;
    text-shadow: none !important;
    box-shadow: none !important;
    margin-top: 15px !important;
}

#logo {
    background-image: var(--logo) !important;
    background-repeat: no-repeat;
    background-position: center;
    background-size: 120px 120px;
    margin-top: 10px;
    margin-bottom: 10px;
}

/* top of the page */
#header-bar {
    background-color: var(--bg) !important;
    border: 0 !important;
    border-bottom: .7px solid var(--accent) !important;
    height: 15px !important;
    box-shadow: none !important;
}

#thread-stats {
    color: var(--accent) !important;
    margin-right: 20px;
}

#updater {
    color: var(--accent) !important;    
}

#custom-board-list>a,
#custom-board-list,
.menu-button {
    color: var(--accent) !important;
}

.dead-thread,
.disabled:not(.replies-quoting-you) {
    opacity: 1 !important;
}

#scroll-marker {
    opacity: 0;
}

.boardBanner {
    margin-top: 20px;
}

#bannerCnt {
    border: 2px solid var(--accent) !important;
}

.boardTitle {
    margin-top: 30px !important;
    margin-bottom: 30px !important;
    letter-spacing: .01em !important;
    font-family: georgia !important;
    color: var(--accent) !important;
}

.qr-link {
    background: none !important;
    border: 2px solid var(--accent) !important;
    border-radius: 0 !important;
    color: var(--accent) !important;
}

/* thread */
.thread {
    margin-bottom: 10px !important;
    width: 63% !important;
    margin-left: auto !important;
    margin-right: auto !important;
    padding: 15px;
    background-color: transparent;
}

.opContainer {
    margin-bottom: 15px !important;
    padding: 5px;
}

/* Replies */
.post.reply {
    width:100% !important;
    margin-left: auto !important;
    margin-right: auto !important;
    margin-bottom: 16px !important;
    background-color: var(--bg) !important;
    border: 1px solid var(--accent);
}

.name,
.postNum>a,
.file-info,
.file-info>a, 
.fileText,
.summary,
.postNum>span,
.backlink,
.subject,
.quotelink,
a.replylink:not(:hover), 
div#absbot a:not(:hover),
.nameBlock.capcodeMod span.name,
span.capcodeMod a span.name,
span.capcodeMod span.postertrip,
span.capcodeMod strong.capcode,
.postMessage>a,
.linkify,
.postertrip,
#absbot,
.stub>a,
.embedder,
.pages>a,
.dateTime,
.video-controls>a,
.closed,
.new,
h4,
h2,
#arc-list .quotelink,
tbody,
#copyright,
header,
h3,
.content h3,
.box-content,
.boxcontent>ul,
p,
.content,
.boxcontent {
  color: var(--accent)  !important;
}

.quote {
    color: var(--greentext) !important;
}

s,
s a:not(:hover){
    background: var(--accent) !important;
    color: transparent !important;
}

s:hover {
    background-color: transparent  !important;
    color: var(--accent) !important;
}

.postMessage {
    color: var(--accent);
}

.postInfo,
.file {
    margin-left: 5px !important;
    font-size: 8.5pt;
}

.file-info {
    margin-left: -24px !important;
}

.fileText {
    margin-left: 18px !important;
    margin-top: -1px !important;
}

.dateTime {
    display: none;
}

.nameBlock:hover ~ .dateTime {
    display: initial;
}

.backlink {
    margin-right: 5px;
}

.fileThumb {
    margin: 5px 10px 10px 10px !important;
}

.quotelink.forwardlink,
.backlink.forwardlink {
    font-weight: bold;
}

/* image spoiler*/
a.fileThumb.imgspoiler>img {
    display: none;
}

.postContainer.opContainer a.fileThumb.imgspoiler {
    background-image: url("https://i.imgur.com/ocOuyL2.jpg");
    background-size: 110px 110px;
    width: 110px;
    height: 110px;
}

.postContainer.opContainer.expanded-image a.fileThumb.imgspoiler {
    height: initial !important;
    width: initial !important;
    margin-top: 20px;
}

.summary {
    font-size: 8.5pt !important;
}

.postMessage {
    text-align: justify !important;
    margin-left: 30px !important;
    margin-bottom: 10px !important;
    font-size: 10pt;
}

/* embedded post*/
.inline {
    border: 0 !important;    
}

.inline .post {
    background-color: var(--bg) !important;
    border: 1px solid var(--accent) !important;
    margin-right: 30px !important;
    border-radius: 0 !important;
}

/* backlink hover */
.post.reply.qphl {
    outline: 2px dotted var(--dubs) !important;
    border: 1px solid transparent !important;
}

.reply:target,
.reply.highlight {
    background-color: var(--bg) !important;
    border: 1px solid var(--accent) !important
}

:root.yotsuba-b .inline,
:root.yotsuba .inline {
    background: none !important;
}

.yotsuba-b .dialog,
.yotsuba .dialog {
    background: var(--bg) !important;   
    border: 1px solid var(--accent) !important;
    box-shadow: none !important;
}

#qp {
    background-color: var(--bg) !important;
    border: 0 !important;
    outline: 1.5px solid var(--accent) !important;
}

/*dubs highlight*/
.filter-highlight.opContainer, .filter-highlight > .reply {
    box-shadow: none !important;
    border: 1px solid var(--dubs) !important;
}

/*your post*/
:root.highlight-own .yourPost.opContainer,
:root.highlight-own .yourPost > .reply,
:root.yotsuba-b.highlight-own .yourPost > .reply {
    border-left:3px dotted var(--accent) !important;
}

/*(you)*/
:root.highlight-you .quotesYou.opContainer, :root.highlight-you .quotesYou > .reply,
:root.yotsuba-b.highlight-you .quotesYou > .reply {
    border-left:3px dashed var(--accent) !important;
}

/*(you) but in the watchlist*/
.replies-quoting-you > a,
.last-page > a > .watcher-page {
    color: var(--dubs) !important;
}

/* archived thread */
div.closed {
    letter-spacing: 1px;
    line-height: 23px !important;
    padding-top: 30px;
    padding-bottom: 30px;
    font-family: Georgia !important;
}

.warning {
    color: var(--accent) !important;
    font-weight: bold;
}

/* footer and pagelist */
.pagelist {
    background: none !important;
    border: 1px solid var(--accent) !important;
    margin-bottom: 10px !important;
    margin-left: 32vw !important;
    margin-right: 32vw !important;
}

.pages:not(a) {
    color: transparent !important;
}

strong>a {
    font-style: italic !important;
}

.next>a>button,
.prev>a>button {
    background-color: var(--bg) !important;
    border: 0 !important;
    padding: 4px !important;
    color: var(--accent)!important;

}

#absbot {
    text-transform: lowercase;
}

#absbot>span {
    background-color: var(--bg);
    padding: 3px;
}

/* quick reply */
#qr {
    border-radius: 5px;
    background-color: var(--bg) !important;
    border: 1px solid var(--accent) !important;
    margin-bottom: 40vh !important;
    box-shadow: none !important;
}

.persona>input[name=name] {
    background: var(--accent) !important;
    color: var(--accent) !important;
    border-radius: 5px 0 0 5px;
    border: 0 !important;
    padding: 2px;
    margin: 3px 0 3px 3px;
    text-align: center;
}

.persona>input[name=email] {
    background: var(--accent) !important;
    color: var(--bg) !important;
    border: 0 !important;
    padding: 2px;
    margin: 3px 0 3px 0;
    text-align: center;
    border-radius: 0 5px 5px 0;
}

.persona>input[name=sub] {
    background: var(--accent) !important;
    color: var(--bg);
    border-radius: 0 5px 5px 0;
    border: 0 !important;
    padding: 2px;
    margin: 3px 3px 3px 0;
    text-align: center ;
}

.textarea>.field,
.textarea>.field::placeholder {
    background: none;
    border-radius: 3px;
    font-size: 8.5pt;
    color: var(--accent) !important;
    border: none !important;
}

#file-n-submit>input[type="button"] {
    background: var(--accent) !important;
    color: var(--bg) !important;
    border-radius: 5px 0 0 5px;
    border: 0 !important;
    margin-left: 3px;
    margin-bottom: 3px;
}

#qr-filename-container {
    background: var(--accent) !important;
    color: var(--accent) !important;
    border-radius: 0 !important;
    border: 0 !important;
    text-align: center;
}

#file-n-submit>input[type="Submit"] {
    background: var(--accent) !important;
    color: var(--bg);
    border-radius: 0 5px 5px 0;
    border: 0 !important;
    margin-right: 3px;
}

.persona>input[name=name]::placeholder,
.persona>input[name=email]::placeholder,
.persona>input[name=sub]::placeholder,
#file-n-submit>input[type="button"]::placeholder,
#file-n-submit>input[type="Submit"]::placeholder, 
#qr-no-file,
.has-file #qr-filename {
    color: var(--bg) !important;
    font-weight: normal !important;
}

a#qr-oekaki-button,
a#url-button,
a#dump-button {
    color: var(--bg) !important;
    opacity: 1 !important;
}

/* thread Watcher */
#thread-watcher {
    border: 1px solid var(--accent) !important;  
    box-shadow: none !important;
}

#watched-threads {
    background: var(--bg) !important;
    border: 0 !important;
}

#watched-threads>.dead-thread>a,
#watched-threads>.replies-unread>a {
    color: var(--accent) !important;
}

div.dead-thread,
.dead-thread {
    opacity: .4 !important;
}

/* menus */
#menu,
.submenu,
#fourchanx-settings {
    color: var(--accent) !important;
    border-radius: 0 !important;
}

/* front Page */

/* logo */
#logo-fp {
    background-image: var(--logo) !important;
    background-repeat: no-repeat;
    background-position: center;
    background-size: 120px 120px;
    margin: 10px 0 10px 0;
}

#logo-fp>a>img {
    opacity: 0;
}

/* board list */
.box-inner {
    background: var(--bg) !important;
    color: var(--accent) !important;
    padding: 0 !important;
    border-left: .5px solid var(--accent) !important;
}

.top-box {
    background: 0 !important;
}

.boxbar {
    background: 0 !important;
    color: var(--accent);
    text-align: center;
    margin-top: 10px;
    margin-bottom: 10px;
}

li>a {
    color: var(--accent) !important;
}

.box-outer {
    border: 1px solid var(--accent) !important;
}

.warning {
    color: var(--accent) !important;
}

.c-thread img {
    border: 1px solid var(--accent) !important;
}

/* links at the bottom */

#ft li.current,
#ft li.fill {
    background: none !important;
    color: transparent !important;
}

#ft li {
    background-color: var(--bg) !important;
    border-color: var(--accent);
}

#ft ul {
    border-top: 0;
}

.stat-cell {
    display: inline;
    margin-right: 40px;
}

/* internal archive */
.postblock {
    padding: 5px;
    background-color: var(--bg);
    color: var(--accent);
    border-color: var(--accent);
}

table.flashListing tr:nth-of-type(odd) {
    background-color: transparent;
}

td {
    background-color: var(--bg);
}

/* 4chan x gallery */
#a-gallery {
    background-color: #000000de;
}

a.gal-start,
a.gal-stop,
.gal-buttons>a.menu-button,
a.gal-close,
.gal-count,
a.gal-name,
.gal-thumbnails {
    color: var(--bg) !important;
}

.gal-name:hover,
.gal-buttons a:hover {
    color: var(--dubs) !important;
}

.gal-prev,
.gal-next {
    display: none;
}

:root.gal-fit-height:not(.gal-pdf):not(.gal-hide-thumbnails) .gal-buttons,
:root.gal-fit-height:not(.gal-pdf):not(.gal-hide-thumbnails) .gal-name,
:root.gal-fit-height:not(.gal-pdf):not(.gal-hide-thumbnails) .gal-count {
    right: 154px !important;
}

.gal-thumbnails {
    border-left: .7px solid var(--bg) !important;
    background: 0 !important;
}

.gal-highlight {
    background: 0 !important;
}

.gal-highlight>img,
.gal-highlight>video {
    background: 0 !important;
    padding: 4px;
    border: 1px solid var(--bg);
}

.gal-fit-width .gal-image img,
.gal-fit-width .gal-image video {
    max-width: calc(100% - 10px) !important;
}

.gal-image img,
.gal-image video {
    margin-left: 4px !important;
    margin-right: 4px !important;
}

/* various elements */
.right-box,
.left-box,
.content h3{
    background-color: var(--bg);
}

.content h3 {
    border-color: var(--accent);
}

hr,
.left-box hr,
.right-box hr {
    background-color: var(--accent) !important;
    color: var(--accent);
}

dt.first,
.boxcontent dd,
dt {
    color: var(--accent);
    border-color: var(--accent);
}

#announce,
#disclaimer-dialog {
    background-color: var(--bg);
    color: var(--accent);
    box-shadow: none;
}

#disclaimer-dialog .hd {
    border-bottom: 1px solid var(--accent) !important;
    background: none;
    padding: 3px;
    font-size: 9pt;
}

button {
    background: var(--bg);
    border: 1px solid var(--accent);
    padding: 3px;
    color: var(--accent);
}

h1 {
    letter-spacing: 1.5px;
    line-height: 23px;
}

/* catalog */

:root:not(.werkTyme) .catalog-thread.watched .catalog-thumb,
:root:root.werkTyme .catalog-thread.watched:not(:hover),
:root:root.werkTyme:not(.catalog-hover-expand) .catalog-thread.watched,
:root.werkTyme.catalog-hover-expand .catalog-thread.watched > .catalog-container:hover > .catalog-post {
    border: none !important;
}

.catalog-stats {
    color: var(--accent);
}

.postContainer.catalog-container,
.thread.catalog-thread {
    max-width: 300px !important;
    margin: 0 10px 10px 5px !important;
    background-color: var(--bg);
    border-radius: 5px;
}

.thread.catalog .postInfo.desktop,
.thread.catalog-thread .postMessage {
    text-align: center !important;
    margin-top: 4px;
    margin-left: 0 !important;
}

.catalog-link {
    height: 160px;
}

.catalog-link>img {
    max-height: 90%;
    vertical-align: middle !important;
}

.catalog-thumb {
    box-shadow: none !important;
    outline: 1px solid var(--accent);
    border-radius: 0 !important;
}

:root.yotsuba-b.catalog-hover-expand .catalog-container:hover > .post,
:root.yotsuba.catalog-hover-expand .catalog-container:hover > .post {
    display: none;
}
}

