style.css:
/* i really want this to be global */
#page-homepage,
#page-homepage>.page-wrapper,
.page-wrapper,
.the-article .main,
#page-video,
#video-featured,
#page-video>section.white-bg,
#article-nextreads,
.the-article.layout-no-sidebar.type-picture .the-article-body figure.video,
.the-article.layout-no-sidebar.type-picture .the-article-body table.panorama,
.the-article.layout-no-sidebar.type-picture .the-article-body table.picture,
html,
.the-article-body table.audio td.pic,
.the-article-body table.panorama td.pic,
.the-article-body table.picture td.pic,
.the-article.type-hasvideo.layout-no-sidebar .the-article-body table.gallery .caption,
.the-article.type-text.layout-no-sidebar .the-article-body table.gallery .caption,
footer,
.comment-widget .comment .comment-form.preview,
.the-article-body,
.the-article-body>*,
.the-article-body a,
.the-article-body figure.cms-video.video,
.the-article-breadcrumb,
body:not([id]),
#section-publish .section-content .article-list .article-item {
	background-color: var(--bg-color-second, #2E3440)!important;
}

.article-item .article-title a,
#widget-ticker .latest-news ul li a,
#section-featured div[data-content=newsfeatured] .article-item:first-child .article-summary,
.article-item .article-related>li a,
.the-article-body,
.the-article-summary,
.the-article-title,
.the-article-category a,
#widget-weather em,
#widget-weather strong,
#widget-weather span,
.the-article.type-picture .the-article-body table.picture,
.section .section-title h2,
.section .section-title h3,
.article-item .article-summary,
.the-article-body .article-item .article-summary,
.comment-widget .comment .comment-meta,
.the-article-meta .the-article-author a,
.the-article-meta .the-article-author span,
.the-article.layout-no-sidebar.type-picture .the-article-body figure.video .caption,
.the-article.layout-no-sidebar.type-picture .the-article-body table.panorama .caption,
.the-article.layout-no-sidebar.type-picture .the-article-body table.picture .caption,
.section .section-title ul li a,
.the-article-body p.question,
.the-article.type-hasvideo.layout-no-sidebar .the-article-body table.gallery .caption,
.the-article.type-text.layout-no-sidebar .the-article-body table.gallery .caption,
.the-article.type-hasvideo.layout-no-sidebar .the-article-body table.picture .caption,
.the-article.type-text.layout-no-sidebar .the-article-body table.picture .caption,
.comment-widget .comment-list>.btnMore,
#zing-header .category-menu li>a,
a,
.box-category .category-title a,
.comment-widget .comment .comment-meta .author,
#innerarticle:after,
.the-article-tags,
.the-article-tags:before,
.the-article-tags strong,
.comment-widget .comment-list .list-header .list-title,
.wiki-author,
.the-article.layout-no-sidebar .the-article-body>h1,
.the-article.layout-special .the-article-body>h1,
.the-article-body .notebox {
	color: #fff;
}

#section-multimedia:before,
.section.videobox .section-sidebar,
#page-article.layout-special .the-article.layout-special .the-article-comment {
	background-color: #660066 !important;
}

table.picture.gallery .photoset-item:last-of-type {
	margin: auto !important;
}

.the-article.type-hasvideo.layout-no-sidebar .the-article-body table.picture .caption,
.the-article.type-text.layout-no-sidebar .the-article-body table.picture .caption {
	text-align: center;
}

.comment-widget .comment-list>.btnMore {
	text-shadow: 1px 1px 0px #000;
	background-color: #333 !important;
}

#zing-header {
	background-color: #BF616A !important;
	border-bottom: 0px solid #4C566A;
	Height: 53px;
}

#zing-header .logo {
	background-color: #BF616A !important;
	padding: 4px;
}

#zing-header .category-menu .more .dot,
#zing-header .category-menu .more .dot:before,
#zing-header .category-menu .more .dot:after {
	background-color: #fff !important;
}

:hover {
	transition-duration: 500ms;
}

#zing-header>.page-wrapper .category-menu li.parent.current>a,
#zing-header>.page-wrapper .category-menu li.parent:hover>a,
#zing-header .toolbox .searchform-wrap button span:hover,
.article-item .article-title a:hover,
.article-item .article-thumbnail:hover+header .article-title a,
a:hover{
	color: #EBCB8B;
}
.article-item .article-thumbnail:hover+header .article-title a {
	color: #EBCB8B;
}
.article-item .article-title a,
.the-article-title {
	color: #A3BE8C;
}

#zing-header>.page-wrapper .category-menu li.parent.current::after,
#zing-header>.page-wrapper .category-menu li.parent:hover::after,
#widget-ticker .corona-event,
.the-article-tags strong {
	background-color: #EBCB8B;
}

#widget-ticker .corona-event strong {
	background-color: #B48EAD;
	border: 1px solid #B48EAD;
	Color: #ECEFF4;
}
.article-item .article-thumbnail img:hover,
.article-item .article-thumbnail video:hover {
	transform: scale(1.1);
    transition-duration: 2s;
}
.comment-widget .comment-form .comment-author strong,
.article-item .article-meta .category, .article-item .article-meta .sponsor,
#page-article .sticky-header .sticky-header__title p>span {
	color: #BF616A;
}
#page-article .sticky-header.sticky-header--show {
	background: #A3BE8C;
    border-bottom: 1px solid #A3BE8C;
}
