style.css:
@-moz-document domain("forum.onligamez.ru") {
  #logo,
  .PanelScroller,
  .siropuChat_h3,
  footer,
  .uSocial-Share,
  .sidebar,
  #Block1st,
  .home,
  .pageNavHeader,
  .sharePage,
  .siropuChatAds,
  .b-page_newyear,
  #siropuChatHeader div span {
    display: none !important;
  }
  html#XenForo {
    background: url(https://creative-moscow.ru/assets/images/background-3.jpg)
      no-repeat #1d1e22;
    background-size: cover;
    min-height: 100%;
    background-attachment: fixed;
  }
  .siropuChatArchive .username {
    color: green;
    font-weight: bold;
  }
  .section label {
    color: white;
  }
  .pageWidth {
    background: #101012;
  }
  #header {
    background-image: none;
  }
  .resourceInfo h1 {
    color: lime;
  }
  .messageUserBlock .avatar img {
    border: none;
  }
  .xenForm {
    color: white;
  }
  .titleBar h1 {
    color: rgb(152, 12, 12);
  }
  .pageContent .breadcrumb {
    background: #333641;
  }
  .linkGroup a,
  .siropuChatMessages,
  .siropuChatArchive {
    color: white;
  }
  #siropuChatBannedMessage {
    color: white;
  }
  #moderatorBar {
    background: none;
    border: none;
  }
  .nodeList .categoryStrip {
    background-color: #333641;
  }
  .redactor_box .redactor_smilies {
    background: #333641;
    padding: 5px;
  }
  .OverlayCloser {
    color: white;
  }
  .siropuChatUsers > li:last-child {
    color: white;
  }
  .redactor_box .redactor_smilies .tabs {
    padding-bottom: 3px;
  }
  .pageContent .breadcrumb .crust .arrow {
    display: none;
  }
  #siropuChat,
  .siropuChatMessages,
  .siropuChatUsers,
  #siropuChatPrivateUsers,
  .breadcrumb {
    background: #1d1e22;
    border-right: none;
  }
  .pageContent .breadcrumb .crust .arrow {
    display: none;
  }
  .siropuChatTagged {
    background-color: #333641;
    padding: 5px;
    border-radius: 0px;
  }
  .pageContent .breadcrumb .crust a.crumb {
    background: #333641;
    color: white;
    border: none;
  }
  .pageContent .breadcrumb {
    height: 40px;
    border: none;
  }
  .mainContent {
    margin-right: 0px;
  }
  #content .pageContent {
    background: #1d1e22;
  }
  .sectionMain #siropuChat {
    border: 0px;
  }
  #siropuChatEditor {
    border-radius: px;
  }
  .sectionMain {
    border-radius: 0px;
    padding: 0px;
    border: none;
  }
  .siropuChatUsers a {
    color: orange;
  }
  .siropuChatMessage,
  .siropuChatMessage a {
    color: #bed3dc;
  }
  .siropuChatContentLeft {
    color: #9a8297;
    font-size: 16px;
    font-family: 'Trebuchet MS', Helvetica, Arial, sans-serif;
    overflow: hidden;
    margin-right: 75px;
  }
  .siropuChatUsers > li {
    margin-bottom: 10px;
  }
  .siropuChatBot .siropuChatMessage {
    color: #d4c9d3;
    font-size: 90%;
  }
  .avatar img,
  .avatar .img,
  .avatarCropper {
    border: none;
    background: none;
    padding: 0px;
  }
  #siropuChat .username {
    color: orange;
  }
  .siropuChatMessages .avatar img {
    width: 30px !important;
    height: 30px !important;
  }
  .siropuChatMaximized #siropuChatContent {
    height: 640px;
  }
  .bbCodeQuote .quoteContainer,
  .bbCodeBlock .type {
    background: none;
    border-bottom: none;
  }
  .bbCodeBlock {
    border: 1px dashed white;
  }
  .siropuChatUsers .avatar img {
    width: 10px;
    height: 10px;
  }
  .siropuChatUsers,
  #siropuChatPrivateUsers {
    width: 130px;
  }
  #siropuChatEditor {
    background: none;
    border-top: none !important;
  }
  .internalLink:link {
    color: #a7925a;
  }
  #siropuChatEditor .button {
    box-shadow: none;
    border-radius: 0px;
  }
  #siropuChatEditor .textCtrl {
    background: none;
    border: 0px;
    border-bottom: 1px solid white;
    border-radius: 0px;
    color: white;
  }
  #Block1st,
  #Block2nd {
    background-color: #1d1e22;
    border: none;
  }
  #Block1st,
  #Block2nd a {
    color: #ddca7e !important;
    border: none;
  }
  .VietXfAdvStats_SectionItem {
    background: none;
  }
  #Block2nd {
    margin-left: 0px;
  }
  #Block2nd .tabs,
  #Block1st .tabs {
    border: none;
    margin-bottom: 10px;
  }
  .tabs li a,
  .tabs.noLinks li {
    background: none;
    border: none;
    color: rgb(183, 183, 183);
  }
  .tabs {
    border-bottom: none;
  }
  .PageNav a.text,
  .PageNav a {
    color: white;
  }
  .VietXfAdvStats #Block1stPanes,
  .VietXfAdvStats #Block2ndPanes {
    border: none;
  }
  .VietXfAdvStats h3 {
    background-color: #1d1e22;
  }
  .XenBase .navTabs .navTab.selected .tabLinks {
    background-color: #1d1e22;
  }
  .PageNav a {
    background: none;
    border: none;
  }
  #siropuChatSmilies .tabs.Tabs li a {
    color: black;
  }
  #siropuChatSmilies .tabs.Tabs li.active a {
    color: rgb(152, 12, 12);
  }
  #siropuChatContent {
    height: 700px;
  }
  .sectionMain #siropuChatHeader {
    background: #1d1e22;
    border: none;
    color: rgb(183, 183, 183);
  }
  #siropuChatHeader > h3,
  #siropuChatOptions > a,
  #siropuChatOptions > a:hover,
  #siropuChatToggleRooms > em:hover,
  .Popup .PopupControl.PopupOpen:not(.navTab),
  .Popup.PopupContainerControl.PopupOpen:not(.navTab),
  .Popup .PopupControl:hover,
  .Popup.PopupContainerControl:hover {
    color: rgb(183, 183, 183);
  }
  #siropuChatUsers a,
  .dataTable tr.dataRow td {
    color: rgb(183, 183, 183);
  }
}
