style.css:
@-moz-document url-prefix("https://www.baidu.com/s"), url-prefix("https://www.baidu.com/baidu") {

/* 去除无用的logo */
#lg, #result_logo, #foot, .s_logo, .bdsug-feedback-wrap, #page .fk {
	display:none !important;
}


/***** 头部（搜索框部分）*****/
#head {
	height: 75px !important;
    background-color: #f1f1f1 !important;
    position: fixed !important;
    top: 0px !important;
    z-index: 301 !important;
}
/* 关闭滚动时头部阴影 */
#head.s_down {
    box-shadow: none !important;
}


#header_top_bar {
	margin: 40px 0 20px !important;
}

.s_tab_inner, .tab-wrapper {
    position: fixed !important;
    top: 0px !important;
    left: 900px !important;
    z-index: 301 !important;
    padding-top: 20px !important;
    font-size: 20px !important;
}

#s_tab a, #s_tab b {
	font-size: 16px !important;
}

#s_tab {
	height: auto !important;
}

/***** 搜索结果部分 *****/
body, #rs {
	background-color: #f7f7f7 !important;
}

#container {
    padding-top: 35px !important;
    width: 1400px !important;
}

/* 搜索结果卡片 */
.c-container {
	background: #fff !important;
    padding: 25px !important;
    width: 750px !important;
    margin-bottom: 25px !important;
    font-size: 20px !important;
    line-height: 2 !important;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
}

a {
	text-decoration: none !important;
    color: #4879BD !important;
}
a em {
	text-decoration: none !important;
}

#rs, #rs table {
	width: 750px !important;
}
#rs th {
	width: 250px !important;
    font-size: 18px !important;
    line-height: 2 !important;
}

.c-container .c-abstract {
	font-size: 18px !important;
    line-height: 1.6 !important;
    margin-top: 10px !important;
    margin-bottom: 10px !important;
}
.c-span18 {
	width: 600px !important;
}

/* 大的头部输入部分 */
#form, .fm {
	padding-left: 100px !important;
    padding-top: 10px !important;
}


/* 输入框包裹层 */
.s_ipt_wr, .soutu-url-wrap {
    width: 650px !important;
	height: 40px !important;
    background-color: #FFF !important;
}


/* 内部输入框 */
.s_ipt, #soutu-url-kw {
    width: 600px !important;
	height: 30px !important;
    font-size: 20px !important;
}

/* 搜索按钮 */
#su, .soutu-url-btn {
	height: 42px !important;
}





/* 提示建议 */
#head .bdsug, #head .soutu-layer .soutu-url-wrap {
	top: 51px !important;
    width: 650px !important;
    
}


#head .bdsug li {
	width: 630px !important;
    font-size: 20px !important;
    height: 34px !important;
    line-height: 34px !important;
}



/* 底部页码 */
#page a, #page strong {
	height: auto !important;
    line-height: 48px  !important;
}
#page strong .pc {
	background-color: #4285F4 !important;
}
#page strong .pc, #page .pc {
    width: 60px  !important;
    height: 48px  !important;
    line-height: 48px  !important;
}


.soutu-layer {
	width: 750px !important;

}

















}
