style.css:
@-moz-document domain("last.fm") {
/*************
GENERAL
*************/
/*** FONT ***/
body,
div,
p,
table,
tr,
td {
    font-family: Lucida Grande, Helvetica Neue, Helvetica, Arial, Sans-serif;
    font-size: 12px }

input,
textarea,
.content-form .form-control,
.content-form input[type=date],
.content-form input[type=email],
.content-form input[type=number],
.content-form input[type=password],
.content-form input[type=text],
.content-form input[type=url],
.content-form select,
.content-form textarea {
    font-size: 9pt }

.no-data-message {
    font-size: 8pt }

.about-me-sidebar p {
    font-size: 11px }

h3 {
    font-size: 9pt }

.date-heading {
    font-size: 10pt;
    font-weight: 700 }

.header-follower-btn .follower-btn-add-follower {
    font-size: 0pt; }

.header--overview.header--user .header-title-secondary {
    font-size: 8pt; }

.anhv1 .content-top-header {
    font-size: 20pt }

.home-welcome-header {
    font-size: 12pt;
    font-weight: 700 }

.recs-feed .context {
    font-size: 8pt }

.grid-items-item-main-text a,
.recs-feed .recs-feed-title {
    font-size: 10pt!important }

/*** HIDE ADS ***/
#shoutbox,
.external-links-section,
.chartlist-row:first-child .chartlist-count-bar .stat-name,
.full-bleed-ad-container,
.sidebar-ad-container,
.lazy-features-page-bottom,
.trc_related_container,
.trending-tracks-section,
.col-xs-2,
.copyright.col-xs-12,
div.feedback-message,
.deferred.loading-indicator,
.LastAd,
.LastAd *,
#LastAd_leaderboard,
.lastfm-ad,
.lastfm-ad-mobile,
.lazy-features-footer,
.nag-bar--catalogue-beta-in,
.pagination a::before,
.player-bar .player-bar-progress-slug::after,
.related-ads,
.subscribe-cta,
.join-cta,
#taboola-right-rail-thumbnails,
.mpu-subscription-upsell,
.mpu-subscription-upsell--mpu,
.highcharts-text-outline,
.header-popularity-rank-tooltip::before,
.chartlist tr[data-ad-container].open-ad-container-desktop,
.chartlist tr[data-ad-container].open-ad-container-mobile,
.recs-feed-item--ad {
    display: none!important; }

/*** FOOTER ***/
h2.footer-heading {
    font-size: 12px!IMPORTANT;
    line-height: 18px;
    letter-spacing: 0px!important;
    font-weight: 700!important;
    text-transform: none!important; }

.footer .footer-links {
    font-size: 11px!important;
    line-height: 1.5em!important; }

.footer .footer-links a,
.footer .mimic-link {
    color: #aaa; }

/* FIX RECENT & LOVED TRACKS */
.chartlist-row,
.chartlist--with-bar {
    padding-top: 1px !important;
    padding-bottom: 0px !important;
    padding-left: 5px !important;
    padding-right: 5px !important; }

section#user-loved-tracks-section .chartlist-play,
section#recent-tracks-section .chartlist-play,
section.tracklist-section .chartlist-play {
    display: none }

section#user-loved-tracks-section .chartlist-loved,
section#recent-tracks-section .chartlist-loved,
section.tracklist-section .chartlist-loved {
    order: 6; }

section#user-loved-tracks-section .chartlist-image,
section#recent-tracks-section .chartlist-image,
section.tracklist-section .chartlist-image {
    order: 1; }

section#user-loved-tracks-section .chartlist-artist,
section#recent-tracks-section .chartlist-artist,
section.tracklist-section .chartlist-artist {
flex-shrink: 0;
    order: 2; }

section#user-loved-tracks-section .chartlist-name,
section#recent-tracks-section .chartlist-name,
section.tracklist-section .chartlist-name {
    order: 3;}

section#user-loved-tracks-section .chartlist-more,
section#recent-tracks-section .chartlist-more,
section.tracklist-section .chartlist-more {
    order: 5; }

section#user-loved-tracks-section .chartlist-timestamp,
section#recent-tracks-section .chartlist-timestamp,
section.tracklist-section .chartlist-timestamp {
    order: 8; }

/********************
LISTENING REPORTS
*********************/
.user-dashboard-heading:first-child {
    font-size: 18pt }

.user-dashboard-date {
    font-weight: 700;
    font-size: 13pt }

.user-dashboard-discovery-label,
.metadata-title,
.user-dashboard-catalogue-item-percentile-label,
.discovery-stat-multiple-heading,
.user-dashboard-on-repeat-item-date,
.user-dashboard-catalogue-item-title {
    font-size: 7pt }

.user-dashboard-catalogue-item-percentile-tooltip {
    font-size: 7.5pt }

.user-dashboard-module-description {
    line-height: 1.5em;
    font-size: 9pt }

.user-dashboard-catalogue-item-name {
    font-size: 11pt }

.user-dashboard-catalogue-item-total-inner {
    font-size: 20pt }

.user-dashboard-big-datapoint-value {
    font-size: 25pt }

.user-dashboard-discovery-stats .user-dashboard-discovery-data-point {
    font-size: 16pt }

.listening-report-promo-title {
    font-size: 8pt;
    line-height: 8pt;
    margin-top: 5px }

.listening-report-promo {
    padding: 15px 15px; }

.listening-report-promo-date {
    font-size: 10.5pt }


/*****
PROFILE PAGE HEADER
*****/
.btn-following-text--alt,
.follower-btn-following:focus .btn-following-text--default,
.follower-btn-following:hover .btn-following-text--default,
.follower-btn-unfollow,
.header .header-background--has-image {
    opacity: 0.9;
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
    filter: grayscale(100%); }

.header .header-background:before {
    background-color: rgba(0, 0, 0, 0.75); }

h2 {
    font-weight: 700;
    font-size: 11pt }

.anhv1 .more-link,
.section-controls .dropdown-menu-clickable-button,
.section-controls .dropdown-menu-clickable-button-js-only,
.section-with-settings .section-control,
.dropdown-menu-clickable-button.section-control,
.section-control,
.anhv1 .dropdown-menu-clickable-item {
    font-size: 8pt }

.anhv1 .navlist-more-wrap,
.anhv1 .secondary-nav-item,
.navlist-more,
.anhv1 .navlist-hidden-list a {
    text-transform: uppercase;
    font-size: 7pt }

.header .secondary-nav-item-link {
    padding-bottom: 7.5px; }

.header-title-label-wrap .header-title,
.header-title a {
    font-size: 9pt;
    font-weight: 700; }

.header .secondary-nav-item-link {
    padding-bottom: 7px; }

.header-title-column-ellipsis-wrap {
    position: absolute;
    width: 100%;
    top: -3px; }

.header .tasteometer-body p {
    font-size: 8pt;
    line-height: 15pt; }

.header .tasteometer-body p:first-child {
    margin-top: 0px }

.header .header-metadata-title,
h2.metadata-title {
    font-size: 7pt;
    line-height: 18px;
    text-transform: uppercase;
    letter-spacing: .025em;
    margin-bottom: 0;
    font-weight: 400;
    color: #999; }

.header .header-metadata-display,
p.metadata-display {
    font-size: 11.5pt;
    line-height: 18px;
    letter-spacing: -.025em; }

/* FEATURED ITEM */
.featured-item-heading,
.recommended-next-page-title {
    padding-top: 5px;
    font-size: 9px!important; }

.featured-item-name,
.recommended-next-page-name {
    font-size: 12px!important;
    font-weight: 700!important;
    line-height: 18px!important; }

.featured-item-artist,
.recommended-next-page-artist {
    margin-top: -2px;
    font-size: 11px!important; }

.user-list-about-me {
    display: none }

/********************
STATION LINKS
*********************/
.stationlink-list .stationlink:after {
    border-bottom: 0px solid #ebebeb; }

.stationlink-list .stationlink:before {
    zoom: 55%;
    -ms-zoom: 0.55;
    -webkit-zoom: 0.55;
    -moz-transform: scale(0.55, 0.55);
    -moz-transform-origin: left center; }

.stationlink-list .stationlink {
    display: block;
    color: inherit;
    width: 100%;
    white-space: nowrap;
    padding-left: 0;
    position: relative;
    text-align: left;
    padding: 3px 0 5px 30px;
    min-height: 12px;
    font-size: 11px; }

ul.stationlink-list li {
    line-height: 1em; }

/********************
LIBRARY
*********************/
.library-header .library-header-title {
    font-weight: 700;
    font-size: 12pt;
    line-height: 20px;
    margin-top: 4px }

.library-artist-section-header {
    font-weight: 700;
    font-size: 11pt }

.library-track-metadata .love-button:before {
    zoom: 0.55 }



/*************
NEW ARTIST PAGES 
*************/
.header-new .navlist-item {
    text-transform: uppercase;
    font-size: 7pt }

.top-listeners-item .top-listeners-track,
.user-list-shared-artists {
    font-size: 8.5pt }

.user-list-shared-artists {
    margin-top: 0px }

h4.user-list-name {
    font-size: 8.75pt;
    top: 8px }

.user-list-avatar {
    margin-top: 0px }

.user-list-shared-artists,
.user-list-description {
    line-height: 1.75em }

.header-new > .header-new-inner {
    background-color: #2c2c2c!important; }

.header-new-background {
    opacity: 0.5;
    -webkit-filter: saturate(0%);
    filter: saturate(0%); }

.header-new-background-overlay {
    background: linear-gradient( 0.25turn,
    rgba(44, 44, 44, 1),
    rgba(152, 108, 92, 0))!important; }

.wiki-block-inner,
.wiki-block-inner-2 {
    text-align: justify }

.header-new--artist,
.header-new-background,
.header-new-content,
.header-new-gallery-outer {
    max-height: 257px!important;
    overflow: hidden }

.header-new--artist.header-new--overview .header-new-content,
.header-new--artist.header-new--subpage .header-new-content {
    padding-top: 45px; }

a.header-new-on-tour {
    text-transform: uppercase;
    font-size: 7.5pt; }

.header-new-title {
    font-size: 25pt;
    line-height: 30pt;
    letter-spacing: -0.025em;
    font-weight: 700; }

.subpage-title {
    font-size: 15pt;
    letter-spacing: -0.025em;
    font-weight: 700; }


.header-new-on-tour,
.header-new-actions,
.header-new-chart-position-desktop {
    display: none }

.header-metadata-tnew {
    margin-left: 0px; }

.header-new-info-desktop {
    margin-top: 15px; }

.header-metadata-tnew-title {
    text-transform: uppercase;
    font-size: 7.5pt;
    letter-spacing: 0.005em }

.artist-header-featured-items-item-header {
    text-transform: uppercase;
    font-size: 7pt; }

p.header-metadata-tnew-display {
    font-size: 13pt;
    line-height: 18px; }

.artist-header-featured-items {
    margin-top: 25px; }

.artist-header-featured-items-item-name {
    font-size: 11pt;
    line-height: 24px;
    letter-spacing: -.05px;
    font-weight: 700; }

.artist-header-featured-items-item-aux-text {
    font-size: 8pt }

.artist-header-featured-items-item .media-item,
.artist-header-featured-items-item .link-block-cover-link {
    width: 80px;
    height: 80px; }

.artist-header-featured-items-item {
    min-height: 80px }

.artist-header-featured-items-item {
    padding-left: 95px }

.artist-header-featured-items-item-wrap--video-thumbnail .artist-header-featured-items-item .media-item {
    top: -5px;
    width: 120px;
    height: 80px; }

.artist-header-featured-items-item-wrap--video-thumbnail .artist-header-featured-items-item {
    padding-left: 135px;
    min-height: 80px; }

.artist-header-featured-items-item-video {
    min-height: 100px; }

.video-preview-playlink .video-preview {
    position: absolute;
    top: 0%; }

.shoutbox-heading,
.text-18 {
    font-size: 11pt }

.section-with-separator:after {
    margin-left: -15px;
    margin-right: -15px;
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    content: "";
    display: block;
    background-color: #fff;
    height: 0px; }

.catalogue-overview-similar-artists-full-width-item {
    text-align: center }

.catalogue-overview-similar-artists-full-width-item-aux-text,
.catalogue-overview-similar-artists-full-width-item-regular-tags,
.artist-top-albums-item-aux-text,
.artist-top-albums-item-regular-tags,
.dropdown-menu-clickable-button.section-control,
.section-control {
    font-size: 8pt }

.anhv1 .navlist-more-wrap,
li.masthead-nav-item .masthead-nav-control {
    font-size: 7pt;
    text-transform: uppercase; }

.label {
    font-size: 8px;
    margin-top: 10px }

.header--overview + * .content-top-header,
.header--sub-page + * .content-top-header {
    font-size: 13pt;
    font-weight: 700 }

.user-sidebar-header {
    font-size: 12pt;
    font-weight: 700 }

.artist-similar-artists-sidebar-item-name {
    font-size: 9pt }


.js .auth-dropdown-menu,
.auth-dropdown-item-secondary {
    font-size: 11px }

.anhv1 .auth-dropdown-menu-item {
    padding-top: 4px;
    padding-bottom: 4px; }

.auth-dropdown-menu-item strong {
    font-size: 12px }


/*****
CHARTLISTS
*****/
.chartlist-break:after,
.chartlist-row:after {
    content: "";
    display: block;
    position: absolute;
    bottom: 0;
    left: 15px;
    right: 15px;
    border-bottom: 0px solid #ebebeb; }

.chartlist-play,
.buylinks-dropdown-menu,
.menu-title {
    display: none!important }

.chartlist-index {
    text-align: center!important;
    color: #d0d0d0!important;
    font-weight: 700;
    line-height: 0px;
    height: 100%;
    font-size: 16px!important;
    letter-spacing: -1px; }

.chartlist-timestamp,
.chartlist-timestamp {
    font-size: 7.5pt; }

.chartlist-image {
    top: 0px }

.chartlist-row {
    height: 45px }

/* IMAGE GRIDS */
.grid-items-item-main-text a,
.recs-feed .recs-feed-title {
    font-size: 12px!important; }

.grid-items-item-aux-text a,
.recs-feed .recs-feed-description a {
    color: #fff;
    font-size: 11px!important; }

.recs-feed .recs-feed-description,
.grid-items-item-aux-text a:last-child {
    color: #C0C0C0!important; }
    
.show-focus-controls:not([data-ajax-form-state=deleted]) .chartlist-album {
    margin-right: -24.5px}
    
/* TOP TRACKS */
.chartlist--with-play .chartlist-index {order: 1}
.chartlist--with-play .chartlist-image {order: 2; top: 0px}
.chartlist--with-play .chartlist-name {order: 3}
.chartlist--with-bar.chartlist--with-index.chartlist--with-play.chartlist--with-image.chartlist--with-loved .chartlist-row--with-artist .chartlist-artist {order: 4; left: 75.5px}
.chartlist--with-play .chartlist-loved {order: 500}
.chartlist--with-play .chartlist-more {order: 6}

.chartlist-buylinks {order: 5; display: none}
.chartlist--with-play .chartlist-duration {order: 4}
.chartlist--with-play .chartlist-bar {order: 999}

.chartlist-artist {
    -webkit-box-flex: 0;
    -ms-flex-positive: 0;
    flex-grow: 0;
    order: 2
}

.chartlist-count-bar {line-height: 40px;}

.chartlist-count-bar-slug{
border-radius: 0px 10px 10px 0px}

.chartlist--with-bar .chartlist-row--with-artist .chartlist-artist {
    position: absolute;
    margin-top: 9.5px;
}

.chartlist--with-bar.chartlist--with-index.chartlist--with-play.chartlist--with-image .chartlist-row--with-artist .chartlist-artist {
    left: 75px;
}

.chartlist--with-bar .chartlist-row--with-artist .chartlist-name {
    margin-bottom: 19px;
}

.catalogue-overview-similar-artists-item {text-align: center}

.catalogue-overview-similar-artists-item-aux-text, .catalogue-overview-similar-artists-item-regular-tags {
    margin-top: 3px;
    font-size: 11px;
    line-height: 12px;
}

section#top-artists img {    
    -webkit-filter: grayscale(100%);
    -moz-filter:    grayscale(100%);
    -ms-filter:     grayscale(100%);
    -o-filter:      grayscale(100%);
 }

section#user-loved-tracks-section .chartlist-name,
section#recent-tracks-section .chartlist-name,
section.tracklist-section .chartlist-name {
 margin-bottom: 18px;}
    
section#user-loved-tracks-section .chartlist-artist,
section#recent-tracks-section .chartlist-artist,
section.tracklist-section .chartlist-artist {
    position: absolute;
    left: 52px;
    top: 21.5px;}

.chartlist--with-bar.chartlist--with-index.chartlist--with-play.chartlist--with-loved .chartlist-row--with-artist .chartlist-artist {
    left: 27.5px;
}
}
