style.css:
@-moz-document domain("flashscore.com") {
div.container, div.container__content.content, div.container__myMenu, div.extraContent__content {background: #000!important;}

div.event, ul#my-leagues-list, li {background: #242424!important;
color: #bdbdbd!important;}


div.event__check {background: white;}

span, a {color: white!important;}

.event__match.event__match--twoLine:hover {background:#101010!important; }

.fontBold {text-decoration: underline;
color:#cdcccc;}

.fontBold:hover {text-decoration: none;}

.tabs {background:none!important;}

/*Sports*/
.tennis div.event__header, .tennis li.selected, .tennis .tabs__tab.selected {background: #a37e00!important;
color: #f0f0f0;}

.tennis .menuTop__content .menuTop__item--active {background: #bf9a17;}

.tennis .menuTop__content .menuTop__item--active:hover {background:#bf9a17;}

.tennis .tabs__tab {background: #7a5e00;
color: white;}

.tennis .tabs__tab:hover {background: #a37e00!important;}

.menuTop__content .menuTop__item--active .menuTop__text  {color: White!important;}

/*-----------------------------------------------------*/

.soccer div.event__header, .soccer li.selected, .soccer .tabs__tab.selected {background: #085f00!important;
color: #f0f0f0;}

.soccer .menuTop__content .menuTop__item--active {background: #1b7700;}

.soccer .menuTop__content .menuTop__item--active:hover {background:#1b7700;}

.soccer .tabs__tab {background: #053600;
color: white;}

.soccer .tabs__tab:hover {background: #085f00!important;}

/*-----------------------------------------------------*/

.basketball div.event__header, .basketball li.selected, .basketball .tabs__tab.selected {background: #ab4630!important;
color: #f0f0f0;}

.basketball .menuTop__content .menuTop__item--active {background: #cd634c;}

.basketball .menuTop__content .menuTop__item--active:hover {background:#cd634c;}

.basketball .tabs__tab {background: #91311d;
color: white;}

.basketball .tabs__tab:hover {background: #ab4630!important;}

/*-----------------------------------------------------*/

.hockey div.event__header, .hockey li.selected {background: #00406d!important;
color: #f0f0f0;}

.rugby-union div.event__header, .rugby-union li.selected {background: #2b2b2b!important;
color: #f0f0f0;}

.baseball div.event__header, .baseball li.selected {background: #856842!important;
color: #f0f0f0;}

.cricket div.event__header, .cricket li.selected {background: #7b081b!important;
color: #f0f0f0;}

.golf div.event__header, .golf li.selected {background: #4e7019!important;
color: #f0f0f0;}

.esports div.event__header, .esports li.selected {background: #352860!important;
color: #f0f0f0;}

.badminton div.event__header, .badminton li.selected {background: #16875a!important;
color: #f0f0f0;}

.horse-racing div.event__header, .horse-racing li.selected {background: #614235!important;
color: #f0f0f0;}

.volleyball div.event__header, .volleyball li.selected {background: #b5773a!important;
color: #f0f0f0;}

.rugby-league div.event__header, .rugby-league li.selected {background: #2b2b2b!important;
color: #f0f0f0;}

.snooker div.event__header, .snooker li.selected {background: #004000!important;
color: #f0f0f0;}

.aussie-rules div.event__header, .aussie-rules li.selected {background: #ad3f21!important;
color: #f0f0f0;}

.aussie-rules div.event__header, .aussie-rules li.selected {background: #ad3f21!important;
color: #f0f0f0;}

.american-football div.event__header, .american-football li.selected {background: #652b1d!important;
color: #f0f0f0;}

}

@-moz-document url-prefix("https://www.flashscore.com/match") {
body {background: black!important;}

div, tr.odd, tr.even {background: #242424!important;
color: #bdbdbd!important;}

.detail-experimental #detail .h-part {background: #101010!important;
color: white;}

tbody a, td span {color:#bdbdbd!important;}

strong a, strong {color: #cdcccc!important;
text-decoration: underline!important;}

strong a:hover, strong:hover {text-decoration: none!important;}

td.kx.max {background: #242424!important;}

span.txt, span.sortarrow, th span {color:#656565!important;}

.highTeam {background: #242424!important;}

.highlight:hover td {background: #101010!important;}

.prematch .odds span {color: black!important;}

/*sports*/
.tennis li:hover {background: #a37e00!important;}

.soccer li:hover {background: #085f00!important;}

.basketball li:hover {background: #ab4630!important;}

.hockey li:hover {background: #00406d!important;}

.rugby-union li:hover {background: #2b2b2b!important;}

.baseball li:hover {background: #856842!important;}

.cricket li:hover {background: #7b081b!important;}

.golf li:hover {background: #4e7019!important;}

.esports li:hover {background: #352860!important;}

.badminton li:hover {background: #16875a!important;}

.horse-racing li:hover {background: #614235!important;}

.volleyball li:hover {background: #b5773a!important;}

.rugby-league li:hover {background: #2b2b2b!important;}

.snooker li:hover {background: #004000!important;}

.aussie-rules li:hover {background: #ad3f21!important;}

.american-football li:hover {background: #652b1d!important;}

}

@-moz-document url-prefix("https://www.flashscore.com/draw") {
div, tr {background: #242424!important;
color: #bdbdbd!important;}

.flat ul.stages-menu li.bubble{background: #444!important;}

div .match:hover {background: #101010!important;}

span {color:#bdbdbd!important;}

.winner .name {color: #cdcccc!important;
text-decoration: underline!important;}

.winner .name:hover{text-decoration: none!important;}

}
