style.css:
@-moz-document domain("sisu.jyu.fi") {
:root {
    --border-color: #b5b5b5;
}

.name-parent {
    display: flex;
    flex-flow: row wrap;
}

attainment-tree-parent:nth-child(2n) {
    background: #f7f7f7;
}

.name-parent > .name {
    order: 2;
}

.name-parent > span.code-parent {
    order: 1;
    width: 80px;
    font-weight: 600!important;
}

search-result-row > .row:nth-child(2n) {
    display: none;
}

.results > div {
    padding-top: 10px;
    padding-bottom: 10px;
    border-bottom: solid 1px var(--border-color)
}

search-result-row {
    padding: 0px;
    border-top: none;
}

.results-name {
    display: flex;
}

.course-unit-heading {
    margin-bottom: 0px!important;
}

.results-name > .credits-box {
    order: 2;
    background: #ececec!important;
    color: #000!important;
    border-radius: 4px!important;
    width: auto!important;
    padding-left: 10px;
    padding-right: 10px;
}

.results-name > .title-box {
    order: 1;
    display: flex!important;
    top: 0px!important;
    margin-right: 20px;
}

.results-name > .title-box > .courseunit-code > a {
    order: 1;
    width: 128px;
    height: 32px;
    background: #3091f0;
    color: #fff!important;
    line-height: 2.4rem;
    display: inline-block;
    text-align: center;
    padding-top: 4px;
    border-radius: 4px;
    margin-left: 0px!important;
}

.results-name > .title-box > .courseunit-title {
    margin-left: 0px!important;
    line-height: 3.4rem;
}

.results-name > .title-box > .courseunit-code > a {
    color: #fff!important;
}


.results-name > .title-box > .courseunit-title {
    order: 2;
}

.study-event {
    border-bottom: solid 1px var(--border-color);
    margin-bottom: 0px!important;
    padding-top: 5px;
    padding-bottom: 5px;
}

study-event-summary {
    display: flex;
}

study-event-summary > .row:first-child {
    width: 100px!important;
}

.event-detail-row {
    width: 100%;
}

.event-detail-row:nth-child(1n) {
    text-transform: capitalize;
}

course-unit-info-text-field > div > p {
    width: 100%
}

course-unit-info-section > div > .content > .content-row > .content-text > div > .row > div {
    width: 100%;
}

course-unit-info-text-field > div {
    margin-bottom: 20px!important
}

.form-group-mimic {
    margin-bottom: 20px!important;
}

.course-unit-enrolments {
    margin-bottom: 0px!important;
    border-bottom: 1px solid var(--border-color);
    border-radius: 0px!important;
    overflow: hidden!important;
}

.course-unit-enrolments > course-unit-realisation {
    margin-bottom: 0px!important;
}

.credits-box > .credit-amount {
    width: 30px!important;
}

.cu-notification-header {
    width: 30px!important;
}

.cu-notification-content {
    width: calc(100% - 30px)!important;
}

.courseunit-realisation-bottom-row {
    padding: 0 0 0 30px!important;
}

.courseunit-realisation-bottom-row::before {
    width: 30px!important;
}

div.courseunit .courseunit-top-row .credits-box > .credit-amount {
    width: 38px!important;
}

course-unit-box > .courseunit {
    margin-top: 5px!important;
    margin-right: 0px!important;
}

.module-border {
    margin-top: 5px!important;
}

@media (min-width: 992px) {
    .col-md-3 {
        width: 30%;
    }

    .col-md-9 {
        width: 70%;
    }
}

.rule-body > .sub-rule {
    margin-top: 0px!important;
}

.rule-body {
    background: #fff;
    border-radius: 5px;
    padding: 10px;
}


.rule-body > .sub-rule > rule-select > course-unit-rule {
    margin-top: 0!important;
}

.rule-body > .sub-rule > rule-select > course-unit-rule > course-unit-box > .courseunit {
    margin-top: 0!important;
}

.rule-body > .courseunit {
    margin-top: 0!important;
}

.tree-list-body > .row > .col-sm-6 {
    padding-left: 0px;
    padding-right: 0px;
}
.tree-list-item {
    padding-left: 30px!important;
}

.tree-list-heading {
    background: #fff;
}

.module-credits-header {
    z-index: 1000!important;
}

student-enrolments > table > tbody > tr > td {
    padding: 6px 8px!important;
}

student-enrolments > table > tbody > tr:nth-child(2n) {
    background: #f7f7f7;
}

plan .plan-state-notification-container ~ plan-module > div {
    padding-top: 40px;
}

.tree-list-body > .row {
    display: grid!important;
    grid-template-columns: repeat(1, 1fr);
}

.tree-list-body > .row > .col-sm-6 {
    width: 100%!important;
}

course-unit-box {
    width: 100%!important;
    overflow: hidden;
    margin-right: 0;
}

div.courseunit.cu-color-4 {
    margin-top: 0px!important;
    min-height: 2em;
    height: 4em!important;
    display: block;
}

div.courseunit {
    margin-top: 0px!important;
    min-height: 2em;
    height: 2em!important;
    display: block;
}

div.courseunit .courseunit-top-row {
    height: 2em!important;
}

.tree-list-body > .row > .col-sm-6:nth-child(2n) {
    background: #f7f7f7!important;
}

.tree-list-body > .row > .col-sm-6 > course-unit-box > div.courseunit {
    height:2em!important;
}

timeline timeline-course-unit.unit-minimal-bigger div.courseunit .courseunit-top-row .title-box, timeline timeline-custom-course-unit-attainment.unit-minimal-bigger div.courseunit .courseunit-top-row .title-box {
    width:auto!important;
    background:#fff;
    overflow:hidden;
}

div.courseunit .courseunit-top-row .title-box {
    display: flex;
    height: 2em!important;
    background: none;
}

div.courseunit .courseunit-top-row .title-box > span.courseunit-code {
    width: 100px!important;
    font-size: 1.4rem;
    font-weight: 400;
    line-height: 2em;
    padding-top: 0;
    margin-left: 0px;
    order: 1;
    padding-left: 5px;
    background: #fff;
}

div.courseunit .courseunit-top-row .title-box > span.courseunit-code > a {
    width: 100px!important;
}

div.courseunit .courseunit-top-row .title-box > span.courseunit-code {
    display: block;
}

div.courseunit .courseunit-top-row .title-box > span.courseunit-title {
    font-size: 1.4rem;
    font-weight: 400;
    line-height: 2em;
    padding-top: 0;
    margin-left: 0px;
    order: 2;
    width: auto;
    padding-right: 5px;
    overflow: hidden;
    text-overflow: ellipsis;
}

.tree-list-body > .row > .col-sm-6 > course-unit-box > .courseunit > .courseunit-top-row > ng-transclude {
    display: none;
}

div.courseunit .courseunit-top-row .title-box .substitution-indicator-box {
    display: block;
    width: 2em!important;
    height: 2em!important;
    position: relative;
    order: 3;
}

div.courseunit .courseunit-top-row .title-box .substitution-indicator-box .SISUicon-Recycle {
    margin-top: 0;
    margin-left: 0
}

div.courseunit .courseunit-top-row .credits-box {
    order: 3;
    float: right;
    height: 2em!important;
}

div.courseunit .courseunit-top-row .grade-box {
    float: right;
    height: 2em!important;
    line-height: 2em!important;
}

div.courseunit .courseunit-top-row .credits-box > .credit-amount {
    height: 2em!important;
    line-height: 2em!important;
    margin: 0!important;
}

div.courseunit .courseunit-top-row .grade-box .grade-text {
    line-height: 2em;
}

div.courseunit .courseunit-top-row .title-box > span {
    margin-left: 0;
}

div.courseunit .courseunit-top-row .title-box::after {
    display: none;
}

div.courseunit .courseunit-top-row {
    background: none;
}

i.SISUicon-Graduate {
    display: none!important;
}

div.courseunit .courseunit-top-row .credits-box > .credit-amount {
    width: 5px;
}

div.courseunit .courseunit-top-row .credits-box .credit-abbreviation {
    font-size: 1.4rem;
    line-height: 1.8rem;
    font-weight: 300;
    opacity: 1;
}

.rule-body > .sub-rule > rule-select > course-unit-rule > course-unit-box > .courseunit {
    margin: 5px;
}

div.courseunit .courseunit-top-row .additional-box.toggle-box .checkbox-toggle {
    margin: 0;
    margin-top: 5px;
    height: 2em;
    margin-left: -15px;
}

course-unit-rule course-unit-box div.courseunit .courseunit-top-row .grade-box {
    display: none;
}

course-unit-rule course-unit-box div.courseunit .courseunit-top-row .credits-box {
    display: none;
}

div.courseunit .courseunit-top-row .additional-box {
    height: 2em!important;
    min-width: 20px!important;
    width: 20px!important;
    background: none;
}

div.courseunit .courseunit-top-row .additional-box:before {
    display: none;
}

read-more .read-more .visible-content {
    margin-bottom: 10px;
}

div.courseunit .cu-notification-bar {
    margin-top: 0px;
    height: 2em;
}

timeline timeline-course-unit,
timeline timeline-custom-course-unit-attainment {
    height: 2em;
    background: #fff;
}

timeline .timeline-period-row {
    height: 2em;
    overflow:hidden;
}

timeline timeline-course-unit.unit-minimal-bigger div.courseunit .courseunit-top-row .title-box,
timeline timeline-custom-course-unit-attainment.unit-minimal-bigger div.courseunit .courseunit-top-row .title-box {
    overflow: hidden;
}

div.courseunit .courseunit-top-row .grade-box + .title-box {
    width: auto;
}

div.courseunit .cu-notification-bar .cu-notification .cu-notification-header i.SISUicon-Warning:after {
    left: 5.5px;
}

div.courseunit .cu-notification-bar .cu-notification .cu-notification-header {
    border-radius:0
}

plan-module .tree-list .tree-list-item-level-1 .module-border .tree-list-heading.tree-list-heading-has-children {
    border-radius:4px;
}

.accordion-panel>.header>.title {
    font-size: 1.4rem;
    font-weight: 600;
}

.accordion-panel>.header>.collapse {
    padding-bottom:0;
    padding-top:0;
    margin-top: -10px;
}

i.SISUicon-ChevronDown {
    margin-top: 10px;
    display:block;
}

.accordion-panel>.header>.title>.additional {
    font-size: 1.4rem;
    font-weight: 400;
}

.accordion-panel>.header.has-header-items {
    display:flex;
    height:auto;
}

.accordion-panel>.header.has-header-items>.title {
    width:100%;
}

.accordion-panel>.header>.collapse>i:before {
    transition: all .4s linear;
    border-radius: 50%;
    background: none;
    color: #474747;
    border: solid 1px #3091f0;
    padding: 0px 5px 0;
    display: inline-block;
}

timeline timeline-course-unit.unit-minimal-bigger div.courseunit .courseunit-top-row .credits-or-grade.credits-box, timeline timeline-custom-course-unit-attainment.unit-minimal-bigger div.courseunit .courseunit-top-row .credits-or-grade.credits-box {
    z-index: 100;
}

timeline timeline-course-unit.unit-minimal-bigger div.courseunit .courseunit-top-row .credits-box, timeline timeline-course-unit.unit-minimal-bigger div.courseunit .courseunit-top-row .grade-box, timeline timeline-custom-course-unit-attainment.unit-minimal-bigger div.courseunit .courseunit-top-row .credits-box, timeline timeline-custom-course-unit-attainment.unit-minimal-bigger div.courseunit .courseunit-top-row .grade-box {
    z-index: 120;
}

.js-unit-shrinkable .repeatSetIdjy-CU-30329-v1 .unit-minimal-bigger .hover::hover {
    display:none;
}

.courseunit-realisation > .courseunit-realisation-top-row > .credits-box {
    width: 30px!important; 
}

.courseunit-realisation-top-row > .additional-box > i.SISUicon-ChevronDown {
    margin-top:0;
    margin-left:-10px;
}

.calendar-right-bar-content {
    padding:0!important;
}

.calendar-view-container .calendar-right-bar .calendar-right-bar-content .calendar-enrolment-filters {
    padding:5px;
    margin:0
}

div.courseunit-realisation .cu-notification-bar .cu-notification {
    border-radius:0;
}

div.courseunit-realisation.collapsed .cu-notification-bar .cu-notification:last-child .cu-notification-header {
    border-radius:0;
}

div.courseunit-realisation {
    display:block;
}

common-course-unit div.courseunit .courseunit-top-row .title-box {
    background: #fff;
}

div.courseunit .courseunit-top-row .additional-box .btn {
    margin-left:-107px;
    top:0;
    margin-top:2px;
    margin-bottom:0px;
    height:2em!important;
}
}
