style.css:
@-moz-document url("https://www.zooniverse.org/projects/sassydumbledore/chimp-and-see/classify"), url("https://www.zooniverse.org/projects/edh/rainfall-rescue/classify"), regexp("https://www.zooniverse.org/projects/(meredithspalmer|shuebner729|aguthmann|sandiegozooglobal)/.*/classify") {
@media screen and (orientation: portrait), screen and (max-width: 900px) {
    .content-container {
        margin: 5px;
    }
    .classifier {
        flex-direction: column;
    }
    /* control buttons below subject image */
    button.secret-button {
        min-width: 35px;
        height: 35px;
    }
    .sc-cSHVUG {
        margin-left: 0px;
    }
    .task-container {
        margin: 0!important;
        min-width: 750px;
        max-width: 1000px;
        margin-bottom: 0!important;
    }
    .survey-task-chooser-characteristics button {
        min-height: 40px;
    }
    .survey-task-chooser-characteristics button span {
        font-size: 14px;
    }

    .survey-task-chooser-choices {
        padding-bottom: 2px;
    }
    .survey-task-chooser-choice-button {
        min-height: 40px;
        width: 200px;
        font-size: 14px;
    }
    .survey-task-image-flipper {
        float: left;
        max-width: 48%;
    }
    .survey-task-image-flipper img {
        max-width: 100%;
        max-height: none;
    }
    .survey-task-choice-content {
        float: right;
        max-width: 50%;
        font-size: 10px;
        padding-bottom: 5px;
    }
    .survey-task-choice-content div {
        font-size: 14px
    }
    .survey-task-choice-content div:first-child {
        font-size: 16px
    }
    .survey-task-choice-content div span {
        font-size: 14px
    }
    .survey-task-choice-content div span label {
        font-size: 14px
    }
    .survey-task-choice-confusions span button {
        margin-bottom: 3px!important;
    }
    .survey-task-choice-answers span {
        margin-bottom: 3px;
        padding-bottom: 3px;
    }
    .survey-task-choice div:last-child {
        clear: both;
    }
    /* Done & Talk and Done buttons */
    .task-nav {
        font-size: 16px;
        margin: 3px 0 3px;
    }
}
@media screen and (min-width: 900px) {
    .task-container {
        margin-bottom: 5px!important;
        min-height: 580px;
    }
    .survey-task-image-flipper {
        Xpadding:0;
        Xwidth:90%
    }
    .survey-task-image-flipper img {
        min-height:195px;
        max-height:195px;
        max-width:370px;
        width: auto;
        text-align: center;
    }

    /*  */
    .survey-task-chooser-choice-button {
        white-space: nowrap;
    }
    .survey-task-chooser-choice{
        margin: 0.23em 0.6em;
    }
    .survey-task-choice-confusionsX {
        visibility: hidden;
        height: 0;
        margin: 0;
    }
}
@media screen and (min-width: 1024px) and (orientation: portrait) {
    .task-container {
        min-height: auto;
    }
    .survey-task-image-flipper img {
        max-height: max-content;
    }
}

