style.css:
/* i really want this to be global */

html,
body {
    background: #ffffff !important;
}

#page {
    display: grid;
    grid-template-rows: auto;
    grid-template-columns: 300px auto;
}

header {
    grid-column-start: 1;
    grid-column-end: 2;
}

#content {
    grid-column-start: 2;
    grid-column-end: -1;
}

#masthead {
    position: sticky;
    top: 0;
    height: 100vh;
    box-sizing: border-box;
    display: flex;
    /*align-items: center;*/
}

.main-header-bar-wrap{
    margin-top: 50px;
}


.main-header-container{
    display: flex;
    justify-content: center;
}


.ast-main-header-bar-alignment {
    margin-top: 50px;
}

.main-navigation {
    margin: 0;
    padding: 0 !important;
}

#primary-menu {
    flex-wrap: wrap;
    justify-content: flex-start;
}

#primary-menu li {
    text-align: center;
    margin: 0;
    padding: 0;
    flex-basis: 100%;
}

#primary-menu li a {
    margin: 0;
    padding: 0;
}

/*img.custom-logo{
    position: fixed;
    top: 50px;
    left: 100px;
}*/
.main-header-bar {
    /*background: linear-gradient(to right, transparent 25%, black 25%);*/
    /*background: #ffffff;*/
    background-color: rgba(255, 255, 255, 0.9);
    border: none;
}

.cookie-notice-container,
.intro-text {
    display: none;
}

.ast-container {
    width: 100%;
    max-width: unset;
}

.ast-row::before {
    display: none;
}

.ast-row {
    display: grid;
    /*flex-wrap: wrap;*/
    grid-template-rows: auto;
    grid-template-columns: 1fr;
    /*grid-gap: 50px;*/
    /*justify-content: center;*/
    /*align-content: center;*/
}

.ast-row > article:first-child {}

article {
    grid-column-start: 1;
    grid-column-end: 2;
    width: 50% !important;
    /*justify-self: center;*/
    margin-left: 100px !important;
    /* box-shadow: 0 0 20px #000;*/
}
