style.css:
@-moz-document url-prefix("http://www.kgirls.net/") {
body {
    background-color: #1F1F1F;
}

#content {
    border: solid 1px black;
}

#logo a {
    filter: invert(88%);
}

#gnb_wrp {
    border-bottom: 1px solid black;
}

#header_wrp {
    border-bottom: 1px solid black;
}

#tmb_lst p b {
    color: white;
}

#bd .replyNum {
    filter: invert(88%);
}

#gnb .li1 .on1 {
    filter: invert(88%);
}

#gnb .a1 {
    filter: invert(88);
}

#top_mn {
    filter: invert(88%);
}

#gnb_wrp div {
    filter: invert(88%);
}

#tmb_lst .deco2 {
    filter: invert(88%);
}

#tmb_lst .deco1 {
    filter: invert(88%);
}

.rd_hd .board .top_area {
    background: #1f1f1f;
    border-top: 1px solid black;
    border-bottom: 1px solid black;
}

.rd_hd .board .btm_area {
    border-bottom: 1px solid black;
}

.bd a {
    color: white;
}

.cmt_editor {
    background: #1f1f1f;
}

.bd .itx,
.exForm input,
.bd select {
    background: #1f1f1f;
}

.cmt_wrt_btm .fdb_tag {
    background: #1f1f1f;
}

.xe_content {
    color: white;
}

*::selection {
    background-color: #1F6CE0 !important;
}

#footer {
    border-top: 4px solid #1f6ce0;
}

.bd .btn_img {
    background: #1f1f1f;
}
.bd {
    color: white;
}

#tmb_lst .tmb_wrp {
    border: 1px solid #1f1f1f;
}
}
