style.css:
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain("lumpics.ru"){

.header-area, .main-content-area {
    background: #0e0e0e;
    color: #fff;}

body, pre, .single-post-footer, #secondary aside, .wp-caption {
    background: #131313;}

#secondary aside, .homecomments {
    border-radius: 5px;
    padding: 14px 16px 4px;
    box-shadow: 0 0 5px rgba(180,180,180,0.5);
    -moz-box-shadow: 0 0 5px rgba(180,180,180,0.5);
    -webkit-box-shadow: 0 0 5px rgb(0, 0, 0);}

div#toc_container {
    background: #131313;
    border: 1px solid #131313;
    border-radius: 5px;
    padding: 10px 20px !important;}

blockquote {
    margin: 40px 0;
    padding: 15px;
    background: #ff99001a;
    color: #fff;
    border-radius: 5px;}

.myButton {
    background-color: #a96800;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    border: 1px solid #000;
    display: inline-block;
    cursor: pointer;
    color: #fff !important;
    padding: 12px 50px;
    text-decoration: none;
    margin: 10px 5px;}

.comment-body {
    background: #151414;
    padding: 13px;
    border-radius: 5px;
    box-shadow: 0 0 5px rgba(180,180,180,0.5);
    -moz-box-shadow: 0 0 5px rgba(180,180,180,0.5);
    -webkit-box-shadow: 0 0 5px #151414;}

body, q, code, kbd, tt, var, .header-menu a, .comment-navigation a, .page-title, .entry-meta, .paging-navigation, .post-navigation, .comment-navigation, .social-links a, .response-title, .site-footer, .entry-content div.sharedaddy div.sd-block, .entry-summary div.sharedaddy div.sd-block {
    color: #000000;}

body, q, code, kbd, tt, var, .header-menu a, .comment-navigation a, .page-title, .entry-meta, .paging-navigation, .post-navigation, .comment-navigation, .social-links a, .response-title, .site-footer, .entry-content div.sharedaddy div.sd-block, .entry-summary div.sharedaddy div.sd-block {
    color: #696969;}
}
