style.css:
@-moz-document domain("nuscast.ap.panopto.com") {
/* General Viewer Colour */
.viewer {
    background-color: #222324 !important;
    color: #aaaaaa !important;
}

/* Top Header Bar Colours */
#viewerHeader {
    background-color: #292929 !important;
}

/* NUS Logo */
#siteLogo_customerLogoLink > img {
	filter: drop-shadow(0px 0px 8px rgba(255, 255, 255, 1));
}
/* Panopto Logo Words */
#panoptoLogoLink {
    display: none !important;
}

/* Parent Folder Breadcrumb Icon */
.parent-icon {
    color: #ffc300 !important;
}

/* Child Folder Breadcrumb Icon */
.chevron {
    color: #ffc300 !important;
}

/* Sign out button */
#PageContentPlaceholder_LoginStatus {
    color: #ffc300 !important;
}

/* Rate Lecture Button */
#starRating {
    display: none !important;
}

/* "Search this recording button */
#searchInput {
    background-color: #292929 !important;
}

/* Interaction Buttons and Fields on the Left */
.event-input, select {
    background-color: #292929 !important;
    border-color: #ffc300 !important;
    color: #aaaaaa !important;
}

#thumbnailList {
    background-color: #292929 !important;
    border-color: #ffc300 !important;
    color: #aaaaaa !important;
}

#transportControls {
    background-color: #4f4f4f !important;
    border-color: #4f4f4f !important;
    color: #4f4f4f !important;
}


}
