style.css:
@-moz-document domain("gbatemp.net") {
div#content {
    background-image: url("/*[[custom-bg]]*/"); /*your image URL here*/
    background-color: rgba(0, 0, 0, 0.5);
    background-blend-mode: darken;
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}

.portal_block > h3, .resourceUpdate {
    background:rgba(0,0,0,0.5) !important;
    border-bottom: 4px solid rgba(255, 255, 255, 0.3);
}

#rc_switch li {
    background-color: rgba(0,0,0,0.5)
}

.portal_block, .messageList .message, .bbCodeQuote .attribution, .bbCodeBlock .type, .likesSummary, #portal_left_navigation, .resourceInfo h2, .pageNavLinkGroup, .quickReply, .forum_who_is_viewing_list, .discussionListItems, .messageUserBlock, .profilePage .primaryUserBlock, .profilePage .userStats dl, .profilePage #ProfilePanes, .blogEntry, .messageList .message.alt, .PollContainer .secondaryContent, div.section.sectionMain.searchResults, ol.memberList, .sidebar .secondaryContent, .blueHeader, .subViewBlock, .bbCodeQuote .quoteContainer, .bbCodeSpoiler .spoilerContainer, .bbCodeBlock pre, .bbCodeBlock pre.prettyprint, .bbCodeBlock .code, .dynamicMain, #review_content, .breadcrumb, .PageNav a:hover, .PageNav a.currentPage, .PageNav a, .breadcrumb .jumpMenuTrigger:hover, .breadcrumb .crust:hover, #rc_switch li.active a, #portal_left_navigation li ul, .xenOverlay .section .heading, .xenOverlay .sectionMain .heading, #jumpMenu .nodeList li.d0, .entries .entry {
    background:rgba(0,0,0,0.5) !important;
}

.PageNav a, .breadcrumb .jumpMenuTrigger,#jumpMenu h3 {
    background: rgba(0,0,0,0.3)
}

.xenOverlay .primaryContent, .xenOverlay .secondaryContent, .xenForm .ctrlUnit {
    background: transparent
}

.xenOverlay .section, .xenOverlay .sectionMain {
    border: 8px solid rgba(255,255,255,0.3)
}

.xenOverlay > .section, .xenOverlay > .sectionMain, .resourceListItem, div.section.reviews, .nodeList .node.level_2, div#content.gbatemp_staff_list > div.pageWidth > div.pageContent > div.section.sectionMain, .balloon, .messageSimple .comment, .messageSimple .secondaryContent, .profilePage .primaryUserBlock .userStatus, .breadcrumb .crust a.crumb, .navigationSideBar .section, .thread_create .xenForm, .thread_reply .xenForm, .conversation_add .xenForm, .conversation_reply .xenForm, .review_list, .categoryGrid .category.simple a, form.xenForm.Preview.AutoValidator {
    background: rgba(0,0,0,0.5) !important;
}

.profilePage .primaryUserBlock .userStatus::after {
    border-color: rgba(255,255,255,0.3) transparent
}

.balloon .arrow, .balloon .arrow::after {
    border-color: transparent rgba(255,255, 255, 0.3) transparent transparent;
}

.balloon, .profilePage .primaryUserBlock .userStatus {
    border: rgba(255, 255, 255, 0.3) 1px solid
}

.navigationSideBar .heading {
    color: rgb(210, 217, 223)
}

.resourceInfo h1, #main-title-container, .blueHeaderCompact, dl.sectionHeaders, .sidebar h3, .profilePage .mast .section.infoBlock h3, .dynamicTitle, .bbCodeHeader, .main-title, .tabs li a, .tabs.noLinks li, .tabs li.active a, .tabs.noLinks li.active, #rc_switch li:hover, .blockLinksList a:hover, .navigationSideBar .heading, .navigationSideBar a:hover, .navigationSideBar .secondaryContent, .titleBar h1, .sectionFooter, .secondaryContent, .Menu .menuHeader {
    background-color: rgba(0,0,0,0.75);
}

.tabs li a:hover, .tabs.noLinks li:hover, .navPopup .PopupItemLinkActive:hover {
    background-color: rgba(0,0,0,0.85)
}

.resourceInfo h1, #main-title, #main-title-right, .blueHeaderCompact, #taigachat_full .taigachat_title, .sidebar h3, .profilePage .mast .section.infoBlock h3,.blueHeader, .dynamicTitle, .bbCodeHeader, .main-title, .xenOverlay .section .heading, .xenOverlay .sectionMain .heading, .navigationSideBar .heading, .titleBar h1 {
    border-bottom: 4px solid rgba(255, 255, 255, 0.3) !important;
}

div.section.sectionMain.searchResults {
    margin: 0px auto;
}

.balloon .likesSummary {
    background: rgba(0,0,0,0.5) !important;
}

#main-title-right, .bbCodeQuote, .bbCodeBlock, .discussionListItem, .discussionListItem .posterAvatar .avatarContainer, .promoNews #promoNews-block, .userBanner.gb2, .UserInfo .MsgsLikes, .userBanner.gb21, .messageUserBlock div.avatarHolder, .discussionListItem .posterAvatar, .discussionListItem .stats, .messageSimpleList, .primaryContent, #taigachat_full .primaryContent, .sidebar .sectionFooter, .sectionMain, .mainContentBlock .primaryContent, .bbCode > dl > dd, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, #review_approach, .node .nodeLastPost,.breadcrumb .crust:last-child a.crumb, #rc_switch, .resourceListItem .resourceImage, .resourceListItem .resourceStats, .Menu {
    background-color: transparent;
}

.categoryGrid .category.simple a {
    border: 2px solid rgb(255, 255, 255, 0.3)
}

.bbCode > dl > dd {
    border: 1px solid rgb(255, 255, 255, 0.3)
}

.bbCodeBlock, .likesSummary, .tabs li.active a, .tabs.noLinks li.active,#jumpMenu .nodeList li.d0 {
    border-left: 4px solid rgb(255, 255, 255, 0.3);
}

.messageUserBlock .arrow {
    border-color: rgba(0, 0, 0, 0.5) transparent transparent transparent
}

.profilePage .primaryUserBlock {
    border-left: 9px solid rgba(255,255,255,0.3)
}

.pageNavLinkGroup {
    border-top: 4px solid rgba(0,0,0,0);
    border-bottom: 4px solid rgba(0,0,0,0);
}

#portal_left_navigation, .portal_block, .messageList .message, .quickReply, .forum_who_is_viewing_list, .messageUserBlock, .profilePage #ProfilePanes, .PollContainer .secondaryContent, ol.memberList,.sidebar .secondaryContent, .subViewBlock, #portal_left_navigation li ul, .thread_create .xenForm, .thread_reply .xenForm, .conversation_add .xenForm, .conversation_reply .xenForm, .entries .entry, .review_list, .xenForm {
    border-bottom: 2px solid rgba(255, 255, 255, 0.3);
}

.profilePage .primaryUserBlock {
    box-shadow: 0px 2px 0px 0px rgba(255,255,255,0.3)
}

.tabs {
    box-shadow: inset 0 -5px 0px 0px rgba(255,255,255,0.3)
}

#portal_left_navigation, #portal_left_navigation li ul {
    border-top: 4px solid rgba(255, 255, 255, 0.3);
}

div.primaryContent.previewContent {
    background-color: rgba(0,0,0,1) !important
}

.styleChange:hover, .backToTop:hover, .footerLegal:hover, #legal:hover {
  background: linear-gradient(105deg,
    #fda085, #f6d365, #ff2400, #e81d1d, #e8b71d, #e3e81d, #1de840, #1ddde8, #2b1de8, #dd00f3, #dd00f3,
    #fda085, #f6d365, #ff2400, #e81d1d, #e8b71d, #e3e81d, #1de840, #1ddde8, #2b1de8, #dd00f3, #dd00f3,
    #f6d365);
  background-size: 200% 200%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: rainbow 10s linear infinite;
}

@keyframes rainbow {
    0% { background-position: 100% 100% }
  100% { background-position: 0% 0% }
}

.styleChange a::after {
    content: ' (Use tempStyle 2 Dark)'
}
#legal::after {
    content: ' â€¢ GBAtemp Custom Background by sks316#2523';
}
}
