style.css:
@-moz-document domain("skinbaron.de") {
body, html {
    background: #252525;
    color: #fff;
}
header.header{
    background: #000;
}

a {
    color: #fff;
    text-decoration: none;
}
.scrollToTop {
    background: #333;
}
.toolbar {
    height: 0;
    position: relative;
    font-size: 10px;
    color: #fff;
}
.filter {
    background: #333;
}
.subnavigation {

    background: #000;
}
.main-content .product-container .product-wrapper .product {
    background-color: #111
}
.filter-wrapper.fixed, .personal-area-header {
    background-color: #000;
}
.subnavigation .sub-nav ul li a {
    background: #000;
    border-right: 0;
}
.subnavigation .sub-nav .mp-lvl li.s-lvl a {
    background: #000;
    color: #fff;
    border-right: 0px solid #191630;
}
.subnavigation .sub-nav ul li a:after, .subnavigation .sub-nav ul li a:before {
    content: "";
    position: absolute;
    top: 0;
    left: 100%;
    height: 100px;
    width: 0;
    border-right-width: 0px;
    border-left-width: 0px;
    z-index: 13;
    border-left-color: #000;
    transition: all .3s;
}
input {
    -webkit-writing-mode: horizontal-tb;
    text-rendering: auto;
    color: #fff;
    letter-spacing: normal;
    word-spacing: normal;
    text-transform: none;
    text-indent: 0px;
    text-shadow: none;
    display: inline-block;
    text-align: start;
    -webkit-appearance: textfield;
    background-color: #333;
    -webkit-rtl-ordering: logical;
    cursor: text;
    margin: 0em;
    font: 400 13.3333px Arial;
    padding: 0px 0px;
    border-width: 0px;
    border-style: inset;
    border-color: initial;
    border-image: initial;
}
.filter .filter-block input.dynamic-filter-handle {
    color: #fff;
    font-size: 15px;
    padding: 0 1px;
    text-align: center;
}
.filter .filter-block h4 {
    font-size: 16px;
    color: #fff;
    text-transform: uppercase;
    font-weight: 700;
    margin-bottom: 20px;
    position: relative;
    cursor: pointer;
    transition: color .3s;
}
header.header .main-nav ul li .welcome {
    color: #fff;

}
header.header .main-nav ul li a {
    border-left: 0;
}

.modal-content {
    border-radius: 0;
    border: 0;
    box-shadow: 0 0 100px 10px rgba(8,6,48,.4);
    background: #000;
    padding: 40px;
}
.extended-info-modal .modal-content {
    background: hsla(0,0%,0%,.7);
    padding: 0;
}
.modal-inner-content {
    background: #000;
    padding: 40px;
}
.table td, .table th {
    padding: .75rem;
    line-height: 1.5;
    vertical-align: top;
    border-top: 0;
}
.modal-inner-content .modal-header {
    padding-bottom: 10px;
    border-bottom: 0;
    margin-bottom: 0;
    position: relative;
}
.modal-inner-content .modal-body .modal-info-box {
    padding-bottom: 10px;
    border-bottom: 0;
    margin-bottom: 20px;
    overflow: hidden;
    clear: both;
}
.table-striped tbody tr:nth-of-type(odd) {
    background-color: #000;
}
input {
    background-color: #000;
    -webkit-rtl-ordering: logical;
    cursor: text;
    margin: 0em;
    font: 700 13.3333px Arial;
    padding: 0px 0px;
    border-width: 0px;
    border-style: inset;
    border-color: initial;
    border-image: initial;
}
.subnavigation .sub-nav .mp-lvl {
    background: #000;
    width: 0;
    overflow: hidden;
    position: absolute;
    left: 100%;
    top: 0;
    transition: all .3s;
    z-index: 12;
}
.subnavigation .sub-nav .mp-lvl li.nav-item-testskin a {
    background: #000;
    color: #fff;
    border-right: 1px solid #000;
    font-size: 12px;
    font-weight: 700;
    padding: 55px 5px 0;
}
}
