style.css:
/*
/* i really want this to be global */
/*
/* reCAPTCHA Dark Theme by KMV, 2019 
/* https://userstyles.org/styles/175231/
/*
/* Other themes by KMV:
/* https://userstyles.org/users/370722
/*
/* Contact - http://old.reddit.com/r/KMV/
/*
*/


/*--------------------------- Text ---------------------------*/

/*Headers*/

#cf-wrapper h1,
#cf-wrapper h2,
.cf-error-footer p,
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > h1:nth-child(2),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(2) > h2:nth-child(1),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(3) > h2:nth-child(1),
h4#text,
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div p.cap_mess.cap_text.text-center,
html body.no-selection div.challenge-modal div.container div.modal.no-outline div.header h2#modal-title,
div.captcha_absolute div.captcha_div p.cap_mess.cap_text.text-center b,
html body.visible_mode div.wraper div.bottom span.powered_span
{
    color:#fff!important;
}


/*Text*/

#cf-wrapper h2 + p,
#cf-wrapper p + p,
#checkbox-label.no-selection,
#cf-spinner-please-wait,
.cf-subheadline,
.prompt-text,
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(2) > div:nth-child(2),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(3) > div:nth-child(3),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(3) > div:nth-child(4),
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.cap_head.clearfix div.cap_side.pull-right p.cap_note.cap_text.count_down,
p.cap_mess:nth-child(5),
html body.no-selection div.challenge-modal div.container div.modal.no-outline div#modal-content.content div.information div.instructions p.copy,
html body.no-selection div.challenge-modal div.container div.modal.no-outline div#modal-content.content div.information div.feedback,
html body.no-selection div.challenge-modal div.container div.modal.no-outline div#modal-content.content div.accessibility p.copy,
p.cap_note.cap_text.count_down
{
    color:#ccc!important;
}


/*Language Dropdown List Text*/

html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content > li
{
    color: #aaa !important;
}
/*Language Dropdown Text - Active*/
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content > li.active,
html body.no-selection div.challenge-modal div.container div.modal.no-outline div#modal-content.content div.accessibility div.status div.header div.text
{
    color: #fff !important;
}

/*hCaptcha Dropdown Menu and Button Text*/
html body.no-selection div.challenge-interface div.menu div.option.button div.wrapper div.copy,
html body.no-selection div.challenge-modal div.container div.modal.no-outline div#modal-content.content div.software-bug div.buttons div.button-cancel div.send-text,
html body.no-selection div.challenge-interface div.language-selector div#language-selector.list.button div#options-view.language-view div#options-list.language-list.no-outline div[id*="option-"] > .text,
div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content li
{
    color: #aaa !important;
    transition:.4s;
}
/*Language Dropdown Text - Hover*/
html body.no-selection div.challenge-interface div.menu div.option.button div.wrapper div.copy:hover,
html body.no-selection div.challenge-modal div.container div.modal.no-outline div#modal-content.content div.software-bug div.buttons div.button-cancel div.send-text:hover,
html body.no-selection div.challenge-interface div.language-selector div#language-selector.list.button div#options-view.language-view div#options-list.language-list.no-outline div[id*="option-"] > .text:hover,
div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content li:hover
{
    color: #fff !important;
    transition:.4s;
}


/*Links*/

#cf-wrapper a,
.challenge-modal [class*="link"],
.challenge-modal a[class*="link"]
{
    color: #00bcb7;
    transition:.4s;
}
#cf-wrapper a:hover,
.challenge-modal [class*="link"]:hover,
.challenge-modal a[class*="link"]:hover
{
    color: #00fff8 !important;
    transition:.4s;
}

/*------------------------ Backgrounds ------------------------*/

#cf-error-details.cf-error-details-wrapper div.cf-section.cf-wrapper div.cf-columns.two div.cf-column p,
#cf-wrapper #cf-error-details .cf-error-overview h2,
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(2) > div:nth-child(2),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(3) > div:nth-child(3),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(2) > div:nth-child(3) > div:nth-child(4)
{
    background: #0000 !important;
}

.challenge-modal [class*="header-title"],
html body.visible_mode div.wraper div.header,
html body.visible_mode div.wraper div.middle,
html body.visible_mode div.wraper div.bottom,
.rc-doscaptcha-header
{
    background: #000 !important;
}

html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div,
html.no-js body div#cf-wrapper div#cf-error-details.p-0 div.my-8.bg-gradient-gray
body > div:nth-child(1) > div:nth-child(1),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(2),
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div:nth-child(2) > div:nth-child(2) > div:nth-child(2) > div:nth-child(1),
body > div:nth-child(3),
#cf-wrapper,
#cf-wrapper #cf-error-details,
#challenge-wrapper [class="prompt-block"],
.cf-highlight,
.challenge-header .challenge-prompt .examples,
.challenge-modal [class*="container"],
html body.no-selection,
html body.no-selection div.challenge-interface div.menu-background,
html body.no-selection div.challenge-interface div.language-selector div#language-selector.list.button div#options-view.language-view div#options-list.language-list.no-outline div[id*="option-"].option,
.captcha_div,
div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content
{
    background: #111 !important;
    background-color: #111 !important;

}

html body.no-selection div.challenge-interface div.menu div.option.button div.bg,
html body.no-selection div.challenge-interface div.language-selector div#language-selector.list.button div#options-view.language-view div#options-list.language-list.no-outline div[id*="option-"].option:hover,
div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content li:hover
{
    background-color: #333 !important;
    transition:.4s;
}

/*------------- Image Backgrounds and Icons Tweak -------------*/

/*Main Page Right-hand side Imagery*/

#cf-wrapper #cf-error-details .cf-screenshot-container,
#Captcha-Container [class*="help-image"],
#Captcha-Container [class*="refresh-image"],
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div:nth-child(2) > div:nth-child(2) > div:nth-child(3) > div:nth-child(1)
{
    filter: invert(.95) grayscale(1);
    transition:.4s !important;
}

/*hCaptcha Images Background - "Movie-strips"*/

.task-image,
.example-image
{
    background-color: #181818!important;
    filter:brightness(.8)!important;
    transition:.4s;
}
.task-image:hover
{
    background-color: #171717!important;
    box-shadow: 0px 0px 3px 0px #555;
    filter:brightness(1)!important;
    transition:.4s!important;
}


.task-image div.loading-indicator
{
    filter:invert(1)!important;
}

/*hCaptcha Logo and Icons Tweak*/

.hcaptcha-logo, .icon-color, .icon-color:hover, .icon-grey, .icon-grey:hover
{
    transition:.4s !important;
}

/*"hCaptcha" Text Brightness Increase*/

#Captcha-Container [class*="text-img"]
{
    filter: brightness(4) !important;
}


/*hCaptcha Hovering*/

#Captcha-Container [class*="task-image"],
.rc-image-tile-target
{
    filter: brightness(.8)!important;
    transition:.4s!important;
}
#Captcha-Container [class*="task-image"]:hover,
.rc-image-tile-target:hover
{
    filter: brightness(1)!important;
    transition:.4s!important;
}

/*--------------------- reCAPTCHA itself ---------------------*/

/*Backgrounds*/

.anchor,
#Captcha-Container,
.rc-anchor-light,
#rc-imageselect,
.rc-anchor-checkbox-label,
.recaptcha-checkbox-border,
.yt-uix-button,
.rc-imageselect-desc-wrapper,
.rc-doscaptcha-body-text,
.jfk-textinput,
.rc-anchor-invisible-text,
#cf-wrapper #cf-error-details .cf-highlight .cf-highlight-inverse,
html body.invisible_mode div.wraper div.middle,
html body.invisible_mode div.wraper div.bottom
{
    background: #111 !important;
    background-color: #111 !important;
    color: #ccc !important;
}


/*-------------------------- Borders --------------------------*/

/*Main Border*/
.anchor.normal.light,
.challenge-modal [class*="container"],
div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content
{
    border: 1px solid #444;
    border-radius: 3px;
}

/*Border over Footer*/
#cf-wrapper #cf-error-details .cf-error-footer
{
    border-top: 1px #444 solid;
}

/*hCaptcha Main Window Border*/

body > div:nth-child(3)
{
    border: 1px solid #444 !important;
    border-radius:6px;
}
#Captcha-Container
{
    border-radius:4px;
}

/*hCaptcha "Selected" Border*/
div.task-image > .border
{
    border: 3px solid #777 !important;
    width: 136px !important;
    height: 136px !important;
    transition:.4s;
}


/*-------------------------- Buttons --------------------------*/

/*Skip Button*/

.rc-button-default:focus
{
    background-color: #222 !important;
    transition: all 0.4s ease-in-out !important;
    box-shadow: 0 0 0 1px #555 !important;
}

/*New hCaptcha Skip-Button*/
.submit-background
{
    background-color: #222 !important;
    transition: all 0.4s ease-in-out !important;
    border: 1px solid #555;
}
.submit-background:hover
{
    background-color: #333 !important;
    transition: all 0.4s ease-in-out !important;
    border: 1px solid #666;
}


/*Buttons*/
html body.no-selection div.challenge-modal div.container div.modal.no-outline div.button-close.button .close-image
{
    transition:.4s;
}

input#hcaptcha_submit,
#recaptcha-demo-submit,
#recaptcha_submit,
input#recaptcha_submit,
.rc-button-default,
.rc-doscaptcha-header,
html body.no-selection div.challenge-interface div.button-submit.button,
.challenge-modal [class*="button-"],
div.captcha_div div.dropdown button.dropdown-toggle
{
    background-color: #222 !important;
    border: 1px solid #444 !important;
    border-radius: 4px;
    transition: all 0.4s ease-in-out !important;
    color: #fff !important;
}
#hcaptcha_submit:hover,
.rc-button-default:hover,
.rc-doscaptcha-header:hover,
.submit-background:hover,
html body.no-selection div.challenge-interface div.button-submit.button:hover,
html body.no-selection div.challenge-modal div.container div.modal.no-outline div.button-close.button:hover,
html body.no-selection div.challenge-modal div.container div.modal.no-outline div#modal-content.content div.software-bug div.buttons div.button-cancel:hover,
div.captcha_div div.dropdown button.dropdown-toggle:hover
{
    background-color: #333 !important;
    filter:brightness(1);
    transition: all 0.4s ease-in-out !important;
}

#hcaptcha_submit:active,
.rc-button-default:active,
.rc-doscaptcha-header:active,
.submit-background:active,
html body.no-selection div.challenge-interface div.button-submit.button:active,
html body.no-selection div.challenge-interface div.menu div.option.button div.bg:active,
html body.no-selection div.challenge-modal div.container div.modal.no-outline div.button-close.button:active,
div.captcha_div div.dropdown button.dropdown-toggle:active,
div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content li:active
{
    filter:brightness(.6)!important;
    transition: all 0s ease-in-out !important;
}


/*Aid buttons, Bottom*/

.rc-button
{
    background-color: transparent !important;
    filter: invert(100%) !important;
}

/*hCaptcha Help- & Refresh (Oct. 2020)*/
html body.no-selection div.challenge-interface div.help.button,
html body.no-selection div.challenge-interface div.refresh.button
{
    background: #0000 !important;
    transition:.4s;
}

html body.no-selection div.challenge-interface div.refresh.button div.image-on,
html body.no-selection div.challenge-interface div.refresh.button div.image-off
{
    transition:.4s;
}
html body.no-selection div.challenge-interface div.help.button:active,
html body.no-selection div.challenge-interface div.refresh.button:active
{
    filter:brightness(.6)!important;
    transition:0s!important;
}

/*hCaptcha Logo*/
div.hcaptcha-logo div.icon-color
{
    filter: grayscale(1) brightness(.5) !important;
}
div.hcaptcha-logo div.icon-color:hover
{
    filter: grayscale(0) brightness(1) !important;
}
div.hcaptcha-logo div.icon-color:active
{
    filter: grayscale(0) brightness(.6) !important;
    transition:0s!important;
}

/*Language Dropdown - Button*/

html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown button.dropdown-toggle,
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content
{
    background: #111 !important;
}

/*Language Dropdown Hover*/

html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown button.dropdown-toggle,
html body.invisible_mode div.wraper div.captcha_absolute div.captcha_div div.dropdown.open div.dropdown-menu.dropdown-menu-container ul.dropdown-menu-content > li:hover
{
    background: #333;
}


/*------------------------ Checkboxes ------------------------*/

/*Main Checkbox*/
html body.no-selection div#anchor.anchor.normal.light div.anchor-content div.checkbox-wrapper div.checkbox-container div#checkbox.checkbox
{
    width: 30px;
    height: 30px;
    border: 1px solid #555;
    border-radius: 4px;
    background-color: #222;
}

/* Image Select Checkbox*/

.rc-imageselect-checkbox,
.rc-imageselect-tileselected .rc-imageselect-checkbox,
.rc-imageselect-dynamic-selected .rc-imageselect-checkbox
{
    background-color: transparent !important;
    filter: grayscale(1) !important;
}
.rc-imageselect-incorrect-response,
.rc-imageselect-error-dynamic-more,
.rc-imageselect-error-select-more,
.rc-imageselect-error-select-something
{
    background-color: #222 !important;
    color: #fff !important;
    filter: grayscale(1) !important;
}


html body.invisible_mode form.form-group.form-captcha
{
    margin-bottom: 0px;
}
