#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    border-radius: 0 2.5px 2.5px 0;
}

/*-Watch-Later-Icon*/

ytd-thumbnail-overlay-toggle-button-renderer {
    border-radius: 50%;
    transition: border-radius .5s, background-color .5s;
    background-color: var(--by-main-fg-color-tp);
    backdrop-filter: blur(5px);
    margin: 7px 7px 0 0;
}

ytd-thumbnail-overlay-toggle-button-renderer[use-expandable-tooltip][hovered] {
    border-radius: 0 20px 20px 0;
    background-color: var(--by-main-popup-color);
}
