    border-radius: 20px 0 0 20px;
    background-color: var(--by-main-popup-color);
}

/*-- Video Player --*/

.ytp-exp-chapter-hover-container {transition: margin-top .25s, height .25s;}
.ytp-progress-list {overflow-x: hidden;}
.ytp-drag .ytp-exp-chapter-hover-effect, .ytp-progress-bar-container:hover:not([aria-disabled=true]) .ytp-exp-chapter-hover-effect, .ytp-exp-chapter-hover-container:hover {
 transform: none;
 height: 10px;
 margin-top: -2.5px;
 border-radius: 500px;
 transition: margin-top .25s, height .25s;
}

.ytp-hover-progress-light {background: var(--by-main-lighter-color-5);}

.ytp-load-progress {
 background: var(--by-main-lighter-color-5);
 transition: transform .25s ease;
}
.ytp-play-progress {border-radius: 5px;}

.ytp-tooltip.ytp-preview .ytp-tooltip-bg {
 border-radius: 15px;
}

.ytp-exp-marker-tooltip .ytp-tooltip.ytp-preview:not(.ytp-text-detail) {
 background-color: var(--by-main-popup-color-55); 
}

/*[[unfold-animation]]*/

