/* ==UserStyle==
@name           zhihu暗黑模式2019
@namespace      USO Archive
@author         体物
@description    `不建议白天使用，不建议强光下使用。知乎黑暗模式，基本做到所有界面全覆盖；顶栏自动消失；去掉了碍眼的广告；闲时无事更新，哪里有随着官方更新没适配到可以告诉我。`
@version        20191226.11.23
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document url-prefix("https://www.zhihu.com/") {
    /* Chrome - 修改滚动条 */
    ::-webkit-scrollbar {
        width: 8px!important;
        height: 4px!important;
    }
    ::-webkit-scrollbar-track-piece {
        background-color: #121429!important;
        -webkit-border-radius: 0!important;
    }
    ::-webkit-scrollbar-thumb:vertical {
        height: 8px!important;
        background-color: #3183F9!important;
        -webkit-border-radius: 3px!important;
    }
    ::-webkit-scrollbar-thumb:horizontal {
        width: 8px!important;
        background-color: #3183F9!important;
        -webkit-border-radius: 3px!important;
    }



    /*背景*/
    body {
        background-color: #07091e;
    }

    .AppHeader,
    .Creator-sideColumn {
        background: #121429;
    }
    .Creator-menuItemHeaderName {
        color: #81829f;
    }
    .Creator-subMenuItem:hover {
        background: #2a2c44;
        color: #81829f;
    }

    .Card {
        background-color: #121429;
        border-bottom: 1px solid #222e40;
    }

    .MoreAnswers .List-headerText {
        background: #121429;
    }

    .MoreAnswers .List-header:after {
        border-bottom: 1px solid #252525;
    }



    .ContentItem-actions {
        background: #121429;
    }
    /*问题区域边框*/
    .Topstory-mainColumn .Card:not(.Topstory-tabCard) {
        border-bottom: 5px solid #07091e;
    }
    /*首页推荐关注热榜区域*/
    .Topstory-tabs {
        border-bottom: 8px solid #07091e;
    }

    .TopstoryTabs-link.Tabs-link.is-active {
        color: #444444;
    }

    .QuestionTopicReviewCardExtraInfo-cardTitle {
        color: #b6acbb;
    }

    .VotableTopicCard {
        background-color: #252525;
    }

    .QuestionHeaderTopicMeta.Card {
        border: 1px solid #b6acbb;
    }

    .ClubSliderList-header:before {
    border-bottom: 1px solid #121429;
}

    .ContentItem-more {
        color: #3183F9;
    }
    .ClubSliderList-name {
    font-size: 11px;
    line-height: 1.64;
    color: #8590A6;
}
    .ClubSliderList-arrow {
    width: 16px;
    cursor: pointer;
    height: 16px;
    border-radius: 12px;
    background-color: #2F314A;
}
    .GlobalSideBar-navNumber {
        background: #252525;
    }
    .GlobalWrite-topTitle {
    font-size: 12px;
    line-height: 1;
    text-align: center;
    color: #8590A6;
}
    .Topstory--old .HotListNav {
    border: solid #121429;
    border-width: 0 0 .5px;
}
    .CreatorEntrance-indexPageTitle {
    font-size: 14px;
    font-weight: 500;
    line-height: 1.4;
    color: #8590A6;
}
    .css-vurnku,
    .GlobalWrite-navTitle,
    .ProfileSideCreator-readCountNumber
    {
    color: #8590A6;
}
    .GlobalWrite-answerLater
    {
    border-right: 1px solid #121429;
}
    .GlobalWrite-navTop {
    position: relative;
    padding: 20px 15px;
    border-bottom: 1px solid #121429;
}
    .CornerButton,
    .ClubSliderList
    {
        background: #121429;
    }


    .HotListNav {
        background: #121429;
        border-bottom: 1px solid #292a40;
    }
    .HotListNav-item,
    .HotListNav-sortableItem[data-hotlist-identifier=total] {
        color: #8590a6;
        background: #2f314a;
    }
    .HotList,
    .Topstory-content {

        border: 0px solid #292a40;
        background: #121429;
    }


    /*第一个推荐-软广*/
    .EventcardItem {
        display: none
    }


    .QuestionHeader {
        background: #121429;
    }
    /*ziti*/
    .HotListNavEditPad-title,
    .HotListNavEditPad-titleHint {
        color: #aaaab7;
    }

    .QuestionHeader-footer {
        background: #121429;
    }

    a {
        color: #b6acbb;
    }

    .Body--Mobile {
        background: #121429;
    }

    .Body--Mobile .RichContent-inner--collapsed .ContentItem-more:before,
    .Body--Mobile .RichContent.is-collapsed .RichContent-inner .ContentItem-more:before {
        background: #121429;
    }
    .Body--Mobile .RichContent-inner--collapsed .ContentItem-more,
    .Body--Mobile .RichContent.is-collapsed .RichContent-inner .ContentItem-more {
        background: none;
    }
    .TopstoryMain {
        background-color: #252525;
    }



    .CommentTopbar-title {
        color: #b6acbb;
    }

    .CommentListV2-header-divider {
        color: #b6acbb;
        background-color: #121429;
    }

    .CommentsV2-footer {
        background: #252525;
    }

    .CommentsV2-Packup-Button {
        background: #121429;
    }

    .CommentListV2 {
        background-color: #252525;
    }
    .SearchTabs {
        background-color: #121429;
    }
    .Tabs-link {
        color: #b6acbb;
    }
    .WikiBoxHeader-Meta {
        color: #b6acbb;
    }
    .WikiBoxHeader-IntroductionWrapper {
        color: #b6acbb;
    }
    .RichContent {
        color: #b6acbb;
    }
    .Search-container {
        color: #b6acbb;
    }
    .TopSearch-itemLink {
        color: #b6acbb;
    }
    .RelevantQuery h2 {
        color: #b6acbb;
    }
    /*热榜*/
    .HotItem {
        background-color: #121429;
    }
    .HotList-nav {
        background-color: #121429;
        border-bottom: 1px solid #07091e;
    }




    /*控制条区域*/
    /*正文链接区域*/
    .LinkCard-content {
        border-radius: 10px;
        background-color: #2b2c40;
    }
    .LinkCard-backdrop {
        position: static;
    }
    /*标题高亮*/
    .Highlight em,
    .TimeBox-ContentTitle em {
        font-style: normal;
        color: #fff;
    }
    .LinkCard-title {
        color: #b6acbb;
    }

    /*回答数量*/
    .List-headerText {
        color: #b6acbb;
    }
    /*评论区*/
    .CommentListV2 {
        background-color: #121429;

        background: #121429;
        border-bottom: 0px solid
    }
    /*顶部更多按钮*/
    .Menu-item.is-active {
        background: #303146;
    }
    QuestionHeader-menu-item Button--plain/*举报区域*/
    .Modal-inner {
        color: #b6acbb;
        background: #121429;
    }
    .ReportMenu-item {
        color: #b6acbb;
        border-bottom: 1px solid #292a40;
    }

    /*顶部标题*/
    .TopicMetaCard-title {
        color: #0077e6;
    }




    .RichText {
        color: #b6acbb;
    }
    .UserLink {
        font-size: 14px;
    }



    .CommentEditorV2-inputWrap--active {

        border: 1px solid #121429;
        background-color: #121429;
    }
    .CommentEditorV2--normal.CommentEditorV2--active {
        background-color: #121429;
        border-top: 1px solid #292a40;
    }

    .NestComment--rootComment {
        position: static;
    }
    .NestComment .NestComment--child {
        position: static;
    }


    .CommentEditorV2-input .Editable,
    .CommentEditorV2-input .Editable--focus {
        background: #2f314a
    }

    .CommentEditorV2-inputWrap {
        border: 1px solid #121429;
        border-radius: 2px;
        background-color: #121429;
    }
    .CommentsV2-footer {
        background: #121429;
        border-top: 0px solid #5C5F71;
    }

    /*关注问题*/
    .Button--primary.Button--blue,
    .Button--blue,
    .Button--grey {
        color: #99a4bb;
        ;
        border-color: #2f3148;
        ;
        background-color: #2f314a;
        ;
    }
    .Select-option {
        color: #99a4bb;
        border: none;
        background-color: #121429;
    }
    .Select-option:hover {
        display: inline;
        color: #3e6cc8;
        background-color: #121429;
        text-align: left;
        background: none;
        border: none;
    }


    .Modal-inner {
        border-radius: 10px;
        background-color: #121429;
    }



    /*去赞赏,去footer刘看山等版权信息*/
    .Reward,
    .Footer,
    .CornerButtons {
        display: none
    }


    /*用户个性签名*/
    .AuthorInfo-badgeText {
        color: #babed7;
    }
    #Popover39-content {
        border-radius: 10px;
        background-color: #121429;
    }



    /*顶部*/
    .Input-wrapper.Input-wrapper--grey,
    .Input-wrapper {
        background: #2f314a;
        border-top: 0px solid #5C5F71;
        border-left: 0px solid #07091e;
        border-right: 0px solid #07091e;
        border-bottom: 0px solid #07091e;
    }

    .Tag {
        position: relative;
        display: inline-block;
        /* height: 30px; */
        padding: 0 12px;
        font-size: 14px;
        line-height: 30px;
        color: #0084ff;
        vertical-align: top;
        border-radius: 100px;
        background: rgba(152, 164, 175, 0.1);
    }
    .AppHeader-TabsLink.is-active {
        color: #babed7;
    }
    .Tabs-link {
        color: #5C5F71;
    }
    .AppHeader-TabsLink:hover {
        color: #0077e6;
    }
    .Button--blue:hover,
    .Button--grey:hover,
    .Button--primary.Button--blue:hover {
        border-color: #0077e6;
        background-color: #0077e6;
    }



    /*等你来答顶部*/
    .QuestionWaiting-types {
        -webkit-box-align: center;
        -ms-flex-align: center;
        align-items: center;
        background: #121429;
        border-bottom: 1px solid #f6f6f6;
        border-radius: 2px 2px 0 0;
        display: -webkit-box;
        display: -ms-flexbox;
        display: flex;
        height: 68px;
        padding: 0 10px;
    }
    .QuestionWaiting-typesTopper {
        background-color: #141631;
    }
    .ListQuestionItem-title {
        color: #d9dadd;
        font-size: 18px;
        line-height: 27px;
        margin-bottom: 8px;
        font-weight: 600;
    }

    .ListQuestionItem-writeAnswerButton,
    .Button--grey.Button--plain {
        color: #838ea3;
        background-color: #2e3144;
        border-color: #2e3144;
        border-radius: 8px;
    }

    /*打开通知*/
    div.PushNotifications-item {



        color: #5C5F71;
    }
    .PushNotifications-item:after {
        height: 1px;
        padding: 0 16px;
        background-color: #141631;
        background-clip: content-box;
    }


    .Popover-content,
    .Messages-footer {
        position: absolute;
        background: #121429;
        border: 0px solid #ebebeb;
        border-radius: 8px;
    }
    /*阅读全文*/
    .ContentItem-more {
        color: #5478f0
    }

    /*问题列表顶部*/
    .List-header {
        border: 0px solid #ebebeb;
    }

    .PushNotifications-header {
        border-bottom: 0px solid #ebebeb;
    }
    .Popover-arrow:after {
        background-color: #5C5F71;
        border: 0px solid #ebebeb;
    }
    .PushNotifications-tab,
    .PushNotifications-tab:not(:last-child):after {
        position: static;
    }
    .Notifications-footer {
        color: #5C5F71;
        background-color: #121429;
        border-top: 1.5px solid #121429;
        border-left: 0px solid #07091e;
        border-right: 0px solid #07091e;
        border-bottom: 0px solid #07091e;
    }


    /*问题主题的知乎评价系统*/
    .QuestionHeaderTopicMeta.Card,
    .VotableTopicCard {
        border-color: #252635;
        ;
        background: rgba(152, 164, 175, 0.1);
    }
    .TopicVoteCheckbox > input + label {
        border-color: #333444;
        background-color: #121429;
    }

    .QuestionHeaderTopicMeta .VotableTopicCard {
        background: rgba(42, 52, 62, 0);
    }


    /*热榜话题标签*/
    .HotList-navItem,
    .GlobalSideBar-navNumber {
        margin: 0 4px;
        padding: 5px 20px;
        border-radius: 15px;
        font-size: 14px;
        line-height: 20px;
        color: #5C5F71;
        background-color: rgba(152, 164, 175, 0.1);
    }
    .HotList-navItem,
    .GlobalSideBar-navNumber,
    .GlobalSideBar-navLink:hover {
        color: #5C5F71;
        background-color: rgba(152, 164, 175, 0.1);
    }
    /*个人创作区域*/
    .GlobalWrite-navTitle,
    .ProfileSideCreator-readCountNumber {
        color: #8590A6;
    }


    /* 圆形头像 & 圆形图标 */
    .Avatar {
        border-radius: 50%
    }
    /* 圆形头像 & 圆形图标 */
    /* Card 圆角 */
    .Card {
        border-radius: 5px
    }
    /* Card 圆角 */
    /*徽章*/
    .LabelContainer {
        display: none;
    }

    .Topic-bar {

        background-color: rgba(152, 164, 175, 0.1);
    }

    /************************************************去边栏部分*****************************/
    /*去边栏部分-问题页*/
    div.Question-mainColumn {
        width: 680px;
        margin-left: 100px;
        margin-right: 70px;
    }

    /*自动隐藏顶部导航栏*/
    .AppHeader {
        top: -43px !important;
        /*41px是为了把搜索框整个隐藏,40px会露出一条线*/
    }

    .ContentItem-actions {
        bottom: -43px !important;
    }
    .ContentItem-actions:hover {
        bottom: 0px !important;
    }
    /*鼠标滑过显示导航栏*/
    .AppHeader:hover {

        top: 0 !important;
    }

    .Question-sideColumn,
    .Question-sideColumn--sticky {
        display: none
    }



    .QuestionHeaderTopicMeta.Card {
        width: 680px;
        margin: 0 auto;
        border-radius: 5px
    }

    div.Question-mainColumn,
    .QuestionAnswers-answers {
        width: 1000px;
        margin-left: 0px;
        margin-right: 70px;
    }

    .Reward {
        display: none;
    }
    /* ====== 问题详情 ====== */
    .QuestionHeader {
        margin: 10px auto;
        width: 1000px;
        min-width: 1000px;
        border-radius: 6px
    }

    .QuestionHeader-side {
        padding-right: 30px
    }
    /* ====== 问题详情 ====== */
    .QuestionRelatedCard {
        color: #8590a6;
        background: #2e3043;
    }
    .QuestionRelatedCard-content,
    .QuestionRelatedCard-from,
    .QuestionRelatedCard-content > a {
        color: #8590a6;
    }
    .QuestionRelatedCard-content {
        color: #8590a6;
    }
    .LinkCard--noImage .LinkCard-content {
        background-color: #2e3043;
    }
    .LinkCard-image--default {
        background-color: #2e3043;
        color: #d3d3d3;
    }


    /*发现页**************************/
    .ExploreHomePage-ContentSection-header {
        color: #0084ff;
    }
    .ExploreHomePage-specialCard,
    .ExploreHomePage-roundtableCard,
    .ExploreHomePage-collectionCard,
    .ExploreHomePage-columnCard {
        background: #121429;
    }
    .ExploreFollowButton:not(.ExploreFollowButton--colored) {
        color: #b6acbb;
        background-color: #2e3043;
        border-radius: 15px
    }
    .ExploreHomePage-footer {
        display: none;
    }
    .ExploreSpecialCard-header {
        border-bottom: 1px solid #222e40
    }

    .ExploreCollectionCard-contentTypeTag,
    .ExploreHomePage-ContentSection-moreButton a {
        background: #2e3043;
    }
    .ExploreSpecialCard-contentTag {
        background: #2e3043;
        border-radius: 15px;
    }

    .CommentsV2-openComment-divider {
        height: 0px;
        border-top: 1px solid #1e2a3c;
        /*分割线*/
    }

    .CommentItemV2 {
        border-bottom: 1px solid #1e2a3c;
        /*分割线*/
    }

    .Messages-followItem:hover {
        background: #2e3043;
    }
    .QuestionWaiting-type:hover {
        color: #0084ff;
    }
    /*全部通知*/
    .NotificationList,
    .Notifications-Section-header {
        background: #121429;
    }




    /*头像底色*/
    .UserAvatar {
        display: inline-block;
        overflow: hidden;
        vertical-align: top;
        border: 0px solid #2e3043;
        border-radius: 8px;
    }
    .Mask,
    .Mask-mask--black,
    .Mask-mask,
    .UserAvatarEditor-maskInner {
        display: none
    }


    .Field-card:hover {
        color: #0084ff;
        background-color: #121429;
    }
    .Field-card span:hover {
        color: #0084ff;
    }

    /**************通知中心*********/
    .Notifications-Main > header h1,
    .Notifications-Section-header h2,
    .NotificationList-DateSplit {
        color: #5C5F71;
    }
    .NotificationList-Item-header {
        color: #8590a6;
    }

    .NotificationList-Item-extendText {
        color: #8590a6;
    }
    .NotificationList-Item-link {
        font-size: 18px;
    }

    /*私信新建*/
    .Messages-footer {
        top: 450px
    }
    .ProfileHeader-main,
    .ProfileHeader-userCover,
    .ProfileHeader-wrapper {
        background-color: #121429;
    }
    /***************设置页面**********/
    .SettingsMain-mainColumn,
    .ExpandableField,
    .SettingsFAQ {

        background-color: #121429;
        border: 0px solid #2e3043;
    }
    .SettingsTitle-title,
    .ExpandableField-title {
        color: #8590a6;
    }
    .Button--link {
        color: #a7b3ca
    }


    /*分割线颜色*/
    .ProfileSideCreator-analytics,
    .ProfileSideCreator-readCountItem,
    .GlobalWrite-draft,
    .QuestionWaiting-types,
    .QuestionWaiting-questionItem,
    .QuestionLater-title,
    .Messages-header,
    .Profile-lightItem,
    .Profile-footerOperations,
    .Profile-lightItemName,
    .Profile-lightItem:last-child,
    .Profile-sideColumnTitle,
    .ProfileMain-tabs,
    .NumberBoard--divider .NumberBoard-item + .NumberBoard-item .NumberBoard-itemInner,
    .zg-item-log-detail,
    .zm-topic-topbar,
    .Field,
    .Notifications-Section,
    .SettingsInbox,
    .ExpandableField-titleView--bordered,
    .SettingsMain-page,
    .SettingsEmail,
    .SettingsTitle,
    .SettingsAccount-account,
    .SettingsAccount-personalUrl,
    .AccountSocial,
    .BlockedTopics,
    .BlockedUsers,
    .BlockedFollowees,
    .SettingsPrivacy,
    .ExpandableField-content,
    .SettingPrivacySwitch,
    .Switch,
    .Switch--checked,
    .SettingPrivacySwitchContainer .SettingPrivacySwitch,
    .Card-group-2RcF > .Card-card-2K6v + .Card-card-2K6v,
    .Menu-menuItem-1oId + .Menu-menuItem-1oId:before,
    .PubIndex-book-main .Summary .TabContent .ExtInfo {
        border-color: #222e40;
    }

    /*隐私保护按钮*/
    .Switch,
    .SettingPrivacyCard {
        background-color: #394c68;
        border: 0px;
    }
    .SettingsNav-linkTxt,
    .SettingPrivacySwitch,
    .SettingPrivacyCard span,
    .SettingsFAQ-faqTitle,
    .SettingsFAQ-pageTitle,
    .AccountSocial h4,
    .SettingsTitle-subTitle,
    .is-active {
        color: #8590a6
    }

    /*提问框选中*/
    .Input-wrapper.is-focus {
        color: #5C5F71;
        background-color: #2f314a;
        border: 1px solid #606b86;
    }
    #Popover61-toggle {
        color: #5C5F71;
    }
    /*提问框-展开 -工具栏*/
    .Editable-toolbar {
        background: #2f314a;
        ;
        border-bottom: 1px solid #222e40;
    }




    /*绑定社交媒体居中*/
    .AccountSocial {
        text-align: center;
    }
    .AccountSocial-wrap {
        margin-left: 100px;
        padding-top: 14px;
    }

    .NotificationList-Item.NotificationList-Item:after {
        content: "";
        position: absolute;
        top: 0;
        left: 32px;
        right: 32px;
        height: 1px;
        background-color: #222e40;
    }
    .NotificationList-DateSplit.NotificationList-DateSplit:after {

        height: 1px;
        background-color: #222e40;
    }
    .NotificationList,
    .Notifications-Section-header {
        border-color: #222e40;
        background: #121429;
    }
    /*分割线*/
    .ToolsQuestionNew-header,
    .tablist,
    .Tabs,
    .border-pager,
    .ToolsQuestionKnowledgePlan-header,
    .ExploreCollectionCard-followers,
    .ChatBox-title,
    .Title-tipContainer-1woE,
    .Menu-divider,

    .zm-topic-manage-item,
    .TopicBioItem-addButton,
    .QuestionRelatedCard-footer,
    .topic-feed-title,
    .zm-topic-cat-main,
    .Emoticons,
    .QuestionHeader-footer.is-fixed,
    .css-1pp4h0z,
    .topic-recommend,
    .feed-item + .feed-item,
    .SearchBar-label,
    .css-9ytsk0,
    .MoreAnswers .List-header:after,
    .css-zi12qw,
    .zm-comment-form .zm-comment-editable,
    .zm-item-comment + .zm-item-comment,
    .zm-comment-box,
    .zm-comment-form.zm-comment-box-ft.expanded,
    .Tabs-item,
    .ToolsQuestionItem,
    .ToolsQuestionNew-goodatTopicHint,
    .Creator-SubTab,
    .ToolsQuestionRecommend-header,
    .ToolsQuestionRecommend-goodatTopicHint,
    .zm-item,
    .SelectedSection-module-content-1KL- + .SelectedSection-module-content-1KL-:before,
