style.css:
@-moz-document url-prefix("http://www.talkorigins.org/faqs/"), url-prefix("http://www.talkorigins.org/indexcc/"), url-prefix("http://www.talkorigins.org/origins/postmonth/"), url-prefix("http://www.talkorigins.org/origins/feedback/") {
body {
    background-color: #101010; /* Originally #111111, changed it to the same background color of the images in the FAQ */
    color: #CCCCCC !important;
}

address,h1, h2, h3, h4, h5, h6,.refs{
    color: #CCCCCC !important;
}

/*
Copy-pasted rules of the homepage css 
*/
a:link,a:link.nomarkup {
    color:yellow;
}
a:visited {
    color:#c0c000;
}
a:active {
    color:#c0c000;
}
a:hover {
    color:#808000;
}
img[src$="/pictures/thicksep.gif"],img[src*="/pictures/letters/"],img[src*="/pictures/arrow_"] {
    filter:invert(100%);
}

blockquote,.quotebox,.grey,.otherlinks, body p table,
table /*[bgcolor="#e0e0e0"]*/{
    color: #CCCCCC !important;
    background: #1a1919 !important;
    padding: 0.75em;
    margin:1.5em;
}
}

@-moz-document url-prefix("http://www.talkorigins.org/origins/feedback") {
.feedbackheader { background: #29293e;
                       color: #CCCCCC }
.feedback       { background: #505066;
                       color: #CCCCCC;
								  text-align: left }
.responseheader { background: #294029;
                       color: #CCCCCC }
.response       { background: #415341;
                       color: #CCCCCC;
									text-align: left }
									
td.feedbackheader b a:link { background: transparent; color: #eeeeee }
td.feedbackheader b a:visited { background: transparent; color: #eeeeee  }
td.feedbackheader b a:hover { background: transparent; color: white  }
td.responseheader b a:link { background: transparent; color: #eeeeee  }
td.responseheader b a:visited { background: transparent; color: #eeeeee  }
td.responseheader b a:hover { background: transparent; color: white  }
}
