/* Schoology S Badge */

.\_pendo-launcher-badge\_, .\_pendo-launcher-badge\_.\_pendo-launcher-badge-bottom-right\_.onboarding {
    z-index: 400001;
    right: 15px;
    width: 56px;
    height: 56px;
    border-radius: 50%;
    background: transparent;
    transition: box-shadow 80ms ease-in-out;
    box-shadow: 0 1px 6px rgba(0, 0, 0, .06), 0 2px 20px rgba(0, 0, 0, .2);
    transform-origin: center;
    cursor: pointer;
    opacity: 1;
    -webkit-animation: pendo-launcher-badge-appear .25s ease;
    animation: pendo-launcher-badge-appear .25s ease;
    background-clip: content-box;
    visibility: hidden;
}

.\_pendo-launcher-badge\_::before {
    content: '';
    display: block;
    background-image: url(https://i.imgur.com/hbtyQKL.png);
    background-size: 56px 56px;
    background-repeat: no-repeat;
    background-position: center;
    opacity: 1;
    -webkit-transform: rotate(0deg) scale(1);
    -ms-transform: rotate(0deg) scale(1);
    transform: rotate(0deg) scale(1);
    transition: opacity .08s linear, -webkit-transform .16s linear;
    transition: transform .16s linear, opacity .08s linear;
    transition: transform .16s linear, opacity .08s linear, -webkit-transform .16s linear;
    visibility: hidden;
}

.\_pendo-launcher-badge\_::after {
    content: '';
    display: block;
    background-image: url(https://i.imgur.com/hbtyQKL.png);
    background-size: 56px 56px;
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    top: 0;
    bottom: 0;
    width: 100%;
    opacity: 1;
    -webkit-transform: rotate(0deg);
    -ms-transform: rotate(0deg);
    transform: rotate(0deg);
    transition: opacity .08s linear, -webkit-transform .16s linear;
    transition: transform .16s linear, opacity .08s linear;
    transition: transform .16s linear, opacity .08s linear, -webkit-transform .16s linear;
    visibility: hidden;
}


