style.css:
@-moz-document domain("lublin112.pl") {
body {
	color: #969696;
    background-color: #2b2b2b;
}

.single #single-article .content p {color: #969696;}

#further-news>ul li a, #mixed-content h2 a, #mixed-content h4 a, #latest-news .box .category a, #latest-news .box .icons .circle, #further-news>ul li .icons .circle, .box-content-section .box .icons .circle, #this-day .box .year, .share ul li.comment, .share ul li.comment a, .share ul li.comment a:before {
    color: #c9c9c9;
}
#mixed-content h2, #mixed-content h4, .full-width-content .container h2, #video .row.title h2, .thumbnail-section .row.title h2 {
    border-bottom: solid 3px #c9c9c9;
    color: #c9c9c9;
}
#further-news>ul li .category, #latest-news .box .category, #latest-news .box .icons .circle, .share ul li.comment a, .share ul li.comment a:before, .circle, #this-day .box .year {
    background: #444444;
}
#further-news>ul li {
    border-bottom: solid 1px #4a4a4a;
}
.section-more a {
    background: #e310136b;
}
#main-menu-wrapper #main-menu, .breadcrumbs ol {
    background: #353535;
}
@media (min-width: 62em) {
	#main-menu-wrapper #main-menu .nav>li button, #main-menu-wrapper #main-menu .nav>li a {
		color: #949494;
	}
}
#main-menu-wrapper #main-menu .nav>li button .icon img, #main-menu-wrapper #main-menu .nav>li a .icon img {
    filter: brightness(40);
    opacity: 0.6;
}
a.navbar-brand img {
    filter: brightness(40);
    opacity: 0.6;
}
#main-menu-wrapper #main-menu .slogan, #main-menu-wrapper #main-menu .quick-contact span:before {
    color: #aeaeae;
}
#main-menu-wrapper #main-menu .container, #top-bar, #main-menu-wrapper #main-menu, #top-bar .date-and-weather .weather, #mixed-content h2, #mixed-content h4, #mixed-content .container, #praca112-box ul li {
    border-color: #4a4a4a!important;
}
#latest-news .side .box .content .title h2 {
    font-size: 2rem;
    line-height: 1.7;
    color: #dcdcdc;
}
#latest-news .side .box:hover .content .excerpt {
	background: #252525ba;
	padding: 5px 10px;
}
#latest-news .side .box:hover .content .title h2, #latest-news .large .box:hover .content .excerpt {
    margin-bottom: 3px;
    padding: 6px 10px;
    border-bottom: solid 1px #585858ba;
    background-color: #252525ba;
}
.form-control, #praca112-box ul li a.circle, #ogloszenia-box ul li a.circle, #praca112-box ul li a.circle {
    background-color: #4a4a4a;
}
.glyphicon-search:before {
    color: #4a4a4a;
}
#latest-news .large .box:hover .content .title h2 {
    border-bottom: solid 1px #585858ba;
}
#video, #respond {background: #3e3e3e;}
#your-region {
    background: #373737;
}
.box-content-section .box .title {background: #2b2b2b;}

