}
#ogloszenia-box h4 img {
	filter: brightness(40);
    opacity: 0.6;
}
#what-where {
    background: #373737!important;
}
textarea#comment, #respond textarea, #respond input {
    background: #353535;
    border-color: #565656;
}
.error404 #page .content blockquote p, .error404 #single-gallery .content blockquote p, .error404 #single-article .content blockquote p, .page #page .content blockquote p, .page #single-gallery .content blockquote p, .page #single-article .content blockquote p, .single #page .content blockquote p, .single #single-gallery .content blockquote p, .single #single-article .content blockquote p {
