/* {
	background: grey;
}*/
.QuestionWrapper, .RichTextEditorOrViewer-Froala {
	background: transparent;
}
.Markdown.Tekst a, .Theming-plot26 [class*="button_"][class*="_link_accent2"], .Opdracht-sections a {
