@-moz-document url("https://plotify.plot26.nl/") {
.plotify .header {
	background: #131719;
}
.portal#main, .small-6.large-6.columns.contentright {
	background: #1b2022;
}
div[style="position: absolute; top:4.8em; bottom:1em; right: 41.6%; border-right: 1px solid #aaa;left: 0;"] {
	border-color: #323639 !important;
}
div#book-description, div#book-title {
	color: white;
}
}

