body.home.index, body.questions .vertical-center, body.exams .vertical-center, body.questions .question-container .answer-row input.answer, body.exams .question-container .answer-row input.answer {
	background: #131719;
}
.stage.diagnostic a, .modal-dialog, .progress .empty, body.questions .question-container .answer-row input.answer.disabled, body.exams .question-container .answer-row input.answer.disabled, section.stage .stage-name {
	background: #1b2022;
}
.stage.diagnostic a h2, .stage.diagnostic a .sub-title, body.exams.show .explanation h1, body.exams.show .explanation .explanation-text, body.questions .question-container .question, body.exams .question-container .question, body.questions .question-container .answer-row input.answer, body.exams .question-container .answer-row input.answer, body.questions .question-container .answer-row input.answer.disabled, body.exams .question-container .answer-row input.answer.disabled, span.correct-answer.answer {
