style.css:
@-moz-document domain("brick-hill.com") {
body {
    color: #000;
    background-color: #71BAED;
    background-image: url("https://img1.picmix.com/output/stamp/normal/3/1/2/5/365213_759c6.gif");
}

/* navBar */
nav div.primary {
    background-color: #a81005;
    color: #fff;
}

nav div.secondary {
    background-color: #016B1E;
    color: #eee;
}

nav .info {
    background-color: #016B1E;
}

/*cards*/

.card .content {
    background-color: #E9EBF3;
}

.thread-row {
    position: relative;
    border-bottom: 2px solid #c1c4ce;
}

.line, hr {
    border-top: 1px solid #c1c4ce;
    border-bottom: 1px solid #c1c4ce;
}

.toggle-user-desc:not(.open):after {
    background-color: #E9EBF3;
}

.read-more-desc {
    background-color: #E9EBF3;
}

.avatar-card.crate img {
    background-color: #fdfdfd00;
}

.forum-bar .fa-angle-double-right {
    color: #C51105;
}

.blockquote,blockquote {
    border-radius: 0px 5px 5px 0px;
    background-color: #DEE0E5;
}

.blockquote.blue, blockquote.blue {
    border-color: #C51105;
}

.blockquote.green, blockquote.green {
    border-color: #007D1F;
}

.blockquote.blue, blockquote.red {
    border-color: #007D1F;
}

.blockquote.green, blockquote.orange {
    border-color: #C51105;
    
}


.pages .page.active {
    color: #C51105;
}

.pages.green .page.active {
    color: #c51105;
}

.pages.red .page.active {
    color: #c51105;
}

.pages.orange .page.active {
    color: #c51105;
}

.pages .page {
    color: #007d1f;
}

.hover-card.viewed {
    background-color: #D9DBE2
}

.hover-card:hover {
    background-color: #DEE0E5
}

.profile-card:hover {
    background-color: #DEE0E5
}

.friend-card:hover {
    background-color: #DEE0E5
}

.crate-types :hover {
    background-color: #DEE0E5
}

.crate-types .active, .crate-types :hover {
    background-color: #DEE0E5;
    border-left: 4px solid #C51105;
}

/*cards top*/
.card .top:not(.absolute) {
    color: #f2f2f2;
    background-color: #007D1F;
    border-color: #C51105;
}

.card .top:not(.absolute).blue {
    color: #f2f2f2;
    background-color: #C51105;
    border-color: #007D1F
}

.card .top:not(.absolute).green {
    color: #f2f2f2;
    background-color: #007D1F;
    border-color: #C51105;
}

.card .top:not(.absolute).orange {
    color: #f2f2f2;
    background-color: #C51105;
    border-color: #007D1F;
}

.card .top:not(.absolute).red {
    color: #f2f2f2;
    background-color: #007D1F;
    border-color: #C51105;
}

.thread-label.blue {
    background-color: #C51105;
}

.thread-label.green {
    background-color: #007D1F;
}

.thread-label.orange {
    background-color: #C51105;
}

.thread-label.red {
    background-color: #007D1F;
}

/*buttons*/

.button.blue, button.blue {
    color: #fff;
    background-color: #C51105;
    border-color: #950e05;
}

.button.red, button.red {
    color: #fff;
    background-color: #C51105;
    border-color: #950e05;
}

.button.green, button.green {
    color: #fff;
    background-color: #009f28;
    border-color: #007D1F;
}

.button.orange, button.orange {
    color: #fff;
    background-color: #009f28;
    border-color: #007D1F;
}

.button.tab-button.transparent, button.tab-button.transparent {
    background-color: transparent;
    border-color: transparent;
    color: #fff;
}

.input-group .input-button {
    border-left: 1px solid #007D1F;
    color: #cbcbcb;
    background-color: #009f28;
}

/*tabs*/


.tabs .tab-body {
    border: 1px solid #c1c4ce;
    background-color: #E9EBF3;
}

.tabs .tab.active {
    background-color: #E9EBF3;
    border-bottom: 1px solid #c1c4ce;
}

.tabs .tab {
    background-color: #D9DBE2;
    border: 1px solid #c1c4ce;
}

/*membership*/

.mint-gradient {
    background-image: -webkit-gradient(linear,right top,left top,from(#e5f2f7),to(#fefefe));
    background-image: linear-gradient(270deg,#e5f2f7,#0000);
}

.ace-gradient {
    background-image: -webkit-gradient(linear,right top,left top,from(#fededb),to(#fefefe));
    background-image: linear-gradient(270deg,#fededb,#fefefe00);
}

.royal-gradient {
    background-image: -webkit-gradient(linear,right top,left top,from(#fdf1d8),to(#fefefe));
    background-image: linear-gradient(270deg,#fdf1d8,#fefefe00);
}
}
