style.css:
@-moz-document domain("brick-hill.com") {
body {
    color: #000;
    background-color: #71BAED;
/*[[bkgsnow]]*/
}

  /*this theme was made with love by rowbot :^) 
     if you need to use it for some reason, please contact me and we can talk about it
     you can edit this theme for yourself though*/
  
/* navBar */
nav div.primary {
    background-color: #a81005;
    color: #fff;
}

nav div.secondary {
    background-color: #016B1E;
    color: #eee;
}

nav .info {
    background-color: #016B1E;
}

/*cards*/

.card .content {
    background-color: #E9EBF3;
}

.line, hr {
    border-top: 1px solid #c1c4ce;
    border-bottom: 1px solid #c1c4ce;
}

.toggle-user-desc:not(.open):after {
    background-color: #E9EBF3;
}

.read-more-desc {
    background-color: #E9EBF3;
}

.avatar-card.crate img {
    background-color: #fdfdfd00;
}

.forum-bar .fa-angle-double-right {
    color: #C51105;
}

.blockquote,blockquote {
/*[[bkgquote]]*/
}

.blockquote.blue, blockquote.blue {
    border-color: #C51105;
}

.blockquote.green, blockquote.green {
    border-color: #007D1F;
}

.blockquote.blue, blockquote.red {
    border-color: #007D1F;
}

.blockquote.green, blockquote.orange {
    border-color: #C51105;
    
}


.pages .page.active, .pages.green .page.active, .pages.red .page.active, .pages.orange .page.active {
    color: #C51105;
}

.pages .page {
    color: #007d1f;
}

.hover-card.viewed {
    background-color: #D9DBE2
}

.hover-card:hover, .profile-card:hover, .friend-card:hover, .crate-types :hover  {
    background-color: #DEE0E5
}

.crate-types .active, .crate-types :hover {
    background-color: #DEE0E5;
    border-left: 4px solid #C51105;
}

/*cards top*/
.card .top:not(.absolute) {
    color: #f2f2f2;
    background-color: #007D1F;
    border-bottom: 2px solid;
    border-color: #C51105;
    /*[[bkgtype]]*/
}

.card .top:not(.absolute).blue {
    color: #f2f2f2;
    background-color: #C51105;
    border-color: #880c04
}

.card .top:not(.absolute).green {
    color: #f2f2f2;
    background-color: #007D1F;
    border-color: #004611;
}

.card .top:not(.absolute).orange {
    color: #f2f2f2;
    background-color: #C51105;
    border-color: #007D1F;
}

.card .top:not(.absolute).red {
    color: #f2f2f2;
    background-color: #007D1F;
    border-color: #C51105;
}

.thread-label.blue {
    background-color: #C51105;
}

.thread-label.green {
    background-color: #007D1F;
}

.thread-label.orange {
    background-color: #C51105;
}

.thread-label.red {
    background-color: #007D1F;
}

/*buttons*/


button:not(.plain).blue, .button:not(.plain).blue {
    color: #fff;
    background-color: #C51105;
    border-color: #950e05;
}

button:not(.plain).red, .button:not(.plain).red  {
    color: #fff;
    background-color: #C51105;
    border-color: #950e05;
}

button:not(.plain).green, .button:not(.plain).green {
    color: #fff;
    background-color: #009f28;
    border-color: #007D1F;
}

button:not(.plain).orange, .button:not(.plain).orange {
    color: #fff;
    background-color: #009f28;
    border-color: #007D1F;
}

.button.tab-button.transparent, button.tab-button.transparent {
    background-color: transparent;
    border-color: transparent;
    color: #fff;
}

.input-group .input-button {
    border-left: 1px solid #007D1F;
    color: #ffffff;
    background-color: #009f28;
}

/*tabs*/


.tabs .tab-body {
    border: 1px solid #c1c4ce;
    background-color: #E9EBF3;
    border-top: 0px;
}

.tabs .tab.active {
    background-color: #E9EBF3;
    border-bottom: 1px solid #E9EBF3;
}

.tabs .tab {
    background-color: #D9DBE2;
    border: 1px solid #c1c4ce;
}

/*alerts*/

.alert.warning {
    background-color: #ef9526;
    border: 1px solid #d0780c;
}
  
.alert.error {
    background-color: #ef263d;
    border: 1px solid #bd0e22;
}
  
.alert.success {
    background-color: #23d160;
    border: 1px solid #07a03d;
}

.w-full {
    width: 100%;
    color: #fff;
}
  
.bg-gradient-gray {
    background-image: -webkit-linear-gradient(top,#71baed00,#c51105 0%,#016b1e 100%,#71baed00);
}

.text-gray-600 {
    color: #000;
}
  
}

