style.css:
@-moz-document url-prefix("http://fanyi.youdao.com/") {
:root{--dark-color:#000;--foreDark:#1a1b1f;--foreLight:#636363;--blue:#2196f3;--grayDark:#2a2c32;--red:#ce3c41;--redDark:#fe2e35}*{outline:0!important}body{background:var(--dark-color)!important}.item-select,s .fanyi__input__bg{background-color:var(--foreDark)!important;border:2px solid var(--foreDark)}.fanyi__input__bg--focus,.fanyi__input__bg:focus,.item-select:hover{border:2px solid var(--grayDark);outline:0!important}.input__original__area{color:var(--foreLight)!important}.doc__upload--con{background-color:var(--grayDark);border:2px solid var(--grayDark);transition:all .25s}.doc__upload--con:hover{background-color:var(--red);border:2px solid var(--redDark);color:#fff;transition:all .25s}.item-select .select-text{color:var(--foreLight)!important}.item-select ul.select{background-color:var(--foreDark);border:2px solid var(--grayDark);left:-2px!important}.item-select ul.select a{color:var(--foreLight)}.item-select .selected a,.item-select li:hover a{background:var(--grayDark)!important;color:var(--red)!important}#transMachine{background:var(--red)}#transMachine:hover{background:var(--redDark)}.fanyi__operations--man{background:var(--grayDark);color:var(--foreLight);border:2px solid var(--grayDark)}.fanyi__operations--man:hover{background:var(--red)}.input__target__text{background:0 0;color:var(--foreLight)}.input__target__text span.hover{background:var(--dark-color)}.input__target__dict{border-top:2px solid var(--grayDark);margin-top:9pt}.dict__word,.input__target__dict .resource{color:var(--foreLight)!important}.dict__relative a{color:var(--red)}.fanyi__footer{padding:20px;border-top:2px solid var(--foreDark)}.fanyi_footer a{color:var(--foreLight)!important}.fanyi__footer .c_fnl{color:var(--dark-color)!important}.c_fcopyright{color:var(--red)!important;margin-top:9pt!important}.fanyi{min-height:558px!important}.input__original__area,.input__target__text{min-height:15pc!important}.inside__products,.side__nav{display:none!important}
}
