style.css:
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain("www.adme.ru"){


body { background-color: #292727;}

.page {
    width: 1120px;
    position: relative;
    margin: 0 auto;
    display: block;
    padding: 0 40px;
    background: #252323;
    border-radius: 0px;
    min-height: 100%;}

.share-global {
    position: fixed;
    display: none;
    flex-direction: column;
    left: 50%;
    margin-left: -660px;
    top: 140px !important;
    padding: 10px 10px 0 10px;
    background: #353333;
    box-shadow: 0px 0px 10px 1px #353333;
    border-radius: 8px;
    z-index: 99;}

.article-stats-wrap .stats-item {
    display: flex;
    width: 100%;
    height: 24px;
    justify-content: center;
    align-items: center;
    background-color: #353333;
    border-radius: 4px;
    color: #B2B7C3 !important;
    font-size: 12px;}

body {
    font: 12px/1.2 Helvetica, Arial, sans-serif;
    color: #fff;}

a {
    color: #c5c5c5;}

.article-list-block:hover a {
    color: rgb(115, 115, 115);}

.online {
    border-radius: 9px;
    padding: 25px 25px 6px;
    margin: 20px 0 20px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0);}

.online h4 a, .online-title a {
    text-decoration: none;
    color: #c5c5c5;}

.online p, .online-text {
    clear: both;
    padding: 8px 16px;
    font-size: 14px;
    line-height: 20px;
    background-color: #00000017;
    display: inline-block;
    border-radius: 8px;
    margin: 8px 0 0;
    width: 100%;
    box-sizing: border-box;}

.new-header .header-nav li.header-nav-sec > a {
    border: 1px solid #252323;
    border-bottom: 0;
    border-radius: 7px 7px 0 0;
    background: no-repeat right -2px top 14px #252323;
    position: relative;
    padding: 4px 31px 5px 18px;
    line-height: 1.1;}

.sidebar-banner {
    background: #00000047;
    border-radius: 9px;
    -moz-border-radius: 9px;
    -webkit-border-radius: 9px;
    padding: 25px;
    margin: 20px 0;
    min-width: 286px;}

.new-header .header-nav-submenu {
    border: 1px solid #353333;
    top: 32px;
    display: none;
    position: absolute;
    background-image: linear-gradient(totop, #f4f5f4, #fff);
    background-color: #353333;
    border-radius: 0 14px 14px 14px;
    box-shadow: 2px 1px 4px rgba(0, 0, 0, .2);
    padding: 21px 0 11px;
    white-space: nowrap;}

.new-header .header-nav-submenu-list a {
    white-space: nowrap;
    padding: 5px 16px 4px;
    display: block;
    border-radius: 4px;
    color: #fff;
    text-decoration: none;}

.new-header .header__nav-footer a {
    color: #dcdcdc;
    font-size: 12px;}

.dialog {
    position: fixed;
    display: none;
    background: #ffffff1c;
    left: 50%;
    top: 50%;
    border-radius: 15px;
    z-index: 500;
    width: 220px;
    padding: 70px 200px 1;
    -moz-transform: translate(-390px, -290px);
    -o-transform: translate(-390px, -290px);
    -ms-transform: translate(-390px, -290px);
    -webkit-transform: translate(-390px, -290px);
    transform: translate(-390px, -290px);}

.d-close {
    position: absolute;
    width: 15px;
    height: 22px;
    padding: 10px;
    right: 18px;
    top: 21px;
    cursor: pointer;}

.dialog-auth {
    width: 340px;
    padding: 30px 100px 30px;
    transform: translate(-220px, -290px);}

.new-header .user-nav {
    position: absolute;
    background-image: linear-gradient(totop, #f4f5f4, #fff);
    background-color: #252020;
    right: 0;
    top: 35px;
    list-style: none;
    border-radius: 8px 0 8px 8px;
    border: 1px solid #252020;
    z-index: 100;
    display: none;
    box-shadow: -2px 2px 4px rgba(0, 0, 0, .2);}

.user-name-inner {
    background: #fff0;
    padding: 0 14px;
    margin: 0 0 0 -35px;
    text-decoration: none;}

.user-menu a.current {
    background: #65656521;}

input[type="submit"].submitinput {
    width: 100%;
    height: 40px;
    border-radius: 8px;
    border: none;
    text-align: center;
    font-size: 16px;
    background: #353333;
    border: 0;
    color: inherit;
    line-height: normal;
    overflow: visible;
    padding: 0;
    -webkit-appearance: button;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;}

.dialog {
    position: fixed;
    display: none;
    background: #292727;
    left: 50%;
    top: 50%;
    border-radius: 15px;
    z-index: 500;
    width: 680px;
    padding: 30px 50px 0;
    -moz-transform: translate(-390px, -290px);
    -o-transform: translate(-390px, -290px);
    -ms-transform: translate(-390px, -290px);
    -webkit-transform: translate(-390px, -290px);
    transform: translate(-390px, -290px);}

.new-header .user-avatar {
    display: block;
    border: 1px solid #0000;
    position: relative;
    border-radius: 8px;
    background: no-repeat 46px -716px #0000;
    padding: 5px 33px 5px 5px;}

.article a {
    color: #a8d9fd;}

.comment-inner {
    overflow: hidden;
    background: #292727;
    border-radius: 10px;
    padding: 8px 16px;
    box-sizing: border-box;}

.comment-theme {
    font-size: 14px;
    color: #fff;
    font-weight: bold;
    text-decoration: none;
    max-width: 175px;
    white-space: nowrap;
    text-overflow: ellipsis;
    display: inline-block;
    overflow: hidden;
    vertical-align: top;}

.add-comments-auth {
    font-size: 24px;
    padding: 10px 15px;
    background: #00000047;
    border-radius: 15px;
    text-align: center;}

.add-comments-auth a {
    color: #fff9c8;}

.theme-title span {
    position: relative;
    background: #252323;
    z-index: 5;
    padding: 0 22px 0 0;}

.pag a.pag-nav {
    background: #292727;
    color: #fff;
    font-size: 18px;
    padding: 9px 12px;
    margin: 0 10px;}
}
