style.css:
@-moz-document domain("start.firefoxchina.cn") {
	/* 主体 */
    body {background-image:url(https://i.loli.net/2019/10/24/qw1khfF3Ar5HxI6.png) !important;
    background-repeat: no-repeat;
    background-position:-175px 0;
    background-attachment:fixed;
	background-size:  cover;
	-webkit-background-size: cover;/* 兼容Webkit内核浏览器如Chrome和Safari */
	-o-background-size: cover;/* 兼容Opera */
    }
    .body .engine-logos{
    background-color:#bbbbbb;
    }
    /* 输入栏 */
	.engine-key {
   border-left:1px solid #3e3e3e;
   height: 18px;
   font-size: 18px;
}
    .body .engine-action {
    width: 610px;
	                                        
}
	/* 搜索按钮 */
    .btn-engine-bg {
    right: -17.5px;
    height:40px;
    border-bottom-right-radius: 18px !important;
    border-top-right-radius: 4.5px !important;       
    transition: all 0.5s;
    text-align:center;
	transition-timing-function:ease;
    background:#029ae5;
    }

    .btn-engine-bg:hover{
    width:130px;
}
	.btn-engine-bg input{ 
    width:100%;
    transition: all 0.4s;
    background:url(https://i.loli.net/2019/09/22/7yuB4Lb3jNavIlJ.png)-10px -16px no-repeat;
    background-size:70px 70px;
    transition-timing-function:ease-out;
}
	.btn-engine-bg input:hover{ 
    background:url(https://i.loli.net/2019/09/22/7yuB4Lb3jNavIlJ.png)-20px -75px no-repeat;
    background-size:200px 200px;
	text-shadow:3px 3px  1.5px #5a5a5a;
    font-size:120%;
	box-shadow:0 0 5px black;
}
	.btn-engine-bg input:active{ 
	opacity: 0.7; 
    background:url(https://i.loli.net/2019/09/22/7yuB4Lb3jNavIlJ.png)-15px -68px no-repeat;
    background-size:183.3px 183.3px;
    text-shadow:2px 0px  1px #5a5a5a;
    font-size:110%;
    box-shadow:0 0 3px black;
}
	/* 搜索框主体 */	
    .engine-wrapper {
    border:none;
	background-color:#fff;
    opacity:0.5;
	filter:Alpha(opacity=50); 
    border-radius:18px 4.5px !important;
    box-shadow:6px 6px 5px black;
}  
	.engine-key{
    width:537px;
	transition: all 0.4s;
    transition-timing-function:ease;
}
	.engine-key:focus{
    box-shadow:0 0 7px black;
}
	/* 搜索引擎侧 */
	.engine-logos{
	border-bottom-left-radius: 4.5px !important;
    border-top-left-radius: 18px !important;   
    transition: all 0.4s;
    height:42px;
    transition-timing-function:ease;
}

	.engine-logos:hover{
	 box-shadow:0 0 5px rgb(111,111,111);
}
	.engine-logo-more{
    padding-top:0px;
	margin-top:2px;
    height:40.5px;
    overflow:hidden;
	transition: all 0.3s;
    transition-timing-function:ease-out;
}
	.engine-logo-more:hover{
	box-shadow:0 0 5px black;
}
	.engine-logo-list>p{
    transition: all 0.4s;
    transition-timing-function:ease;
}
	.engine-logo-list>p:hover{
	box-shadow:0 0 3px black;
}
.mod-delay-resource{
    display:none !important;
}
}
